INSERT INTO `posts` (`id`, `category_id`, `hy_title`, `ru_title`, `hy_content`, `ru_content`, `image`, `source`, `hy_description`, `ru_description`, `is_general`, `has_video`, `viewed`, `created_at`, `updated_at`) VALUES
(1, 1, 'Կեղծիքները չեն դադարում. Ադրբեջանը շարունակում է Դադիվանքը յուրացնելու փորձերը', '', '<p>Ադրբեջանի իշխանությունները չեն դադարեցնում Քարվաճառի Դադիվանքի հայկական տաճարը յուրացնելու փորձերը:</p>\n<p>Ինչպես հաղորդում է ԱՊԱ-ն, &laquo;ալբանա-ուդիական կրոնական համայնքի անդամներն այցելել են վանք, կրոնական ծես կատարել տաճարում, աղոթքներ կարդացել եւ մոմեր վառել&raquo;: Այցելությունը տեղի է ունեցել Կրոնական կազմակերպությունների հետ աշխատանքի պետական ​​կոմիտեի շրջանակներում:</p>\n<p>Ադրբեջանի իշխանությունները փորձում են հայկական հինավուրց Դադիվանքը որպես &laquo;ալբանական&raquo; ներկայացնել՝ գործելով &laquo;ինչը չի կարելի քանդել, պետք է յուրացնել&raquo; սխեմայով։</p>\n<p>&nbsp;</p>', '', '/upload/default%20(5).jpg', 'https://news.am/arm/news/700840.html', '«Ալբանա-ուդիական կրոնական համայնքի անդամներն այցելել են վանք, կրոնական ծես կատարել տաճարում, աղոթքներ կարդացել եւ մոմեր վառել...', '', 0, 0, 5, '2022-05-09 15:49:22', '2022-05-10 17:16:05'),
(2, 1, 'Զոհվածների հարազատները երթով հասել են «Եռաբլուր». Ոգեկոչման երեկո է կազմակերպվել', '', '<p>Արցախյան 44-օրյա պատերազմում զոհված զինծառայողների հարազատները, որոնք երթով ուղեւորվել էին դեպի &laquo;Եռաբլուր&raquo; զինվորական պանթեոն, հասել են այնտեղ: Երթին միացել են բազմաթիվ քաղաքացիներ։ Մասնակիցներն իրենց ձեռքում ունեն զոհվածների լուսանկարները, Հայաստանի ու Արցախի դրոշներ։</p>\n<p>Զոհվածների մայրերը, քույրերը հագել են իրենց որդիների, եղբայների լուսանկարներով սև շապիկներ։</p>\n<p>&laquo;Սա ավանդական երթ է դառնում։ Անցած տարի էլ Շուշշի ազատագրման օրը մենք երթ էինք կազմակերպել Եռաբլուր՝ ի ոգեկոչում, ի միասնություն, ի սթափություն&raquo;,-նշել են կազմակերպիչները:</p>\n<p>Մշակութաբան, պարագետ, պարահավաք, &laquo;Կարին&raquo; ավանդական երգի-պարի խմբի հիմնադիր և գեղարվեստական ղեկավար Գագիկ Գինոսյանն ասաց՝ ուղիղ 30 տարի առաջ հայի բազուկն ապացուցեց իր հաղթական լինելը. &laquo;Թեկուզ երկու տարի է՝ ինչ խամրեցրել են մեր հաղթանակը, բայց վստահ կարող ենք ասել, որ մենք հավերժ նշելու ենք Շուշիի ազատագրումը։ Նոր ազատագրումը լինելու է այդ ազատագրման վերակնքումը։ Շուշին հային է, և ինչպես կասեր Գարեգին Նժդեհը, մի ազգի հարենիքը չի կարող դառնալ մյուսի մշտական բնակավայր։ Հողը համբերատար սպասում է իր տերերին, եթե իհարկե տերը չի կորցրել այն տիրելու ցանկությունը։ Մենք այստեղ ենք, քանի որ այստեղ անմահներն են։ Իսկ անմահները հրամայում են չենթարկվել խաղաղության կոչերին, չհավատալ թուրքի հետ խաղաղությանը։ Խաղաղություն&nbsp; կարող է լինել միայն Նժդեհի բանձևով, ներել եք ուզում թուրքին, հաղթեք նախ&raquo;,-ասաց Գինոսյանը։</p>\n<p>&laquo;Եռաբլուր&raquo; զինվորական պանթեոնում տեղի է ունեցել ոգեկոչման երեկո։</p>\n<p><iframe src=\"https://www.youtube.com/embed/n1O9swyGAlA\" width=\"620\" height=\"349\"></iframe></p>', '', '/upload/Screen%20Shot%202022-05-10%20at%2020-15-34.png', 'https://news.am/arm/news/700878.html', 'Արցախյան 44-օրյա պատերազմում զոհված զինծառայողների հարազատները, որոնք...', '', 0, 0, 9, '2022-05-09 15:52:48', '2022-05-12 00:46:36'),
(3, 2, 'Կան իրենց 2 կոպեկի համար թուրքի դիմաց կզող «դերասանուհիներ» ու կա արժանապատիվ Լուիզա Ղամբարյան և Ագնեսա Շահնազարյան', '', '<div><img src=\"https://scontent.fevn12-1.fna.fbcdn.net/v/t39.30808-6/280194562_1032163484334036_1790267991193197063_n.jpg?_nc_cat=110&amp;ccb=1-6&amp;_nc_sid=8bfeb9&amp;_nc_ohc=WMCHvJYszAAAX9xV6iq&amp;_nc_ht=scontent.fevn12-1.fna&amp;oh=00_AT8eBkKaRPXVUpatM0skKGnYwBhsHR349qc_leUZvYUIcQ&amp;oe=627A8C34\" /></div>\n<p>Կան իրենց 2 կոպեկի համար թուրքի դիմաց կզող &laquo;դերասանուհիներ&raquo; ու կա արժանապատիվ Լուիզա Ղամբարյան և Ագնեսա Շահնազարյան</p>', '', '/upload/794310%20(1).jpg', 'https://www.blognews.am/arm/news/794310/kan-irenc-2-kopeki-hamar-turqi-dimac-kzox-derasanuhiner-u-ka-arzhanapativ-luiza-xambaryan-ev-agnesa-shahnazaryan.html', 'Կան իրենց 2 կոպեկի համար թուրքի դիմաց կզող «դերասանուհիներ» ու կա արժանապատիվ Լուիզա Ղամբարյան և Ագնեսա ...', '', 0, 0, 2, '2022-05-09 16:03:48', '2022-05-11 07:42:51'),
(4, 1, 'Կանանց երթի ժամանակ մի անհավասարակշիռ սադրիչ մեքենան վարեց մեր նահատակված հերոսների մայրերի ուղղությամբ (Video)', '', '<p>Հարգելի գաղափարակիցներ, Այսօր՝ կանանց երթի ժամանակ, մի անհավասարակշիռ սադրիչ, անգամ՝ թե՛ մեր, թե&rsquo; ոստիկանության կողմից կարգի հրավիրվելուց հետո, դուրս գալով անթույլատրելի հատված, մեքենան վարեց մեր ուղղությամբ: Ուղղություն, որտեղ իրենց արդար պահանջն ու անարդուրությունից խեղդվող ձայնն էին բարձրացնում մեր նահատակված հերոսների մայրերը։ Ի պատիվ Ոստիկանության ապօրինի գործողություններն ստացան համարժեք գնահատական! Հ. գ. Այսօրվա մեր ուղեկիցն ու սադրիչների զսպողն էր նաեւ մեր այս սպիտակամազ ընկերը։</p>\n<p><iframe src=\"https://www.facebook.com/plugins/video.php?height=476&amp;href=https%3A%2F%2Fwww.facebook.com%2Fgohar.meloyan%2Fvideos%2F1454220538333362%2F&amp;show_text=false&amp;width=267&amp;t=0\" width=\"267\" height=\"476\"></iframe></p>', '', '/upload/794319.jpg', 'https://www.blognews.am/arm/news/794319/kananc-erti-zhamanak-mi-anhavasarakshir-sadrich-meqenan-varec-mer-nahatakvats-herosneri-mayreri-uxxutyamb-video.html', 'Հարգելի գաղափարակիցներ,', '', 0, 0, 1, '2022-05-09 16:05:52', '2022-05-10 17:14:42'),
(5, 2, 'Ողբերգական դեպք Լոռու մարզում. Դեբեդ գետից դուրս բերված «Շանթ» հեռուստաընկերության սիրված հաղորդավարուհին մահացել է. ՖՈՏՈՌԵՊՈՐՏԱԺ', '', '<p>SHAMSHYAN.com-ի տեղեկություններով՝ &nbsp;քիչ առաջ ողբերգական դեպք է տեղի ունեցել Լոռու մարզում: Ժամը 16:40-ի սահմաններում Արտակարգ իրավիճակների նախարարության փրկարար ծառայության&nbsp; 911 ճգնաժամային կառավարման կենտրոն ահազանգ է ստացվել, որ Դեբեդ գետը քաղաքացի է ընկել: Ինչպես հայտնում է ֆոտոլրագրող Գագիկ Շամշյանը, Ալավերդի &nbsp;քաղաքի Սանահին կայարանի հարակից Դեբեդ գետից քաղաքացիները փրկարարներն ու Լոռու &nbsp;պարեկները Դեբեդ գետից անգիտաից վիճակում դուրս են բերել երիտասարդ աղջկա, ով շտապօգնության ավտոմեքենայով տեղափոխվել է Ալավերդու բժշկական կենտրոն, որտեղից կարճ ժամանակ անց ոստիկանության Թումանյանի բաժին ահազանգ է ստացվել, որ&nbsp; նա գիտակցության չգալով՝ մահացել է:Գ. Շամշյանի տեղեկություններով՝ մահացածը &laquo;Շանթ&raquo; հեռուստաընկերությամբ հեռարձակվող &nbsp;&laquo;Ճամպրուկս ու ես&raquo; հաղորդման &nbsp;վարող, 31-ամյա Արմինե Ճաղարյանն էր:Փաստի առթիվ&nbsp; ոստիկանության Թումանյանի բաժնում նյութեր են նախապատրաստվում:Գ. Շամշյանի տեղեկություններով՝ փաստի առթիվ հնարավոր է նախաքննություն անցկացնեն Հայաստանի քննչական կոմիտեի Լոռու մարզային քննչական վարչության Թումանյանի քննչական բաժնի քննիչները:Գ. Շամշյանի տեղեկություններով՝&nbsp; սիրված հաղորդավարուհին իր գործընկերների հետ լաստանավի վրա նկարահանումներ կատարելիս է եղել:Տեղում ուժեղացված ծառայություն են կատարել ՀՀ ոստիկանության պարեկային ծառայության Լոռու մարզի գումարտակի 2-րդ վաշտի ծառայողները՝ գումարտակի հրամանատար Արմեն&nbsp; Բեժանյանի, վաշտի հրամանատար Վիկտոր Սարգսյանի և&nbsp;&nbsp; նույն վաշտի 4-րդ դասակի հրամանատար Մնացական Բոյաջյանի գլխավորությամբ:Տեղում ուժեղացված ծառայություն են կատարել նույն դասակի պարեկներ Գոռ Հովհաննիսյանը, Դավիթ Երզնկյանը, Հովիկ Վանեցյանն ու Կարապետ Հարությունյանը, ովքեր նաև ճանապարհ են բացել, որպեսզի փրկարարներն ու Թումանյանի ոստիկանները ազատ տեղաշարժվեն, հետաքրքրասեր քաղաքացիները խցանումներ չառաջացնեն:Ողբերգության հետևանքով մահացած, սիրված հաղորդավարուհի Արմինե Ճաղարյանն էԾանուցում. Ենթադրյալ հանցանքի մեջ կասկածվողը կամ մեղադրվողը համարվում է անմեղ, քանի դեռ նրա մեղավորությունն ապացուցված չէ ՀՀ քրեական դատավարության օրենսգրքով սահմանված կարգով` դատարանի` օրինական ուժի մեջ մտած դատավճռով:Լուսանկարները՝ Էդիկ Սիմոնյանի&nbsp;</p>', '', '/upload/e128c22e8f1bf0429a73ec4f6aa182f0.jpg', 'https://shamshyan.com/hy/article/2022/05/09/1213088/', 'SHAMSHYAN.com-ի տեղեկություններով՝ &nbsp;քիչ առաջ ողբերգական դեպք է տեղի ունեցել Լոռու մարզում: Ժամը 16:40-ի սահմաններում Արտակարգ իրավիճակների նախարարության փրկարա', '', 0, 0, 5, '2022-05-09 16:18:36', '2022-05-12 09:27:38'),
(6, 1, 'ԱՄՆ առաջին տիկինը հանդիպել է Սլովակիայի նախագահի հետ', '', '<p>ԱՄՆ նախագահ Ջո Բայդենի կինը՝ Ջիլ Բայդենը, երկուշաբթի Բրատիսլավայում հանդիպել է Սլովակիայի նախագահ Զուզանա Չապուտովայի հետ։ Այս մասին հայտնում է TASR լրատվական գործակալությունը։</p>\n<p>Հանդիպումը եզրափակել է մայիսի 5-ին մեկնարկած նրա եվրոպական շրջագայությունը, որի ընթացքում Ջիլ Բայդենն այցելել է նաեւ Ռումինիա։</p>\n<p>Բայդենի շրջագայության նպատակն էր աջակցություն հայտնելը փախստականներին Ուկրաինայից եւ դրան սահմանակից երկրներին, որոնք ընդունում են նրանց։ Սլովակիայում նա այցելել է Բրատիսլավա, Կոսիցե քաղաք եւ հանրապետության արեւելքում գտնվող Վիշնե-Նեմեցկե գյուղ։ Ուկրաինայի հետ սահմանին գտնվող Վիշնե-Նիմեցկեից ԱՄՆ առաջին տիկինը կիրակի մեկնել է Ուժգորոդ, որտեղ հանդիպել է Ուկրաինայի նախագահ Վլադիմիր Զելենսկու կնոջ՝ Ելենա Զելենսկայայի հետ։</p>', '', '/upload/default%20(4).jpg', 'https://news.am/arm/news/700851.html', 'ԱՄՆ նախագահ Ջո Բայդենի կինը՝ Ջիլ Բայդենը, երկուշաբթի Բրատիսլավայում հանդիպել է Սլովակիայի նախագահ Զուզանա Չապուտովայի հետ', '', 0, 0, 3, '2022-05-09 16:19:28', '2022-05-10 17:14:02'),
(7, 1, 'Ռոբերտ Քոչարյանը «Հայաստան» խմբակցության պատգամավորների հետ մասնակցում է Համազգային հանրահավաքին', '', '<p>ՀՀ երկրորդ նախագահ Ռոբերտ Քոչարյանը &laquo;Հայաստան&raquo; խմբակցության պատգամավորների հետ մասնակցում է Համազգային հանրահավաքին: Այս մասին NEWS,.am-ը տեղեկանում է &laquo;Հայաստան&raquo; դաշինքից:</p>\n<p>Հիշեցնենք, որ &laquo;Դիմադրության&raquo; շարժումը Ֆրանսիայի հրապարակում համազգային հանրահավաք է հրավիրել: Շաբաթ եւ կիրակի օրերին ավտոերթեր են կազմակերպվել դեպի Վանաձոր եւ Գյումրի:</p>\n<p>Ավելի վաղ NEWS.am-ը հայտնել էր, որ մայիսի 2-ից խաղաղ անհնազանդության ակցիաներ են սկսվել Երեւանում, ամեն օր փակվում են մի շարք փողոցներ: Մայիսի 2-ից Երևանի Ֆրանսիայի հրապարակում հանրահավաքներ են իրականացվում։ Բացառություն է եղել միայն կիրակի օրը: Ֆրանսիայի հրապարակում վրաններ են տեղադրված արդեն մեկ շաբաթ:</p>\n<p>&nbsp;</p>\n<p><iframe src=\"https://www.facebook.com/plugins/post.php?href=https%3A%2F%2Fwww.facebook.com%2Fhayastandashinq%2Fposts%2F306571028320779&amp;show_text=true&amp;width=500\" width=\"500\" height=\"684\"></iframe></p>', '', '/upload/default%20(3).jpg', 'https://news.am/arm/news/700886.html', 'Ռոբերտ Քոչարյանը «Հայաստան» խմբակցության պատգամավորների հետ մասնակցում է Համազգային հանրահավաքին', '', 0, 0, 3, '2022-05-09 16:21:17', '2022-05-10 17:13:26'),
(8, 6, 'Հայիթիում մարդիկ են առեւանգվել, այդ թվում՝ առնվազն ութ թուրք միսիոներ', '', '<p>Հայիթիում մարդիկ են առեւանգվել, այդ թվում՝ առնվազն ութ թուրք միսիոներ, Դոմինիկյան Հանրապետությունից ավտոբուսով ուղեւորիությունից հետո՝ Հայիթիում ավազակային հարձակումների ալիքի ֆոնին:</p>\n<p>Առեւանգումը տեղի է ունեցել կիրակի ցերեկը Կռուա-դե-Բուկեից քիչ հեռու, որտեղ անցյալ տարի ԱՄՆ-ից եւ Կանադայից մի խումբ միսիոներներ էին առեւանգվել, հայտնում է Haiti24 լրատվական կայքը:</p>\n<p>Դոմինիկյան Listin Diario պարբերականի փոխանցմամբ՝ ավտոբուսում եղել են նաեւ հայիթցի ուղեւորներ եւ դոմինիկացի վարորդը։</p>\n<p>Անցյալ տարի նախագահ Ժովենել Մոյզի սպանությունից հետո բանդաները նոր թափ են հավաքում, գրում է AP-ն։</p>\n<p>Երկու շաբաթ առաջ Պորտ-օ-Պրենսի մոտ բանդաների միջեւ բախման հետեւանքով առնվազն 75 մարդ էր զոհվել:</p>\n<p>&nbsp;</p>', '', '/upload/default%20(2).jpg', 'https://news.am/arm/news/700857.html', 'Առեւանգումը տեղի է ունեցել Դոմինիկյան Հանրապետությունից ավտոբուսով ուղեւորիությունից հետո...', '', 0, 0, 5, '2022-05-09 16:22:54', '2022-05-11 06:36:38'),
(9, 6, 'Շառլ Միշելն անսպասելի այցով ժամանել է Օդեսա', '', '<p>Եվրոպական խորհրդի նախագահ Շառլ Միշելն անակնկալ այցով ժամանել է Օդեսա, հայտնում է France 24-ը։ ԵՄ պաշտոնյայի խոսքով՝ Միշելը ստիպված էր ընդհատել հանդիպումը եւ պատսպարվել, երբ քաղաքի վրա &laquo;հրթիռներ տեղացին&raquo;:</p>\n<p>Հարվածը տեղի է ունեցել Ուկրաինայի վարչապետ Դենիս Շմիգալի հետ բանակցությունների ժամանակ։ &laquo;Վարչապետի հետ հանդիպման ժամանակ մասնակիցները ստիպված եղան ընդհատել հանդիպումը՝ պատսպարվելու համար, քանի որ հրթիռները կրկին ընկան Օդեսայի վրա&raquo;,- ասել է պաշտոնյան։</p>\n<p>Տեսակոնֆերանսի ձեւաչափով բանակցություններին միացած&nbsp;Վլադիմիր Զելենսկին շնորհակալություն է հայտնել Միշելին Հաղթանակի օրը՝ Երկրորդ համաշխարհային պատերազմում նացիստական ​​Գերմանիայի պարտության տարելիցի օրը, Ուկրաինա այցի համար։</p>\n<p>&nbsp;</p>', '', '/upload/default%20(6).jpg', 'https://news.am/arm/news/700882.html', 'Միշելը ստիպված էր ընդհատել հանդիպումը եւ պատսպարվել, երբ քաղաքի վրա «հրթիռներ տեղացին»...', '', 0, 0, 4, '2022-05-09 16:24:03', '2022-05-11 07:59:26'),
(10, 5, 'Թողարկվել է «Միսս Մարվել» սերիալի նոր թիզերը', '', '<p>&laquo;Disney+&raquo;-ը հրապարակել է &laquo;Միսս Մարվել&raquo; մինի սերիալի թիզերը, որը պատմում է Կամիլա Խանի արկածների մասին։ Նախագծի հեղինակը Բիշեյ Կ․ Ալին է։</p>\n<p>&nbsp;</p>\n<p>Սերիալի պրեմիերան նախատեսված է հունիսի 8-ին։</p>\n<p>Սյուժեն պատմում է արմատներով Պակիստանից դպրոցականի՝ Կամիլա Խանի մասին, որը հայտնաբերում է, որ իր մարմինը դեֆորմացնելու եւ արտաքինը փոխելու կարողություն ունի։ Կամիլան Marvel-ի առաջին մուսուլման հերոսն է եւ Կապիտան Մարվելի մեծ երկրպագուն, որը սկսում է իր հայրենի քաղաքը՝ Նյու Ջերսին պաշտպանել տարբեր չարագործներից։</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>Հետևեք NEWS.am STYLE-ին Facebook-ում, Twitter-ում և Instagram-ում</p>\n<p><iframe src=\"https://www.youtube.com/embed/o8usJNFvVS4\" width=\"680\" height=\"365\"></iframe></p>', '', '/upload/89474.jpg', 'https://style.news.am/arm/news/89474/toxarkvel-e-miss-marvel-seriali-nor-tizery.html', 'Նա սկսում է իր հայրենի քաղաքը՝ Նյու Ջերսին պաշտպանել տարբեր չարագործներից․․․', '', 0, 1, 4, '2022-05-09 16:26:23', '2022-05-11 08:52:25'),
(11, 5, 'Հայտնի են հեռուստատեսային BAFTA-ի հաղթողների անունները', '', '<p>Կինոյի եւ հեռուստատեսային արվեստների բրիտանական ակադեմիայի (BAFTA) հեռուստատեսային մրցանակաների հանձնման արարողությունը տեղի է ունեցել մայիսի 8-ին Լոնդոնում։ Երեք տարվա ընթացքում այն առաջին անգամ անցկացվում էր հյուրերի եւ հանդիսատեսի ներկայությամբ։</p>\n<p>Լավագույն դրամատիկ դերասանուհի է դարձել Ջոդի Քոմերը (&laquo;Դայակը&raquo;), դերասան՝ Շոն Բինը (&laquo;Ժամկետ&raquo;), երկրորդ պլանի դերասանուհի՝ Քեթի Թայլսոնը (&laquo;Դայակը&raquo;), դերասանը՝ Մեթյու Մաքֆեդիենը (&laquo;Ժառանգները&raquo;)։ Կատակերգական նախագծերում դերերի համար մրցանակի են արժանացել Ջեմի Դեմետրիուն (Stath Lets Flats) եւ Սոֆի Ուիլիանը (&laquo;Alma&rsquo;s Not Normal&raquo;)։</p>\n<p>Որպես լավագույն միջազգային նախագիծ՝ մրցանակի է ստացել Բարի Ջենկիսնի &laquo;Ստորերկրյա երկաթուղային ճանապարհը&raquo;։ Այն առաջ է անցել &laquo;Ժառանգներից&raquo; եւ &laquo;Կաղամարի խաղից&raquo;։</p>\n<p>Հետևեք NEWS.am STYLE-ին Facebook-ում, Twitter-ում և Instagram-ում</p>', '', '/upload/89462.jpg', 'https://style.news.am/arm/news/89462/haytni-en-herustatesayin-bafta-i-haxtoxneri-anunnery.html', 'Հաղթողների ցանկում է Շոն Բինի մասնակցությամբ «Ժամկետ» նախագիծը․․․', '', 0, 0, 6, '2022-05-09 16:27:26', '2022-05-11 16:35:32'),
(12, 10, 'Հաղորդավարուհին գետն է ընկել ռետինե նավով նավարկելիս․ ԱԻՆ-ը նոր մանրամասներ է հայտնում', '', '<p>Արտակարգ իրավիճակների նախարարությունից նոր մանրամասներ են հայտնել հաղորդավարուհի Արմինե Ճաղարյանի մահվան վերաբերյալ։</p>\n<p>Գերատեսչության հայտարարության մեջ նշվում է, որ Արմինեն Դեբեդ գետում ռետինե նավով նավարկելիս է եղել, երբ ընկել է գետը։ Հաղորդավարուհի անգիտակից է եղել, երբ վերջինիս դուրս են բերել գետից։</p>\n<p>Ավելի վաղ հայտնել էինք, որ Արմինե Ճաղարյանի դին փրկարարները դուրս են բերել Ալավերդի քաղաքի &laquo;Սանահին&raquo; կայարանի մոտակայքից և մոտեցրել շտապօգնության ավտոմեքենային։</p>\n<p>Ալավերդու ԲԿ-ում արձանագրվել է վերջինիս մահը։</p>\n<p>Մահացածը &laquo;Շանթ&raquo; հեռուստաընկերությամբ հեռարձակվող &laquo;Ճամպրուկս ու ես&raquo; հաղորդման վարողն էր։</p>\n<p>&nbsp;</p>', '', '/upload/armine-tsagharyan-1-e1652108985208%20(1).jpg', 'https://iravaban.net/382140.html', 'ԱԻՆ-ը նոր մանրամասներ է հայտնում', 'МЧС сообщает новые подробности', 0, 0, 64, '2022-05-09 16:33:22', '2022-05-10 20:02:35'),
(13, 1, 'Տուգանված վարորդը ծանր վիրավորանք է հասցրել Նիկոլ Փաշինյանին․ քրեական գործը դատարանում է', '', '<p>Հ․ Հարությունյանին մեղադրանք է առաջադրվել վարչապետ Նիկոլ Փաշինյանին վիրավորելու համար։</p>\n<p><img src=\"https://iravaban.net/wp-content/uploads/2022/05/nikol-pashinyan-1.jpg\" /></p>\n<p>Iravaban.net-ը &laquo;Դատալեքս&raquo; դատական տեղեկատվական համակարգից տեղեկանում է, որ Հարությունյանը հունվարի 11-ին՝ ժամը 01:10-ին, ոչ սթափ վիճակում &laquo;Մոսկվիչ&raquo; մակնիշի ավտոմեքենան վարելու համար իր նկատմամբ վարչական արձանագրություն կազմած և ավտոմեքենան հատուկ պահպանվող տարածք տեղափոխել ցանկացող պարեկին ասել է արտահայտություն, որով վարչապետ Նիկոլ Փաշինյանի հանրային գործունեությամբ պայմանավորված նրան հասցրել է ծանր վիրավորանք:</p>\n<p>Գործը մակագրվել է դատավոր Գնել Գասպարյանին։ Դատական նիստի օր դեռ չի նշանակվել։</p>\n<p>Նրան նկատմամբ խափանման միջոց է ընտրվել չհեռանալու մասին ստորագրությունը։</p>', '', '/upload/nikol-pashinyan-1.jpg', 'https://iravaban.net/382098.html', 'Հ․ Հարությունյանին մեղադրանք է առաջադրվել վարչապետ Նիկոլ Փաշինյանին վիրավորելու համար։', 'Арутюняну предъявлено обвинение в оскорблении премьер-министра Никола Пашиняна.', 0, 0, 9, '2022-05-09 16:36:00', '2022-05-11 01:33:03'),
(14, 4, 'ՀՀ-ում կնշվեն անասնաբույժի և բույսերի առողջության պաշտպանության օրերը․ Կառավարության որոշում', '', '<p>Կառավարության այսօրվա նիստի ընթացքում&nbsp;&laquo;Հայաստանի Հանրապետության տոների և հիշատակի օրերի մասին&raquo; օրենքում լրացումներ կատարելու վերաբերյալ որոշում է ընդունվել։ Ըստ այդմ՝&nbsp; տոնական երկու նոր օր է ավելացել։ Հայաստանի Հանրապետությունում անասնաբույժի օրը կնշվի ապրիլի վերջին ուրբաթ օրը։ Ապրիլի 24-ը ուրբաթ օրվա հետ համընկնելու դեպքում՝ Հայաստանի Հանրապետությունում անասնաբույժի օրը&nbsp;կնշվի&nbsp;ապրիլի վերջին հինգշաբթի օրը: Հայաստանի Հանրապետությունում բույսերի առողջության պաշտպանության օրը կնշվի մայիսի 12-ը։ Որոշման հիմնավորման մեջ նշվում է, որ Հայաստանի Հանրապետությունում անասնաբույժի և բույսերի առողջության պաշտպանության օր նշելու անհրաժեշտությունը պայմանավորված է բնակչության կյանքի և առողջության, կայուն անասնահամաճարակային և բուսասանիտարական իրավիճակի ապահովման տեսանկյունից բույսերի առողջության պաշտպանության և անասնաբուժության բնագավառների դերն ու նշանակությունը ևս մեկ անգամ ընդգծելու հանգամանքով։</p>', '', '/upload/791497.jpg', 'https://blognews.am/arm/news/791497/hh-um-knshven-anasnabuyzhi-ev-buyseri-aroxjutyan-pashtpanutyan-orery%E2%80%A4-karavarutyan-oroshum.html', 'ՀՀ-ում կնշվեն անասնաբույժի և բույսերի առողջության պաշտպանության օրերը․ Կառավարության որոշում', 'В РА отметят ветеринарные և дни охраны здоровья растений ․ Решение правительства', 0, 0, 4, '2022-05-09 16:38:17', '2022-05-11 08:30:07'),
(15, 7, '«Դիմադրություն» շարժման մշակութային ծրագիրը Ֆրանսիայի հրապարակում. LIVE', '', '<p>&nbsp;</p>\n<p><iframe src=\"https://www.youtube.com/embed/biYcguY2gkc\" width=\"560\" height=\"315\"></iframe></p>', '', '/upload/794335.jpg', 'https://blognews.am/arm/news/794335/dimadrutyun-sharzhman-mshakutayin-tsragiry-fransiayi-hraparakum-live.html', '«Դիմադրություն» շարժման մշակութային ծրագիրը Ֆրանսիայի հրապարակում. LIVE', '«Դիմադրություն» շարժման մշակութային ծրագիրը Ֆրանսիայի հրապարակում. LIVE', 0, 1, 3, '2022-05-09 16:39:46', '2022-05-10 17:11:05'),
(16, 8, 'Պանրով բլիթներ', '', '<p>Բաղադրիչներ Կաթ &ndash; 1.5 բաժակ Ձու &ndash; 2 հատ Ալյուր &ndash; 1 բաժակ Պանիր &ndash; 150 գ Աղ &ndash; 1 թ/գ Շաքարավազ &ndash; 1 թ/գ Փխրեցուցիչ &ndash; 1 թ/գ Բուսական յուղ &ndash; 2 ճ/գ Սամիթ &ndash; ըստ ցանկության Պատրաստման&nbsp;եղանակը Տաք կաթը խառնել շաքարավազի, աղի և ձվերի հետ: Ավելացնել ալյուրը և փխրեցուցիչը: Պանիրն անցկացնել քերիչով և ավելացնել զանգվածին: Այնուհետև ավելացնել մանրացված կանաչին և բուսայուղը: Լավ խառնել և երկու կողմից տապակել:</p>', '', '/upload/762811.jpg', 'https://blognews.am/arm/news/762811/panrov-blitner.html', 'Պանրով բլիթներ', 'Сырное печенье', 0, 0, 4, '2022-05-09 16:41:55', '2022-05-11 09:43:01'),
(17, 9, 'Պատմության մեջ առաջին անգամ գրենլանդյան սառույցների վրա անձրև է տեղացել․ սա բնության աղետ է համարվում', '', '<p>Պատմության մեջ առաջին անգամ գրենլանդյան սառույցների վրա անձրև է տեղացել։ Ինչպես և հյուսիսային կեսագնդի մեծ մասում, այնպես էլ Գրենլանդիայում այժմ չափազանց շոգ է։ Վերջին տասնամյակում արդեն երրորդ անգամ ջերմաստիճանը սառեցման աստիճանից ավելի բարձր է։&nbsp; Տեղանքում ուսումնասիրություններ կատարվում են 1950 թվականից ի վեր։ Այդ ժամանակից սկսած 3216 մ բարձրության վրա երբեք անձրև չէր տեղացել։ Ավելին, այժմ մեկ օրում յոթ անգամ ավելի շատ սառույց է հալչում, քան նախկինում՝ տարվա այս ժամանակին։&nbsp; &laquo;Գրենլանդիան, ինչպես և ողջ աշխարհը, փոփոխությունների է ենթարկվում։ Տասը տարում մենք երեք անգամ հալման ենք ականատես եղել, իսկ մինչ1990 թ․ դա տեղի էր ունենում ամեն 150 տարին միայն մեկ անգամ։ Իսկ այժմ անգամ անձրև է տեղում այնտեղ, որտեղ երբեք նման երևույթ չի գրանցվել&raquo;, - նշում է Կոլորադոյի համալսարանի գիտնականներից մեկը։&nbsp; Վերջին երեք հալչումները տեղի են ունեցել ջերմաստիճանի բարձրացման պատճառով։ Կազմավորվել է ամառային լճերի ցանց, որոնք կարող են ուժգնացնել հալչելու գործընթացը և ապակայունացնել սառցե ծածկը։&nbsp; Անձրևը ոչ միայն նխաստում է հալմանը, այլև կարող է երակարաժամկետ ազդեցություն ունենալ սառցե ծածկի դինամիկայի վրա։ Անձրևը սառում է, կազմավորվում է սառույցի ավելի մուգ շերտ, որը, համապատասխանաբար, ավելի շատ ջերմություն է կլանում։&nbsp; Գիտնականները հայտնում են, որ Գրենլանդիայի սառույցներում բավարար քանակությամբ ջուր կա, որպեսզի Համաշխարհային օվկիանոսի մակարդակը բարձրանա 6 մետրով, իսկ ընդամենը 2 աստիճանով օդի ջերմաստիճանի բարձրացումը կհանգեցնի սառցե ծածկի կոլապսի։&nbsp; Տարածաշրջանի բնակիչները դժվարությունների են հանդիպում այս երևույթի պատճառով։ Կլիմայական փոփոխություններն ազդում են նաև տեղի բնության վրա։</p>', '', '/upload/770294.jpg', 'https://blognews.am/arm/news/770294/patmutyan-mej-arajin-angam-grenlandyan-saruycneri-vra-andzrev-e-texacel%E2%80%A4-sa-bnutyan-axet-e-hamarvum.html', 'Վերջին տասնամյակում արդեն երրորդ անգամ ջերմաստիճանը սառեցման աստիճանից ավելի բարձր է։', 'В третий раз за последнее десятилетие температура превышает точку замерзания.', 0, 0, 1, '2022-05-09 16:47:01', '2022-05-10 17:10:18'),
(18, 9, 'Նման ամպից ուժեղ կարկուտ է տեղում. Գագիկ Սուրենյանը հետաքրքիր լուսանկար է հրապարակել', '', '<div>Armeniasputnik.am-ը գրում է. Հիդրոօդերևութաբանության և մոնիթորինգի կենտրոնի տնօրենի տեղակալ Գագիկ Սուրենյանը բնության հետաքրքիր երևույթի լուսանկար է հրապարակել Facebook-ում։ Դեղնավուն հսկա ամպի լուսանկարը, ըստ նրա, արվել է օգոստոսի 11-ի երեկոյան Սևանի արևմուտքում: &laquo;Ամպի հիմքը մոտ 3 կմ բարձրության վրա է, գագաթը կարող է հասնել 17-18 կմ բարձրությունների, ուղղաձիգ հզորությունը 14-15 կմ, գագաթին օդի ջերմաստիճանը`-60&ndash;ից -70 աստիճան: Նման ամպից տեղում է ուժեղ կարկուտ, տեղատարափ անձրևներ, որը ուղեկցվում է ուժեղ քամիներով: Միջազգային անվանումը՝ cumulonimbus capillatus arcus&raquo;,&ndash; լուսանկարին կից գրել է Սուրենյանը։ Օգտատերերից մեկը հրապարակել է նույն ամպի լուսանկարը, պարզապես ավելի հեռվից։</div>', '', '/upload/769857.jpg', 'https://blognews.am/arm/news/769857/nman-ampic-uzhex-karkut-e-texum-gagik-surenyany-hetaqrqir-lusankar-e-hraparakel.html', 'Հիդրոօդերևութաբանության և մոնիթորինգի կենտրոնի տնօրենի տեղակալ Գագիկ Սուրենյանը բնության հետաքրքիր երևույթի մասին...', 'Гагик Суренян, заместитель директора Центра гидроаэрологии и мониторинга, об интересном явлении природы...', 0, 0, 5, '2022-05-09 16:48:50', '2022-05-11 07:59:55'),
(19, 9, 'Թաց անապատ կամ Մարիանյան ծածկոց․ բնության ամենագեղեցիկ երևույթներից մեկի մասին', '', '<div><img src=\"https://blognews.am/static/content/Angel/content_1280px-Lencois_Maranhenses_3.jpg\" /></div>\n<div><img src=\"https://cdn.vashurok.ru/ckeditor_assets/pictures/36927/content_1280px-Lagoa_e_duna_no_Parque_Nacional_dos_Len%C3%A7ois_maranhenses_%28S%C3%A3o_Lu%C3%ADs_-_BR%29.jfif\" /></div>\n<p>Բրազիլիայի հյուսիսում զարմանալի վայր կա, որտեղ միաժամանակ ավազե արահետներ և լճեր գոյություն ունեն։ Խոսքը գնում է մինի-ջրամբարների մասին, որոնք գտնվում են անապատի մեջ և անձրևային ծագում ունեն։&nbsp; Այս արտասովոր վայրն անվանում են թաց անապատ կամ Մարիանյան ծածկոց։ Արահետներն ունեն սպիտակաթույր երանգ, իսկ թռչնի թռիչքի բարձրությունից իսկապես հիշեցնում են սպիտակ ծածկոց։ Անապատը գոյացել է ջրի և քամու շարժման արդյունքում։ Այս շրջանով հոսող գետերը ավազը դեպի Ատլանտիկա են տանում, իսկ օվկիանոսն այն հետ է բերում։ Աշնանն ուժգին ցիկլոնները տոննաներով ավազ են տանում մայրցամաքի խորքերը՝ մի քանի մետր բարձրություն ունեցող արահետներ կազմավորելով։&nbsp; Երբ սկսվում է անձրևների սեզոնը, ավազի վրա մեծ քանակությամբ ջուր է լցվում։ Այս գեղեցիկ տեսարանը բազմաթիվ զբոսաշրջիկների է գրավում։ Ավազի տակ կավի շերտ գոյություն ունի, որը չի թողնում, որպեսզի ջուրը ներծծվի։ Այդ իսկ պատճառով ողջ սեզոնի ընթացքում՝ հունվարից մինչև ամառվա վերջ, հնարավոր է ականատես լինել յուրօրինակ երևույթին՝ թաց անապատին։&nbsp; Աշնանն անշունչ տեսք ստացող արահետները կարծես վերականգնվում են, իսկ լճերը լցվում կենդանական և բուսական բազմազանությամբ։ Ջրերում հայտնվում են գորտեր, ձկներ և կրիաներ։ Դրանցից շատերը չոր եղանակին թաքնվում են ավազի մեջ։&nbsp;</p>', '', '/upload/768893.jpg', 'https://blognews.am/arm/news/768893/tac-anapat-kam-marianyan-tsatskoc%E2%80%A4-bnutyan-amenagexecik-erevuytneric-meki-masin.html', 'Բրազիլիայի հյուսիսում զարմանալի վայր կա, որտեղ միաժամանակ ավազե արահետներ և լճեր գոյություն ունեն։', 'На севере Бразилии есть удивительное место, где одновременно есть и песчаные тропы, и озера.', 0, 0, 5, '2022-05-09 16:49:27', '2022-05-10 17:09:32'),
(20, 9, 'Նոր Զելանդիայում իշխանություններն արգելում են ամենամաքուր լճում լողալ․ ո՞րն է պատճառը', '', '<p>Մոլորակի վրա բազմաթիվ գեղեցիկ վայրեր կան, որոնք կարելի է բնության իրական հրաշքներ համարել։ Սակայն, ցավոք, դրանք հաճախ տուժում են մարդու բացասական ազդեցության պատճառով։&nbsp; Սակայն, բարեբախտաբար, Երկրի վրա դեռ գոյություն ունի մարդու կողմից չապականված վայր։ Թե որն է դրա պատճառը՝ խիստ օրենսդրությունը, թե գեղեցիկի հանդեպ հարգանքը, անհայտ է։&nbsp; Խոսքը գնում է Նոր Զելանդիայում գտնվող Կապույտ լճի մասին։ Անվանումը հրաշալիորեն նկարագրում է լճի աննկարագրելի տեսքը։ Այն գտնվում է Նելսոն ազգային պարկի տարածքում՝ Հարավային կղզու հյուսիսում։ Պարկն, ընդհանրապես, բավականին հայտնի է զբոսաշրջիկների շրջանում։ Վերջիններիս գրավում են ջրվեժները, ժայռերն ու անտառները։ Սակայն Կապույտ լիճը գլխավոր տեսարժան վայր է համարվում։&nbsp; Լճի առանձնահատկությունը դրա բյուրեղյա մաքրությունն է։ Այն պաշտոնապես համարվում է ամենամաքուրը մոլորակի վրա։ Լիճը լցնում են մոտակայքում գտննվող Կոնստանս ջրամբարի ջրերը։ Դրանք անցնում են լեռների միջով, որտեղ էլ մաքրվում են և զտվում։&nbsp; Ջուրն այնքան թափանցիկ է, որ հեռվից այն հնարավոր է շփոթել օդի հետ։ Գիտնականներին հաջողվել է պարզել, որ տեսանելիությունը լճում հասնում է 70 մետրի։ Այս ցուցիչը շատ մոտ է թորած ջրի թափանցիկությանը, որը կազմում է 80 մետր։&nbsp; Կապույտ լճում լողալ խստորեն արգելվում է։ Նորզելանդական իշխանությունները վախենում են, որ լիճը արագ ու անվերադարձ մարդիկ կաղտոտեն։ Զբոսաշրջիկներին արգելում են նաև մերակայքում գտնվող անտառ այցելել, որտեղ մարդը ոտք չի դրել։ Իրականում լճում լողալը նաև անհարմար կլիներ, քանի որ ջուրը չափազանց սառն է։ Ջրի ջերմաստիճանը դրանում չի անցնում 8 աստիճան Ցելսիուսից։</p>', '', '/upload/765304.jpg', 'https://blognews.am/arm/news/765304/nor-zelandiayum-ishkhanutyunnern-argelum-en-amenamaqur-ltchum-loxal%E2%80%A4-orn-e-pattchary.html', 'Մոլորակի վրա բազմաթիվ գեղեցիկ վայրեր կան, որոնք կարելի է բնության իրական հրաշքներ համարել։', 'На планете много красивых мест, которые можно считать настоящими чудесами природы.', 0, 0, 3, '2022-05-09 16:56:03', '2022-05-10 17:09:12'),
(21, 9, 'Օր, երբ Նիագարայի ջրվեժը կանգ առավ. ո՞րն էր արտասովոր երևույթի պատճառը', '', '<div><img src=\"https://blognews.am/static/content/Angel/content_осуш.jpg\" /></div>\n<div><img src=\"https://blognews.am/static/content/Angel/content_работы.jpg\" /></div>\n<div><img src=\"https://blognews.am/static/content/Angel/content_частично_замерз.jpg\" /></div>\n<p>Նիագարայի ջրվեժը բնության հրաշալիքներից մեկն է, որն ապշեցնում է իր վեհությամբ և չափսերով։ Անհնար է պատկերացնելը, որ որևէ պահի այն կարող է կանգ առնել։ Սակայն նման երևույթ տեղի է ունեցել և կկրկնվի 7 տարի անց։ Նիագարայի ջրվեժն առանձնացնում է Կանադան և Նյու Յորք նահանգի արևմտյան հատվածը։ Այստեղ է ընկած Նիագարա գետի հունը, որն իրար է միացնում երկու ջրամբար։ Սակայն Էրին և Օնտարիոն գտնվում են տարբեր բարձրությունների վրա։ Դրանց միջև 30 մ բարձրության տարբերություն կա, որտեղից էլ հոսում է ջուրը։ Նիագարայի հոսքը միասնական հաակարգ չէ։ Այն կազմված է մի քանի մասին, որոնցից առավել խոշորը կոչվում է &laquo;Պոդկովա&raquo;։ Այն տեսնել հնարավոր է Կանադայի տարածքից։ &laquo;Պոդկովային&raquo; է պատկանում ջրի ծավալի 80%-ը, իսկ Ամերիկային մնում է միայն երկու համեստ տարածք՝ &laquo;Ամերիկյան ջրվեժն&raquo; ու սեկտոր &laquo;Ֆատան&raquo;։ Ցամաքումը վերաբերում էրմիայն &laquo;Ամերիկյան ջրվեժին&raquo;։ 1969 թ. տարածքում անցկացրին ջրահեռացում և ջրամբարի գործունեությունը դադարեցվեց 5 ամսով։ Այդ ժամանակահատվածում գիտնականները հետևում էին էրոզիայի ընթացքին։ Կանադական հատվածում զբոսաշրջային մասն անձեռնմխելի մնաց։ Նախատեսվում մի քանի տարի անց կրկին ջրահեռացում իրականացնել՝ վերանորոգման աշխատանքների համար. տարածքում հարկավոր է երկու հարյուրամյա կամուրջ վերականգնել։ Առաջին ջրահեռացման ժամանակ հայտնաբերվել է ավելի քան մեկ միլիոն մետաղադրամ և տարատեսակ այլ իրեր։ Այդ ժամանակից ի վեր անցել է 50 տարի։ Զբոսաշրջիկների թիվն այս ընթացքում ավելացել է մի քանի անգամ։ Ներկայումս հնարավոր կլինի ջրվեժի ջրերի տակից հայտնաբերել նաև հեռախոսներ, մանր տեխնիկա և զբոսաշրջային աքսեսուարներ։ Սական Նիագարայի ջրվեժը ցամաքել է ոչ միայն մարդու ներգոծության հետևանքով։ 1848 թ. նման բան տեղի է ունեցել բնական պատճառներով։ Այդ օրը տեղի բնակիչները չեն լսել ջրի մշտական հոսքի ձայնը։ Ռեկորդային մարտյան սառնամանիքները &laquo;կանգնեցրել են&raquo; ջրվեժը։ Ջրի հոսքի փոխարեն մարդիկ ականտես են եղել հսկայական սառցալեզվակների։ Այս դեպքից հետո բնակչությունը բաժանվեց երկու մասի։ Առաջին խումբն այցելեց եկեղեցի՝ երկնային ուժերից խնդրելու դադարեցնել բնության անոմալիան։ Երկրորդ խմբի ներկայացուցիչներն սկսեցին զբաղվել ջրվեժի ուսումնասիրմամբ։ Սառույցների տակ հայտնաբերվեց 1812 թ. ռազմական գործողությունների ժամանակ օգտագործված զենք, հնդկացիների առօրա իրեր, սոճու հսկայական կոճղեր։ Վերջին գտածոն հատկապես ուրախացրել էր ամերիկացիներին, քանի որ այդ ծառի փայտանյութն օգտակար էր թանկարժեք կահույք պատրաստելու համար։</p>', '', '/upload/763465.jpg', 'https://blognews.am/arm/news/763465/or-erb-niagarayi-jrvezhy-kang-arav-orn-er-artasovor-erevuyti-pattchary.html', 'Նիագարայի ջրվեժը բնության հրաշալիքներից մեկն է, որն ապշեցնում է իր վեհությամբ և չափսերով։', 'Ниагарский водопад – одно из чудес природы, которое поражает своим величием և размерами.', 0, 0, 2, '2022-05-09 16:56:58', '2022-05-10 17:08:49'),
(22, 3, 'Գարիկ Կարապետյանը՝ երիտասարդների աշխարհի չեմպիոն', '', '<p>Հայաստանի թիմը երկրորդ ոսկե մեդալը նվաճեց ծանրամարտի աշխարհի երիտասարդական առաջնությունում:</p>\n<p>Հունաստանի Հերակլիոն քաղաքում ընթացող առաջնության 96 կգ քաշայինների պայքարում Գարիկ Կարապետյանը երկրամարտի 370 կգ արդյունքով հռչկավեց երիտասարդների աշխարհի չեմպիոն:</p>\n<p>Պոկում վարժությունը Կարապետյանը պայքարը սկսեց ամենածանր քաշից, երբ բոլոր մասնակիցները գրեթե ավարտել էին մրցելույթները: Առաջին մոտեցմամբ պոկեց 160 կգ, երկրորդով՝ 164 կգ, ապա 170 կգ եւ նվաճեց այս վարժության փոքր ոսկե մեդալը:</p>\n<p>Հրում վարժությունում առաջին մոտեցմամբ բարձրացրեց 190 կգ, երկրորդ մոտեցմամբ հրեց 195 եւ երրորդ՝ 200 կգ: Այս վարժությունում Գարիկ Կարապետյանը բավարարվեց փոքր բրոնզե մեդալով: Երկամարտի 370 կգ արդյունքով Գարիկ Կարապետյանը նվաճեց երիտասարդների աշխարհի չեմպիոնի տիտղոսը:</p>', '', '/upload/131455.png', 'https://armlur.am/1184825/', 'Երկամարտի 370 կգ արդյունքով Գարիկ Կարապետյանը նվաճեց երիտասարդների աշխարհի չեմպիոնի տիտղոսը:', 'Гарик Карапетян завоевал титул чемпиона мира среди молодежи с результатом 370 кг.', 0, 0, 2, '2022-05-09 17:00:52', '2022-05-10 17:08:29'),
(23, 1, 'Իշխան Սաղաթելյանը ներկայացրեց, թե ինչպիսի Հայաստան է ուզում «Դիմադրություն» շարժումը և ինչպես են հասնելու դրան', '', '<p>Ֆրանսիայի հրապարակում &laquo;Դիմադրություն&raquo; շարժման այսօրվա համազգային հանրահավաքին ելույթ ունեցավ ԱԺ փոխնախագահ Իշխան Սաղաթելյանը, որը ներկայացրեց մի քանի քայլեր, գործողություններ, սկզբունքներ, որոնք պետք է իրականացվեն այս իշխանությունների հեռացումից հետ։</p>\n<p>Իշխան Սաղաթելյանը նշեց, որ ապազգային, ապիկար իշխանության պատճառով Արցախը, Հայաստանի անկախությունը վտանգված են և այս վիճակը պարտավոր ենք շտկել, իսկ ուժեղ Հայաստան կերտելու համար նախ պետք է հեռացնել գործող իշխանությունների և բերել իշխանություն, որը կապահովի թուրք-ադրբեջանական արտաքին ագրեսիայի զսպումը՝ արդիականացնելով Հայաստանի ու Արցախի միասնական անվտանգության համակարգը: Այն հիմված է ռազմավարական գործընկեր երկրների ներգրավման, Հայոց զինված ուժերի վերակառուցման, նոր որակի միջազգային դիվանագիտության վրա, կվերահաստատի Արցախի ժողովրդի անվտանգության եւ ինքնորոշման իրավունքի երաշխավոր լինելու՝ Հայաստանի Հանրապետության հանձնառությունը՝ բացառելով Արցախի որեւէ կարգավիճակ Ադրբեջանի կազմում և առանց Հայաստանի հետ հուսալի ցամաքային կապի Արցախի անկլավային վիճակ: Գործուն քայլերը ուղղված են լինելու 1994 թ. ԵԱՀԿ ձեւաչափով եւ նպատակներով բանակցային գործընթացի վերականգնմանը:</p>\n<p>Սաղաթելյանը նշեց, որ նոր իշխանությունը պետք&nbsp; է բացառի Հայաստանի ինքնիշխանության եւ տարածքային ամբողջականության հաշվին հաղորդակցության ուղիների ապաշրջափակման անվան տակ միջանցքների տրամադրումը, Ադրբեջանի կողմից ուժի կիրառման եւ ուժի կիրառման սպառնալիքի ներքո չի իրականացնի սահմանազատում և սահմանագծում , Հայաստան-Թուրքիա հարաբերություններում կբացառի որեւէ համաձայնություն, որը հարցականի տակ կդնի հայ ժողովրդի հայրենազրկման եւ ցեղասպանության իրողությունը, ինչպես նաեւ հայության հոգեւոր-մշակութային ժառանգության կրողը լինելու իրավունքը։&nbsp;</p>\n<p>Նոր իշխանությունը, նրա խոսքով՝ առաջնահերթ կարեւորություն կտա պարենային անվտանգությանը եւ պետական պահուստի խնդիրներին, կարևորագույն խնդիր կդիտարկի հայ ռազմագերիների, պատանդառվածների ու պահվող այլ անձանց վերադարձը և կվարի պրոֆեսիոնալիզմի եւ հստակ չափորոշիչներով կադրային քաղաքականություն՝ ապահովելով կառավարման համակարգային ճգնաժամի հաղթահարումը:</p>\n<p>&laquo;Պետության կառավարումից իսպառ բացառվելու են պոպուլիզմը, սուտը, կեղծիքը, անգործությունը, անպատասխանատվությունը, կոռուպցիայի բացահայտ և թաքնված ցանկացած դրսևորում։ Խրախուսվելու է աշխատասիրությունը, պետական և ազգային մտածելակերպը, անհատական և կոլեկտիվ պատասխանատվությունը: Կձևավորվի գործունակ և արդյունավետ պետական կառավարման նոր կառուցվածք: Առաջնահերթ կարգով վերականգնվելու են գյուղատնտեսության և մշակույթի նախարարությունները, վերակառուցվելու է իրավապահ համակարգը՝ դրանք ամբողջությամբ ծառայեցնելով քաղաքացուն: Քաղաքում և գյուղում բնակվող մարդու բարեկեցությունը լինելու է վարվող ոլորտային քաղաքականության, պաշտոնատար անձի գործունեության գնահատման չափանիշ: Տնտեսական աճը ուղղակիորեն պետք է արտացոլվի կենսամակարդակի վրա՝ ապահովելով եկամուտների կայուն աճ։ Մարդկանց վարկային և պարտքային բեռը չպետք է զրկի նրանց արժանապատիվ ապրելու հնարավորությունից&raquo;,&ndash; ասաց նա:</p>\n<p>Իշխան Սաղաթելյանը նաև նշեց, որ նոր իշխանությունները վերականգնելու են ներքին համերաշխությունը, հասնելու են համահայկական ներուժի մեկտեղմանը՝ վերացնելով խորացող ատելությունը, պառակտվածությունը և անտարբերությունը:</p>\n<p>&laquo;Կբացառվեն մեր կյանքում անցյալի եւ ներկայի արատավոր բարքերը՝ երկիրը զերծ պահելով ցնցումներից։ Սիրելի՛ հայրենակիցներ, այս կոնտեքստում ուզում եմ անդրադառնալ մի հարցի՝ այս օրերին հաճախ տրվող․ շատ է խոսվում՝ հանրահավաքի վայրում հայտնվում են մարդիկ, որոնք անընդունելի են և ․․․․․․․օդիոզ, նախկինում պաշտոն զբաղեցրած։</p>\n<p>Սփյուռքի, Հայ Առաքելական սուրբ եկեղեցու և ազգային մյուս կառույցների անմիջական ներգրավմամբ կձևավորեն ներքին ու արտաքին մարտահրավերները դիմագրավելու համահայկական հստակ օրակարգեր&raquo;,&ndash; ասաց նա։</p>\n<p>Իշխան Սաղաթելյանն ընդգծեց, որ այդպիսի իշխանություն ձևավորելու համար անհրաժեշտ է.</p>\n<p>● Ազգային համաձայնության/ժամանակավոր կառավարություն: Սա նշանակում է՝ դերակատար քաղաքական ուժերը ստանձնում են հավաքական պատասխանատվություն՝ ձևավորելով պետության կառավարման յուրահատուկ &laquo;ազգային հավաքական&raquo;, որը կբացառի մեկ անձի ներքո իշխանության կենտրոնացումը:</p>\n<p>● Սրանց հեռացնելուց հետո&nbsp; կհրապարակենք այն 250 բարձրակարգ մասնագետների անունները, որոնք առաջիկա տարիներին կլինեն մեր կառավարման համակարգի ու պետական ապարատի հենասյունը։ Նրանցից չի պահանջվելու ո՛չ կուսակցականություն, ո՛չ հավատարմություն ինչ-ոչ մեկի անձի, ո՛չ ղազագիր։ Նրանք ուսապարկի կարգավիճակ չեն ունենալու, այդ մարդիկ ունակ էլ չեն այդպիսի վարքագծի։ Նրանցից շատ-շատերին դուք ճանաչում եք, նրանք մեր մեջ են։ Նրանք արդեն 4 տարի ու այսօր էլ պայքարում են հանուն մեր երկրի ազատության, հանուն ամուր պետականության, հանուն Արցախի, հանուն հզորացման ու թուրքին մեր երկրից քշելու։ Նրանք գրեթե բոլորն այստեղ են, պայքարի թոհուբոհում, հրապարակում, փողոցներում կամ ազատ մամուլի եթերում։ Նրանք իսկական հայրենասերներ են, Պրոֆեսիոնալներ, որոնց գործունեությամբ կարող է հպարտանալ ցանկացած պետական համակարգ ցանկացած երկրում։</p>\n<p>● Բացառել կարեւորագույն հարցերի վերաբերյալ վարչապետի միանձնյա որոշումների կայացումը։ Վարչապետը պաշտպանության և անվտանգության հարցերով կարող է որոշումներ գործադրել միայն անվտանգության խորհրդի հետ միաձայնությամբ։ Վարչապետի գերլիազորությունները սահմանափակվելու են, ներդրվելու են իրական պառլամենտական կառավարման մեխանիզմներ։ Ընդունվելու է նոր օրենք անվտանգության խորհրդի և կառավարության կառուցվածքի ու գործունեության վերաբերյալ։</p>\n<p>&nbsp;● Պետք է արագ ձևավորել զինված ուժերի բարձրագույն հրամանատարական կազմ, ուժեղացնել դիվանագիտական կորպուսը՝ համակարգ վերադարձնելով պրոֆեսիոնալ կադրերին, նվազագույնի հասցնելով քաղաքական նշանակումները։</p>\n<p>ԱԺ փոխնախագահը նաև խոսեց ժամանակավոր կառավարության մասին։ Նա նշել է, որ անմիջապես կձևավորվի քաղաքական ու մասնագիտական իմաստով բարձրակարգ բանակցային խումբ, որը ոչ թե վախի, պարտվողականության ու թրքամետության դիրքերից, այլ բացառապես մեր ազգային-պետական շահերից ելնելով կբանակցի Ադրբեջանի, հարկ եղած դեպքում՝ նաև Թուրքիայի հետ։ Կբանակցի հստակ ու լավ մշակված հայեցակարգով։ Խումբ, որը երբեք ոչ մի ազգային հարցի նշաձող չի իջեցնի։ Նշեմ նաև Արցախում տեղակայված ռուս խաղաղապահների դերի նվազեցումն անթույլատրելի է։</p>\n<p>&laquo;Հետևողական քայլերով մենք կվերացնենք ատելության մթնոլորտը երկրում, մարդիկ այլևս չեն հետապնդվի իրենց քաղաքական հայացքների համար, իսկ օրենքը հավասար է լինելու բոլորի համար՝ անկախ&nbsp; կարգավիճակից։</p>\n<p>Հայաստանը կրկին դառնալու է ներդրումների համար գրավիչ երկիր, իսկ Երևանը կրկին դառնալու է համայն հայության մայրաքաղաքը։</p>\n<p>Իրավիճակի կայունացումից հետո (ենթադրաբար 1-1.5 տարի հետո) կազմակերպվելու են ազատ, արդար, մրցակցային արտահերթ ընտրություններ&raquo;,&ndash; ասել է նա։</p>\n<p>Պատասխանելով հարցին, թե ինչպե՞ս հասնել դրան, Իշխան Սաղաթելյանն ասել է, որ ազգակործան վարչախմբին իշխանությունից զրկելը ենթադրում է երկու փուլ՝ երկիշխանության առաջացում եւ գործող իշխանությունների հեռացում։ Երկիշխանությունն արդեն կայանում է, որի դրսեւորումներն են՝</p>\n<p>1․ձեւավորվել է լայն համախմբում՝ հանրային տարբեր շերտերի ու շրջանակների աջակցությամբ։ Այս հրապարակը դարձել է գործոն։</p>\n<p>&nbsp;2. թե՛ երկրի ներսում, թե՛ արտաքին աշխարհի համար փաստվել է, որ Նիկոլը երկիրը նոր զիջումների տանելու համար լեգիտիմ մանդատ չունի,</p>\n<p>&nbsp;3. հանրային ճնշմամբ իշխանությունները ստիպված արդեն փորձում են քողարկել իրենց հայրենադավ ծրագրերը,</p>\n<p>4. իշխանության միակ հենարանը մնացել է ոստիկական բիրտ ուժը,</p>\n<p>5. երկրում քաղաքական ճգնաժամ է։ 2021թ. ընտրությունները, որ մի հարց չեն լուծել։</p>\n<p>&laquo;Հստակ կարող ենք արձանագրել, որ իշխանությունների հեռացման համար անհրաժեշտ բոլոր պայմաններն առկա են։ Իշխանությունն անկարող է կասեցնել ժողովրդային շարժումը․ Ըստ այդմ՝ մեր առաջիկա խնդիրներն են՝</p>\n<p>1․ Ի ցույց դնել, որ փաստացի իշխանությունը հրապարակներում է։</p>\n<p>2․ Ի ցույց դնել, որ իշխանությունն անկարող է ժողովրդի ցասումի առջև։ Այլ խոսքով՝ ժողովուրդը պետք է դառնա իրողության լիարժեք տերը, թելադրի իր կամքը՝ փակելով ու բացելով ինչ ուզի և երբ ուզի։ Սա վերջնականապես ակնհայտ կդարձնի Նիկոլի փաստացի իշխանազրկումը, որից հետո Նիկոլի հրաժարականը կդառնա անխուսափելի։</p>\n<p>Ի՞նչ է պետք դրա համար։ Ընդամենը մի բան, սիրելի՛ հայրենակիցներ, մեծացնել անհնազանդության ակցիաների ծավալները, մասնակիցների քանակը և դառնալ իրադրության տերը․ սա է առաջիկա օրերին մեր առջև դրված խնդիրը։</p>\n<p>Ես այստեղից դիմում եմ Հայաստանի, Արցախի, Սփյուռքի քաղաքական ուժերին, հասարակական ուժերին, սոցիալական բոլոր շերտերին, մասնագիտական շրջանակներին, կարող անհատներին, հոգևոր-մշակութային-կրթական, բիզնեսի, ՏՏ ռազմական գործիչներին, բոլորին՝ Հայաստանն ամենքինս է։ Կանգնենք կողք կողքի։ Մենք կարող ենք լուծել ցանկացած խնդիր։</p>\n<p>Այստեղ կուսակցական պայքար չկա, արտոնյալ խմբեր ու անհատներ չկան, այստեղ համաժողովրդական ընդվզում է՝ հանուն յուրաքանչուրիս արժանապատվության, հանուն ազգային ինքնության, հանուր արդար, ուժեղ , համերաշխ պետության, հանուն Արցախի, Հայաստանի։</p>\n<p>Վստահե՛ք, մասնակցե՛ք, մենք հաղթելու ենք&raquo;,&ndash; ասաց նա։</p>\n<p><iframe src=\"https://www.youtube.com/embed/fptSXyI-C8Y\" width=\"620\" height=\"349\"></iframe></p>', '', '/upload/Screen%20Shot%202022-05-10%20at%2020-07-59.png', 'https://news.am/arm/news/700893.html', 'Իշխան Սաղաթելյանը նշեց...', 'Ишхан Сагателян упомянул...', 0, 0, 51, '2022-05-09 17:01:52', '2022-05-12 09:53:35'),
(24, 1, 'Պետք է 100 տոկոս ապահովվի Նիկոլ Փաշինյանի և իշխանության անդամների ֆիզիկական անվտանգությունը. Վանեցյան', '', '<p>Ֆրանսիայի հրապարակում արդեն 5-րդ օրն է իրականացվում է նստացույց: Iravaban.net-ի հետ զրույցում Ազգային ժողովի &laquo;Պատիվ ունեմ&raquo; խմբակցության ղեկավար Արթուր Վանեցյանն ասաց, որ իրենք միշտ ասել են՝ անհնազանդության ակցիաները խախաղ բնույթ են կրում և տեղավորվում են օրենքում:</p>\n<p>&laquo;Ինձ ամեն օր մոտենում են հարյուրավոր, հազարավոր մարդիկ և ասում են՝ գնանք Կառավարության շենքը գրավենք ԱԺ-ն գրավենք, գնանք սրան մորթենք, գնանք սրան սպանենք, բայց մենք հասկանում ենք, որ չի կարելի նման ձևով հարցերը լուծել: Մենք շարունակելու ենք ճնշումներն ավելացնել իշխանության վրա&raquo;,-ասաց Արթուր Վանեցյանը:</p>\n<p>Մանրամասները՝ տեսանյութում:</p>', '', '/upload/Screen%20Shot%202022-05-10%20at%2020-57-55.png', 'https://iravaban.net/381655.html', 'Ֆրանսիայի հրապարակում արդեն 5-րդ օրն է իրականացվում է նստացույց: Iravaban.net-ի հետ զրույցում Ազգային ժողովի «Պատիվ ունեմ» խմբակցության ղեկավար Արթուր Վանեցյանն ասաց, որ իրենք միշտ ասել են՝ անհնազանդության ակցիաները խախաղ բնույթ են կրում և տեղավորվում են օրենքում: «Ինձ ամեն օր մոտենում են հարյուրավոր, հազարավոր մարդիկ և ասում են՝ գնանք Կառավարության շենքը գրավենք ԱԺ-ն գրավենք, գնանք սրան...  Read more &raquo;', '', 0, 0, 7, '2022-05-09 17:02:15', '2022-05-11 07:49:46'),
(25, 6, 'Փարիզի վերաքննիչ դատարանը մեղմացրել է Ֆրանսիայի նախկին վարչապետ Ֆրանսուա Ֆիյոնի դատավճիռը', '', '<p>Փարիզի վերաքննիչ դատարանը մեղմացրել է Ֆրանսիայի նախկին վարչապետ Ֆրանսուա Ֆիյոնի դատավճիռը, գրում է Politico-ն։</p>\n<p>Ի սկզբանե Ֆիյոնը 2020 թվականի հունիսին դատապարտվել էր հինգ տարվա ազատազրկման, այդ թվում՝ երեք տարվա պայմանական ազատազրկման՝ պետական միջոցների յուրացման համար, սակայն վերաքննիչ դատարանը պատիժը կրճատել էր մինչեւ չորս տարի, որից երեքը՝ պայմանական։ Դժվար թե Ֆիյոնը ժամանակ անցկացնի բանտում, ամենայն հավանականությամբ նրան կարող են հրահանգել ոտքին կարգավորող սարք կրել։</p>\n<p>Ֆիյոնը վերաքննիչ բողոք է ներկայացրել անցյալ նոյեմբերին, եւ թեեւ դատարանը կրճատել է պատիժը, սակայն ուժի մեջ է թողել 375 000 եվրո տուգանքը եւ նրան 10 տարով զրկել է պետական պաշտոններ զբաղեցնելու իրավունքից: Ֆիյոնի կնոջ՝ Պենելոպեի պատժաչափը նույնպես 3-ից կրճատվել է 2 տարվա պայմանական ժամկետով:</p>\n<p>Ֆիյոնների ընտանիքը եւ նախկին փոխվարչապետ Մարկ Ժուլոն դատարանի առաջ են կանգնել 2017 թվականին &laquo;կեղծ աշխատատեղերի&raquo; ստեղծման բացահայտումից հետո, ըստ որի Պենելոպե Ֆիյոնին պետական միջոցներից մոտ 1 միլիոն եվրո են վճարել, որի դիմաց նա գործնականում ոչ մի աշխատանք չի կատարել:</p>\n<p>Այս սկանդալը տապալել է Ֆիյոնի 2017 թվականի նախագահական արշավը, երբ պահպանողական թեկնածուն այն ժամանակ առաջատար էր:</p>', '', '/upload/default%20(1).jpg', 'https://news.am/arm/news/700891.html', 'Ի սկզբանե Ֆիյոնը 2020 թվականի հունիսին դատապարտվել էր հինգ տարվա ազատազրկման, այդ թվում՝ երեք տարվա պայմանական ազատազրկման՝ պետական միջոցների յուրացման համար․․․', '', 0, 0, 4, '2022-05-09 17:03:12', '2022-05-11 18:24:42'),
(26, 1, 'Անգամ Վեհափառը չի կարող ձեր մեղքերին թողություն տալ․ Աշոտ Մինասյանը՝ իշխանությանը', '', '<p>Բաշ-Ապարանի եւ Սարդարապատի հաղթանակներից 100 տարի անց Հայաստանի ապիկար իշխանությունները ոչ միայն ոչնչացրին արցախյան զորագնդերը, այլ նաեւ Արցախը հանձնեցին թուրքերին։ Այս մասին այսօր՝ մայիսի 9-ին, Ֆրանսիայի հրապարակում &laquo;Դիմադրություն&raquo; շարժման հանրահավաքի ժամանակ ասաց &laquo;Սիսական&raquo; ջոկատի հրամանատար Աշոտ Մինասյանը (Աշոտ Երկաթ):</p>\n<p>&laquo;Այս իշխանությունը Հայաստանը հանձնում է թուրքերին։ Սրանից 24 տարի առաջ իրենց հոգեւոր հայրը հայտարարում էր, որ հեռանում է խաղաղության իշխանությունը եւ գալիս է պատերազմի իշխանությունը։ Չորս տարի առաջ սրանք եկան եւ ստեղծեցին պատերազմի իշխանություն&raquo;,- նշեց Մինասյանը։&nbsp;</p>\n<p>Մինասյանը կոչ արեց համայն հայությանը, զինվորականին, ոստիկանին եւ մտավորականությանը միանալ շարժման պայքարին։</p>\n<p>&laquo;Եթե դուք այսօր կանգնած չեք հայ ժողովրդի կողքին, մտեք այնտեղ, որտեղից դուրս եք եկել։ Այսօր դատավճռի առջեւ է կանգնել իշխանությունը, վաղը պատասխան են տալու իրենց սերունդները, ինչպես 100 տարի հետո պատասխան տվեցին թուրքերի մնացորդները, ծնկի եկան ու խնդրում էին աղերսում էին, որ իրենց ներեն։ Դուք որտե՞ղ եք ձեր մեղքերը քավելու։ Անգամ Վեհափառը չի կարող ձեր մեղքերին թողություն տալ&raquo;,- ընդգծեց Մինասյանը։</p>', '', '/upload/default.jpg', 'https://news.am/arm/news/700896.html', 'Այսօր դատավճռի առջեւ է կանգնել իշխանությունը, վաղը պատասխան են տալու իրենց սերունդները․․․', 'Сегодня правительству предстоит приговор, завтра ответят их поколения ․․․', 0, 0, 4, '2022-05-09 17:05:07', '2022-05-11 01:23:32'),
(27, 4, 'Վատառողջության տիկին նախարարացու, իսկ քո, քո շեֆի ու իր թայֆի տեսակի դեմ ինչ որ օրենք կարա՞ք ընդունեք. դուք ավելի վատ եք անդրադառնում հայ ազգի առողջության վրա', '', '<p>Էն վատառողջության նախարարը սննդի օբյեկտներում աղամանների առկայության թեմայա մեջտեղ բերել, որ իբր կանխի մարդկանց շատ աղ օգտագործելն ու դրա հետևանքով առաջացած սիրտանոթային հիվանդությունները: Տիկին նախարարացու, ասեմ իմանաս, որ քո, քո շեֆի ու ձեր ողջ թայֆի իշխանության մնալը շատ ավելի վատ ա անդրադառնում հայ ժողովորդի դիմադրողականության վրա, քան սրտանոթային հիվանդություններն ու կորոնան միասին վերցրած: Տո դուք դաժե քաղցկեղից եք վատը: Ձեր տեսակի գործունեության դեմ ինչ որ օրենք կարա՞ք ընդունեք:</p>\n<p><iframe src=\"https://www.facebook.com/plugins/post.php?href=https%3A%2F%2Fwww.facebook.com%2Faram1307%2Fposts%2F2840084569623736&amp;show_text=true&amp;width=500\" width=\"500\" height=\"304\"></iframe></p>', '', '/upload/786908.jpg', 'https://blognews.am/arm/news/786908/vataroxjutyan-tikin-nakhararacu-isk-qo-qo-shefi-u-ir-tayfi-tesaki-dem-inch-or-orenq-karaq-ynduneq-duq-aveli-vat-eq-andradarnum-hay-azgi-aroxjutyan-vra.html', 'Վատառողջության տիկին նախարարացու, իսկ քո, քո շեֆի ու իր թայֆի տեսակի դեմ ինչ որ օրենք կարա՞ք ընդունեք. դուք ավելի վատ եք անդրադառնում հայ ազգի առողջության վրա', 'Госпожа министр здравоохранения, можете ли вы принять какой-либо закон против вас, вашего босса и типа его банды? Вы хуже влияете на здоровье армянского народа', 0, 0, 3, '2022-05-09 17:07:33', '2022-05-10 17:06:52'),
(28, 4, 'Որո՞նք են օմիկրոն շտամով վարակված երեխաների ախտանիշները․ մասնագետը պարզաբանում է', '', '<p>Auroranews.am-ը գրում է. Օմիկրոն շտամով վարակված երեխաները ավելի հաճախ են ունենում ախտանիշներ։ Մասնավորապես, նրանց մոտ կարող է լինել կոկորդի ցավ, թուլություն, հազ, փորլուծություն և ցան, կիրակի օրը լրագրողներին ասաց ՌԴ Առողջապահության նախարարության Երեխաների առողջության ազգային հետազոտական կենտրոնի տնօրեն Անդրեյ Ֆիսենկոն, հայտնում է ՏԱՍՍը։ &laquo;Օմիկրոնը խիստ վարակիչ է: Այսօր երեխաները շատ ավելի հաճախ են վարակվում դրանով, քան նոր կորոնավիրուսային վարակի այլ շտամներով։ Այժմ երեխաների մոտ նկատվում է մի ամբողջ ախտանիշային համալիր՝ մարմնի բարձր ջերմաստիճան, թուլություն, գլխացավ, քթահոսություն, հազ, կոկորդի և կոկորդի ցավ, հաճախ լուծ և որոշ դեպքերում՝ ցան։ Ուզում եմ նշել, որ Omicron-ն ազդում է վերին շնչուղիների վրա և ավելի հաճախ առաջացնում բրոնխիտ, որի ոչ ճիշտ բուժումը կարող է հանգեցնել բարդությունների&raquo;,- ասաց նա։ Փորձագետը նշեց, որ չպատվաստված երեխային հնարավոր է պաշտպանել միայն այն դեպքում, եթե նրան շրջապատող մեծերը պահպանեն բոլոր կանխարգելիչ միջոցառումները։&nbsp; &laquo;Երեխաների անվտանգությունն ու առողջությունը կախված է նրանից, թե մեծահասակները որքան պատասխանատվությամբ են մոտենում այս բոլոր պարզ միջոցների պահպանմանը&raquo;,-&nbsp;եզրափակել է Ֆիսենկոն։</p>', '', '/upload/784882.jpg', 'https://blognews.am/arm/news/784882/oronq-en-omikron-shtamov-varakvats-erekhaneri-akhtanishnery%E2%80%A4-masnagety-parzabanum-e.html', 'Օմիկրոն շտամով վարակված երեխաները ավելի հաճախ են ունենում ախտանիշներ։ Մասնավորապես, նրանց մոտ կարող է լինել ...', 'Дети, инфицированные штаммом омикрон, чаще имеют симптомы. В частности, у них может быть...', 0, 0, 9, '2022-05-09 17:08:20', '2022-05-11 06:50:34'),
(29, 4, 'Սննդամթերքի որ տեսակներն են պաշտպանում մազաթափությունից ու ճաղատացումից. օգտակար խորհուրդներ', '', '<p>ArmLur.am-ը գրում է. Մարդու սննդակարգը շատ կարևոր գործոն է, որն ազդում է նրա ֆիզիկական ու հոգեկան առողջության, ինչպես նաև արտաքին տեսքի վրա, ուստի ճիշտ սննդակարգ կազմելը շատ կարևոր է յուրաքանչյուրի համար։ Սննդակարգը կարող է ազդել անգամ մազերի վրա՝ դառնալով մազածածկույթի ինչպես նոսրացման, այնպես էլ խտացման պատճառ, ուստի մազաթափության ու անգամ ճաղատացման դեմ կարելի է պայքարել ճիշտ կազմված սննդակարգի միջոցով։ Սննդաբան Ռայան Ռամանի խոսքով՝ գոյություն ունեն սննդամթերքի որոշ տեսակներ, որոնք սննդակարգում ներառելը կօգնի կանխել մազաթափությունը։ Ինչպես նշում է մասնագետը, առաջին հերթին նման հիանալի ազդեցությամբ է օժտված ավոկադոն, որի ընդամենը մեկ պտուղը պարունակում է վիտամին Е-ի ՝ գլխամաշկը պաշտպանող կարևորագույն տարրերից մեկի օրական անհրաժեշտ չափաբաժնի 20%-ը։ Բացի այդ, սննդաբանը խորհուրդ է տալիս հարստացնել սննդակարգը բատատով՝ քաղցր կարտոֆիլով, որը բետա-կարոտինի հիանալի աղբյուր է և նպաստում է մազերի աճի խթանմանը։ Մասնագետն ընդգծում է, որ բոլոր անհրաժեշը սննդարար նյութերը պարունակող բալանսավորված սննդակարգը խթանում է մազերի աճը՝ օգնելով պայքարել մազաթափության դեմ։ Ընդ որում՝ սա հատկապես կարևոր է այն դեպքերում, երբ մազաթափությունը պայմանավորված է լինում վատ սննդակարգով։</p>', '', '/upload/784196.jpg', 'https://blognews.am/arm/news/784196/snndamterqi-or-tesaknern-en-pashtpanum-mazataputyunic-u-tchaxatacumic-ogtakar-khorhurdner.html', 'Մարդու սննդակարգը շատ կարևոր գործոն է, որն ազդում է նրա ֆիզիկական ու հոգեկան առողջության, ինչպես նաև արտաքին ...', 'Рацион человека является очень важным фактором, влияющим на его физическое и психическое здоровье, а также на его внешнее...', 0, 0, 3, '2022-05-09 17:09:06', '2022-05-10 17:06:03');
INSERT INTO `posts` (`id`, `category_id`, `hy_title`, `ru_title`, `hy_content`, `ru_content`, `image`, `source`, `hy_description`, `ru_description`, `is_general`, `has_video`, `viewed`, `created_at`, `updated_at`) VALUES
(30, 4, 'ՀՀ-ում հայտնաբերվել է Գրիպի Ա տեսակի H3 ենթատեսակի վարակի 12 դեպք, որից 7-ը՝ 0-18 տարեկանների, իսկ 5-ը՝ հղի կանանց շրջանում․ ԱՆ', '', '<p>Գրիպի դետքային համաճարակաբանական հսկողության շրջանակներում վերցված նմուշների լաբորատոր հետազոտության արդյունքում ներկա պահին հայտնաբերվել է Գրիպի Ա տեսակի H3 ենթատեսակի վիրուսի 12 դեպք, որից 7-ը ամբուլատոր-պոլիկլինիկական կազմակերպություններ դիմելիության արդյունքում՝ 0-18 տարեկանների, իսկ 5-ը՝ հղի կանանց շրջանում, որոնք հոսպիտալացվել են &laquo;Վերարտադրողական առողջության, պերինատոլոգիայի, մանկաբարձության և գինեկոլոգիայի հանրապետական ինստիտուտ&raquo; ՓԲԸ-ում: Հղիներից 1-ը ծննդալուծվել է, այս պահին վիճակը կայուն է, 2-ը դուրս են գրվել, մյուս 2-ի վիճակը գնահատվում է բավարար։ Այս մասին հայտնում են Առողջապահության նախարարությունից։ Սեզոնային գրիպի կանխարգելման լավագույն և արդյունավետ միջոցը պատվաստումն է, որը հասանելի է և կիրառվում է արդեն տասնյակ տարիներ: Կորոնավիրուսային հիվանդության (COVID-19) համավարակի պայմաններում հատկապես կարևորվում է սուր շնչառական վարակների, այդ թվում՝ սեզոնային գրիպի կանխարգելումը՝ նշված վարակներով միաժամանակյա հիվանդացման ռիսկերը նվազեցնելու նպատակով: Հիշեցնենք, որ հանրապետության բոլոր պոլիկլինիկաներն ապահովված են սեզոնային գրիպի պատվաստանյութով, և ռիսկի խմբի բոլոր անձինք, ովքեր դեռ չեն ստացել սեզոնային գրիպի դեմ պատվաստում, կարող են դիմել իրենց հաշվառման պոլիկլինիկա և պատվաստվել: Պատվաստումները կատարվում են կամավորության սկզբունքով, անվճար, պետական պատվերի շրջանակներում՝ առաջնահերթություն տալով ռիսկի խմբերին՝ հղիներ, 6 ամսականից մինչև 5 տարեկան երեխաներ, 65 տարեկանից բարձր տարիքի անձինք, շաքարային դիաբետ, շնչառության օրգանների, սրտանոթային համակարգի, լյարդի, երիկամների քրոնիկ հիվանդություններ ունեցող անձինք, բուժաշխատողներ, զինծառայողներ, փակ կազմակերպություններում գտնվող անձինք և սպասարկող անձնակազմ: Ազգաբնակչության այլ խմբերի համար ևս պատվաստումը հասանելի է։ Մեկ անգամ ևս հորդորում ենք՝ եթե դեռ չեք պատվաստվել սեզոնային գրիպի դեմ, դիմեք պոլիկլինիկա և պատվաստվեք՝ պաշտպանելով Ձեզ և Ձեր մտերիմներին:</p>', '', '/upload/782460.jpg', 'https://blognews.am/arm/news/782460/hh-um-haytnabervel-e-gripi-a-tesaki-h3-entatesaki-varaki-12-depq-oric-7-y-0-18-tarekanneri-isk-5-y-hxi-kananc-shrjanum%E2%80%A4-an.html', 'Գրիպի դետքային համաճարակաբանական հսկողության շրջանակներում վերցված նմուշների լաբորատոր հետազոտության ...', 'Лабораторное исследование проб, взятых в рамках эпидемиологического надзора за гриппом...', 0, 0, 3, '2022-05-09 17:09:54', '2022-05-10 17:05:09'),
(31, 3, 'Սալահին խորհուրդ եմ տվել հեռանալ Լիվերպուլից․ Եգիպտոսի սպորտի նախարար', '', '<p>Armsport.am-ը գրում է. Եգիպտոսի սպորտի և երիտասարդության նախարար Աշրաֆ Սոբհին ասել է, որ &laquo;Լիվերպուլի&raquo; հարձակվող Մոհամեդ Սալահին խորհուրդ է տվել այլ ակումբում շարունակել կարիերան։ &laquo;Խորհուրդ եմ տվել նրան, որ իր ճամփորդությունը շարունակի ոչ թե &laquo;Լիվերպուլում&raquo;, այլ ուրիշ ակումբում, բայց հիմա նա ուզում է երկարաձգել պայմանագիրը&raquo;,- Սոբհիի խոսքերն է փոխանցում BBC-ն։ Սալահի պայմանագիրն ակումբի հետ ավարտվում է 2023-ի հունիսին։</p>', '', '/upload/790366.jpg', 'https://blognews.am/arm/news/790366/salahin-khorhurd-em-tvel-heranal-liverpulic%E2%80%A4-egiptosi-sporti-nakharar.html', 'Եգիպտոսի սպորտի և երիտասարդության նախարար Աշրաֆ Սոբհին ասել է, որ «Լիվերպուլի» հարձակվող Մոհամեդ Սալահին ...', 'Министр спорта и молодежи Египта Ашраф Собхи заявил, что нападающий «Ливерпуля» Мохамед Салах...', 0, 0, 2, '2022-05-09 17:11:07', '2022-05-10 17:04:47'),
(32, 3, 'Մալխաս Ամոյանը Եվրոպայի չեմպիոն է․ եզրափակչում հայ ըմբիշը հաղթեց թուրք մարզիկին', '', '<p>Բուդապեշտում ընթացող ըմբշամարտի Եվրոպայի առաջնությունում հունա-հռոմեական ոճի ըմբիշ Մալխաս Ամոյանը 77 կգ քաշային կարգում հռչակվեց Եվրոպայի չեմպիոն։ Եզրափակչում հայաստանցի մարզիկը հաղթեց Թուրքիան ներակայացնող Յունուս Բասարին։</p>', '', '/upload/790053.jpg', 'https://blognews.am/arm/news/790053/malkhas-amoyany-evropayi-chempion-e%E2%80%A4-ezrapakchum-hay-ymbishy-haxtec-turq-marzikin.html', 'Բուդապեշտում ընթացող ըմբշամարտի Եվրոպայի առաջնությունում հունա-հռոմեական ոճի ըմբիշ Մալխաս Ամոյանը 77 կգ ...', 'Борец греко-римского стиля Малхас Амоян (77 кг) на чемпионате Европы по борьбе в Будапеште...', 0, 0, 5, '2022-05-09 17:12:40', '2022-05-11 09:56:23'),
(33, 3, 'Հայաստանի հրաձիգները ևս երկու ոսկե մեդալ են նվաճել Եվրոպայի առաջնությունում', '', '<div><img src=\"https://lurer.com/disc/25-03-22/ab6e0831df41ce2507add8eb23a590e0.jpg\" /></div>\n<p>Lurer.com-ը գրում է. Հրաձգության Հայաստանի հավաքականի մարզիկները շարունակում են հաջող ելույթները Նորվեգիայի Համար քաղաքում մարտի 20-29-ն անցկացվող Եվրոպայի առաջնությունում։ Ինչպես&nbsp;տեղեկացրին Հայաստանի հրաձգության ֆեդերացիայից, երիտասարդների 10մ, վազող թիրախ, նորմալ վազք, 30+30 կրակոց ձևի պայքարում Գոռ Խաչատրյանը խոցել է 571 միավոր և հռչակվել չեմպիոն։ Աղջիկների նույն վարժությունում ոսկե մեդալակիր է դարձել Գոհար Հարությունյանը՝ խոցելով 556 միավոր։ Կանանց պայքարում բարձր արդյունք է ցույց տվել նաև Էլմիրա Կարապետյանը, ում անգամ բալիկ սպասելու կարգավիճակը չի խանգարել. նրան ընդհամենը 1 միավոր չի բավարարել, որ անցնի եզրափակիչ փուլ (օդամղիչ ատրճանակ, 10մ, 60 կրակոց, &nbsp;9-րդ տեղ, 573 միավոր)։ &nbsp;</p>', '', '/upload/789229.jpg', 'https://blognews.am/arm/news/789229/hayastani-hradzignery-evs-erku-oske-medal-en-nvatchel-evropayi-arajnutyunum.html', 'Հրաձգության Հայաստանի հավաքականի մարզիկները շարունակում են հաջող ելույթները Նորվեգիայի Համար քաղաքում ...', 'Спортсмены сборной Армении по стрельбе продолжают свои успешные выступления в городе Норвегии...', 0, 0, 3, '2022-05-09 17:13:07', '2022-05-10 17:04:09'),
(34, 3, 'Մարմնամարզիկ Վահագն Դավթյանը դարձել է աշխարհի գավաթի խաղարկության ոսկե մեդալակիր', '', '<p>Armsport.am-ը գրում է. Մարմնամարզիկ Վահագն Դավթյանը ոսկե մեդալ է նվաճել Կահիրեում անցկացվող սպորտային մարմնամարզության աշխարհի գավաթի խաղարկության երրորդ փուլում։ Դավթյանն օղակների վարժության եզրափակչում հավաքել է 14.800 միավոր և հաղթող ճանաչվել։ Երկրորդ տեղը գրավել է Թուրքիայի, իսկ երրորդ տեղը՝ Իտալիայի ներկայացուցիչը։</p>', '', '/upload/788511.jpg', 'https://blognews.am/arm/news/788511/marmnamarzik-vahagn-davtyany-dardzel-e-ashkharhi-gavati-khaxarkutyan-oske-medalakir.html', 'Մարմնամարզիկ Վահագն Դավթյանը ոսկե մեդալ է նվաճել Կահիրեում', 'Спортсмен Ваагн Давтян завоевал золотую медаль в Каире', 0, 0, 3, '2022-05-09 17:14:12', '2022-05-10 17:03:47'),
(35, 3, 'Տոկիո-2020․ Կարապետ Չալյանը բրոնզե մեդալի համար կգոտեմարտի ադրբեջանցի մարզիկի հետ', '', '<p>Երեկ հունահռոմեական ոճի ըմբիշ, 77 կգ քաշային կարգում Հայաստանի ներկայացուցիչ Կարապետ Չալյանը Տոկիոյի Օլիմպիական խաղերի կիսաեզրափակչում պարտություն էր կրել Ղրղզստանի ներկայացուցչից։ Չալյանն իր առաջին Օլիմպիական խաղերում չի կարողացել դուրս գալ եզրափակիչ ու այսօր՝ օգոստոսի 3-ին, կգոտեմարտի բրոնզե մեդալի համար։ Չալյանը հունահռոմեական ոճի ըմբշամարտի մրցաշարի բրոնզե մեդալի համար կգոտեմարտի ադրբեջանցի Ռաֆիգ Հուսեյնովի հետ։ Վերջինս իր հերթին սփոփիչ գոտեմարտում 11։1 հաշվով պարտության էր մատնել թունիսցի մարզիկին։ Հիշեցնենք, որ այսօր 97 կգ քաշային կարգի եզրափակիչում Արթուր Ալեքսանյանը կգոտեմարտի աշխարհի ու Եվրոպայի կրկնակի չեմպիոն Մուսա Եվլոևի հետ: Ավելացնենք, որ կիսաեզրափակիչները և մեդալայաին գոտեմարտերը կմեկնարկեն Երևանի ժամանակով 13։15-ին, իսկ եզրափակիչները՝ 15։15-ին։</p>', '', '/upload/768847.jpg', 'https://blognews.am/arm/news/768847/tokio-2020%E2%80%A4-karapet-chalyany-bronze-medali-hamar-kgotemarti-adrbejanci-marziki-het.html', 'Երեկ հունահռոմեական ոճի ըմբիշ, 77 կգ քաշային կարգում Հայաստանի ներկայացուցիչ Կարապետ Չալյանը Տոկիոյի Օլիմպիական ...', 'Вчера борец греко-римского стиля Карапет Чалян (77 кг) представлял Армению на Олимпиаде в Токио...', 0, 0, 4, '2022-05-09 17:15:11', '2022-05-10 17:03:24'),
(36, 3, 'Մեր տղերքի հաջողության տարոսը իմ ողջ ժողովրդին թող լինի', '', '<p>Կեցցեն մեր մարզիկները, որոնք մեզ հաղթանակներ են պարգեւում։ Երիցս կեցցեն ադրբեջանցիներին տարբեր մարզաձեւերում հաղթած ու հաղթողները, զորօրինակ վերջերս Հայկը՝ շախմատում եւ Հովհաննեսը՝ բոքսում։ Մենք հիմա անգամ այս իրադարձային հաջողությունների ծարավը շատ ունենք։ Սակայն ազգիս ախտահարված արժանապատվությունը լիարժեք ապաքինվելու համար ավելի մեծ ու կայուն նվաճումների կարիքն ունի։ Ուստի մեր տղերքի հաջողության տարոսը իմ ողջ ժողովրդին թող լինի։</p>', '', '/upload/768533.jpg', 'https://blognews.am/arm/news/768533/mer-txerqi-hajoxutyan-tarosy-im-oxj-zhoxovrdin-tox-lini.html', 'Կեցցեն մեր մարզիկները, որոնք մեզ հաղթանակներ են պարգեւում։', 'Да здравствуют наши спортсмены, которые дарят нам победы.', 0, 0, 3, '2022-05-09 17:15:40', '2022-05-10 23:00:50'),
(37, 2, 'Նիկոլ Փաշինյանի հավատարիմ երկրպագու Կոլոտ Հովոն ադրբեջանցիների հետ միասին ծաղրում է Դադիվանքի նախկին վանահայր Տեր Հովհաննեսին (Photo)', '', '<div>Կոլոտ Հովոն ֆեյսբուքյան իր էջում գրառում էր արել, որտեղ Դադիվանքի նախկին վանահայր Տեր Հովհաննեսին խուլիգան է անվանում, ապա մեկնաբանություններում հայտնվում է ադրբեջանցի մի օգտատեր, ու ծաղրալից մեկնաբանություն թողնում. &laquo;լօլ, սա էն մարդը չի, որ պատերազմի սկզբում մի ձեռքով ավտոմատն էր բռնել, մյուսով փետուրը&raquo;։ Ինչին Կոլոտ Հովոն պատասխանում է. &laquo;Ցավոք, այո&raquo;։ Հիշեցնենք, որ Հովհաննես Ազոյանը թե՛ 2018-ի հեղափոխության օրերին, թե՛ դրանից հետո, և թե առավել ևս պատերազմից հետո մնացել է այն փոքրաթիվ անհայրենիքներից, ովքեր դեռ սատարում են Փաշինյանին, թուրքերի հետ խաղաղություն քարոզում, ու ծաղրում հայրենասեր մարդկանց։ Նույն Հովհաննեսն էր, ով պատերազմից հետո, հազարավոր զոհերի ֆոնին, Մեհրիբան Ալիևայի գրառման տակ մեկնաբանություն էր թողել ու գրել, որ &laquo;Վերջապես խաղաղություն տեսա պարոն Ալիեւի դեմքին&raquo;:</div>', '', '/upload/794446.jpg', 'https://blognews.am/arm/news/794446/nikol-pashinyani-havatarim-erkrpagu-kolot-hovon-adrbejancineri-het-miasin-tsaxrum-e-dadivanqi-nakhkin-vanahayr-ter-hovhannesin-photo.html', 'Կոլոտ Հովոն ֆեյսբուքյան իր էջում գրառում էր արել, որտեղ Դադիվանքի նախկին վանահայր Տեր Հովհաննեսին խուլիգան ...', 'Колот Хово сделал пост на своей странице в Facebook, где бывший настоятель Дадиванка отец Ованес хулиганил...', 0, 0, 5, '2022-05-09 17:18:09', '2022-05-10 17:02:38'),
(38, 2, 'Կան իրենց 2 կոպեկի համար թուրքի դիմաց կզող «դերասանուհիներ» ու կա արժանապատիվ Լուիզա Ղամբարյան և Ագնեսա Շահնազարյան', '', '<div><img src=\"https://scontent.fevn12-1.fna.fbcdn.net/v/t39.30808-6/280194562_1032163484334036_1790267991193197063_n.jpg?_nc_cat=110&amp;ccb=1-6&amp;_nc_sid=8bfeb9&amp;_nc_ohc=WMCHvJYszAAAX9xV6iq&amp;_nc_ht=scontent.fevn12-1.fna&amp;oh=00_AT8eBkKaRPXVUpatM0skKGnYwBhsHR349qc_leUZvYUIcQ&amp;oe=627A8C34\" /></div>\n<p>Կան իրենց 2 կոպեկի համար թուրքի դիմաց կզող &laquo;դերասանուհիներ&raquo; ու կա արժանապատիվ Լուիզա Ղամբարյան և Ագնեսա Շահնազարյան</p>', '', '/upload/794310.jpg', 'https://blognews.am/arm/news/794310/kan-irenc-2-kopeki-hamar-turqi-dimac-kzox-derasanuhiner-u-ka-arzhanapativ-luiza-xambaryan-ev-agnesa-shahnazaryan.html', 'Կան իրենց 2 կոպեկի համար թուրքի դիմաց կզող «դերասանուհիներ» ու կա արժանապատիվ Լուիզա Ղամբարյան և Ագնեսա ...', 'Есть \"актрисы\" которые дерутся в обмен на турка за 2 копейки и есть достойная Луиза Гамбарян և Агнесса...', 0, 0, 4, '2022-05-09 17:18:41', '2022-05-11 05:45:31'),
(39, 2, 'Սոս, ազնիվ եղիր և բացատրի, որ դու ընդամենը էդ հաղորդման հաղորդավարն ես և դրա համար ստանում ես աշխատավարձ, թե չե նիկոլոկան պրոպագանդան քեզնից արդեն Լևոն Հայրապետյանա սարքել', '', '<p>Sos Janibekyan, ես դատապարտում եմ քո դեմ երեկ հնչեցրած հայհոյանքները, բայց ախպոր նման, ազնիվ եղիր և քո հետևորդնդերին բացատրի, որ դու ընդհամենը էդ հաղորդման հաղորդավարն ես և դրա համար ստանում ես աշխատավարձ, ինչը էլի գովելի է: Թե չե նիկոլոկան պրոպագանդան քեզնից արդեն Լևոն Հայրապետյանա սարքել, ինչը տենց չի:</p>', '', '/upload/794068.jpg', 'https://blognews.am/arm/news/794068/sos-azniv-exir-ev-bacatri-or-du-yndameny-ed-haxordman-haxordavarn-es-ev-dra-hamar-stanum-es-ashkhatavardz-te-che-nikolokan-propagandan-qeznic-arden-levon-hayrapetyana-sarqel.html', 'Sos Janibekyan, ես դատապարտում եմ քո դեմ երեկ հնչեցրած հայհոյանքները, բայց ախպոր նման, ազնիվ եղիր և քո հետևորդնդերին ...', 'Сос Джанибекян, я осуждаю оскорбления, сказанные в твой адрес вчера, но по-братски будь честен со своими потомками...', 0, 0, 4, '2022-05-09 17:19:19', '2022-05-10 17:01:48'),
(40, 2, 'Ինչո՞ւ է Գայա Արզումանյանն բուլղարացի պոռնոդերասանուհու հանդերձանքով Արցախի մասին երգ երգում (Video)', '', '<div><img src=\"https://blognews.am/static/content/A2/270963020_889837858391730_4188611179742729316_n.jpg\" /></div>\n<div><img src=\"https://blognews.am/static/content/A2/de-vrouw-die-latexkleren-met-dragen-ranselt-624067280000.jpg\" /></div>\n<p>Ինստագրամում աչքովս մի տեսանյութ ընկավ, որի շարունակությունն էլ տիկտոկում էր։ Մի պահ ձայնն անջատված էր, ինձ թվաց թե ինչ որ պոռնոդերասանուհու ելույթա բեմի վրա, էն էլ պարզվեց չէ, Գայա Արզումանյանն էր երգում, էն էլ Արցախի մասին։ Լատեքս շորերով, 30 սանտիմետրանոց անհասկանալի կոշիկներով մարդի աղջիկը Այայի երկիր Արցախ ու Նախշուն բաջի էր երգում։ Լավ դուք ընդհանրապես հասկանո՞ւմ եք որտեղ, ինչ հագուստով ինչ եք երգում։ Գայա ջան, աղջիկ ջան, լավ ես անում, որ դեռ Արցախի մասին երգեր ես երգում, բայց այ ցավդ տանեմ, էդ բուլղարացի պոռնոդերասանուհու շորերն ի՞նչ կապ ունեն քո երգածի հետ։ &nbsp;</p>\n<p><iframe src=\"https://www.youtube.com/embed/2-0jzMQC0Uw\" width=\"840\" height=\"480\"></iframe></p>', '', '/upload/789891.jpg', 'https://blognews.am/arm/news/789891/inchu-e-gaya-arzumanyann-bulxaraci-pornoderasanuhu-handerdzanqov-arcakhi-masin-erg-ergum-video.html', 'Ինստագրամում աչքովս մի տեսանյութ ընկավ, որի շարունակությունն էլ տիկտոկում էր։', 'Увидел в инстаграме видео, продолжение которого тикало.', 0, 0, 2, '2022-05-09 17:19:52', '2022-05-10 17:01:23'),
(41, 2, 'Արդեն 29 տարեկան այս երգի իմ տարբերակը թող նվերս լինի բոլոր սիրահար զույգերին. Սոֆի Մխեյանի տեսահոլովակի պրեմիերան- «Ջիգյար» (Video)', '', '<p>Լյուբով Ուսպենսկայայի` արդեն 29 տարեկան այս երգի իմ տարբերակը թող նվերս լինի բոլոր սիրահար զույգերին&nbsp;երիտասարդների բարեխոս, արագահաս Ս. Սարգսի տոնի առթիվ։ Սիրո տոնին ու այսուհետ տարածենք միայն Սեր ու ժպիտներ։ Ու թող ողջ տիեզերքն օգնի բոլոր միայնակներին գտնել իրենց երկրորդ կեսին</p>\n<p><iframe src=\"https://www.youtube.com/embed/3UHHjkhByns\" width=\"840\" height=\"480\"></iframe></p>', '', '/upload/784658.jpg', 'https://blognews.am/arm/news/784658/arden-29-tarekan-ays-ergi-im-tarberaky-tox-nvers-lini-bolor-sirahar-zuygerin-sofi-mkheyani-tesaholovaki-premieran--jigyar-video.html', 'Ու թող ողջ տիեզերքն օգնի բոլոր միայնակներին գտնել իրենց երկրորդ կեսին', 'И пусть вся вселенная поможет всем одиночкам найти свою вторую половинку', 0, 0, 2, '2022-05-09 17:20:49', '2022-05-10 17:01:02'),
(42, 2, 'Միայն տեսեք, թե ամուսնության քանի ամյակն է նշում Անահիտ Կիրակոսյանը (Photo)', '', '<div>Անահիտ Կիրակոսյանն ու նրա ամուսինը նշում են ամուսնության 10-ամյակը։ Այս առիթով դերասանուհին ինստագրամյան էջում ջերմ ֆոտոշարք է հրապարակել սիրելիի հետ։ Հրապարակմանը կից Անահիտը գրել է.&nbsp; &laquo;Սեր ...Շնորհավոր մեր օրը... Տասը տարի միասին... Վարդե հարսանիք...&raquo;: Դերասանուհին նաեւ լուսանկարներ է հրապարակել դուստրերի հետ։ &laquo;06.02.2012 _ 06.02.2022։ Տասը տարի անց.... Ինչ արագ է սլանում ժամանակը... Կարծես երեկ լիներ, երբ երկու ձեռքս պարզած գրկեցի երկու փոքրիկ թաթիկներ... Այսօր արդեն մեծ աղջիկներ.... +երկու որդի =ՍԵՐ: Մեր ընտանիքը 10 տարեկան է&raquo;,- գրել է Անահիտը։ &nbsp;&nbsp;&nbsp;&nbsp;</div>', '', '/upload/783974.jpg', 'https://blognews.am/arm/news/783974/miayn-teseq-te-amusnutyan-qani-amyakn-e-nshum-anahit-kirakosyany-photo.html', 'Անահիտ Կիրակոսյանն ու նրա ամուսինը նշում են ամուսնության 10-ամյակը։', 'Анаит Киракосян и ее муж отмечают 10-летие свадьбы.', 0, 0, 3, '2022-05-09 17:21:16', '2022-05-10 17:00:40'),
(43, 2, '«Թռիչք վերջին անգամ». Հայկոյի վերջին երգը՝ նվիրված օդաչու Արմեն Ներսեսյանի հիշատակին (Video)', '', '<p>Կոմպոզիտոր Րաֆֆի Ալթունյանը ներկայացրել է իր և ՀՀ վաստակավոր արտիստ Հայկոյի վերջին համատեղ ստեղծագործությունը։ Երգը կոչվում է &laquo;Թռիչք վերջին անգամ&raquo;։ Խոսքերի հեղինակը Րաֆֆին է, երաժշտությունը գրել է վաղամեռիկ եգիչ, երգահան Հայկոն։ &laquo;Իմ և Ընկերոջս վերջին համատեղ աշխատանքը&raquo;,- իր ֆեյսբուքյան էջում գրել է Րաֆֆին։ Երգը նվիրվում է 44 օրյա պատերազմում զոհված ռազմական օդաչու, գնդապետ Արմեն Ներսեսյանի հիշատակին:</p>\n<p><iframe src=\"https://www.youtube.com/embed/3fehJbu_9k4\" width=\"840\" height=\"480\"></iframe></p>', '', '/upload/783161.jpg', 'https://blognews.am/arm/news/783161/trichq-verjin-angam-haykoyi-verjin-ergy-nvirvats-odachu-armen-nersesyani-hishatakin-video.html', 'Կոմպոզիտոր Րաֆֆի Ալթունյանը ներկայացրել է իր և ՀՀ վաստակավոր արտիստ Հայկոյի վերջին համատեղ ստեղծագործությունը։ ...', 'Композитор Раффи Алтунян представил свою последнюю совместную работу с заслуженным артистом РА Айко. ...', 0, 0, 3, '2022-05-09 17:21:57', '2022-05-10 17:00:09'),
(44, 7, 'Վարչապետը տիկնոջ հետ այցելել է «Հողի գաղտնիքը. Արտաշատը մշակույթների խաչմերուկում» խորագրով ցուցահանդես', '', '<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28888_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28889_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28887_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28886_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28885_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28884_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28883_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28882_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28881_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28880_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28879_b.jpg\" /></div>\n<div><img src=\"https://www.primeminister.am/files/pics/2022/02/18/28878_b.jpg\" /></div>\n<p>Վարչապետ Նիկոլ Փաշինյանը և տիկին Աննա Հակոբյանը այցելել են Հայաստանի պատմության թանգարան, որտեղ ծանոթացել են &laquo;Հողի գաղտնիքը. Արտաշատը մշակույթների խաչմերուկում&raquo; խորագրով ցուցահանդեսին ներկայացված նմուշներին: Ցուցահանդեսը կազմակերպվել է Կրթության, գիտության, մշակույթի և սպորտի նախարարության, &laquo;Հայաստանի պատմության թանգարան&raquo; ՊՈԱԿ-ի և ՀՀ Գիտությունների ազգային ակադեմիայի &laquo;Հնագիտության և ազգագրության ինստիտուտ&raquo; ՊՈԱԿ-ի համագործակցությամբ: Մեկտեղվել են Արտաշատ հնավայրի տարբեր տարիների պեղումներից հայտնաբերված այն գտածոները, որոնք լավագույնս են ներկայացնում հուշարձանի մի քանի հազարամյակ ընդգրկող պատմությունը։ Ցուցադրվում են կնքադրոշմներ, արձանիկներ, գունազարդ խեցեղեն, դրամներ, սափորներ և կարասներ, ռիտոններ, զարդեր և այլ ուշագրավ ցուցանմուշներ, որոնց զգալի մասն առաջին անգամ դերը միջազգային, քաղաքական, տնտեսական և մշակութային հարաբերություններում: 2021 թվականի նոյեմբերից &laquo;Հողի գաղտնիքը&raquo; ծրագրի շրջանակում ԿԳՄՍ նախարարության համակարգմամբ իրականացվում են հնագիտական ուսումնասիրություններից հայտնաբերված գտածոների ցուցադրություններ՝ կարևորելով պատմության և մշակույթի հուշարձանների հանրահռչակումը:</p>', '', '/upload/785418.jpg', 'https://blognews.am/arm/news/785418/varchapety-tiknoj-het-aycelel-e-hoxi-gaxtniqy-artashaty-mshakuytneri-khachmerukum-khoragrov-cucahandes.html', '2021 թվականի նոյեմբերից «Հողի գաղտնիքը» ծրագրի շրջանակում ԿԳՄՍ նախարարության համակարգմամբ իրականացվում ...', 'С ноября 2021 года в рамках программы «Тайна земли», координируемой Министерством образования и науки...', 0, 0, 2, '2022-05-09 17:23:27', '2022-05-10 16:59:49'),
(45, 7, 'Արայիկ Հարությունյանը նշանակել է Արցախի Հանրապետության կրթության, գիտության, մշակույթի և սպորտի նոր նախարար', '', '<p>Արցախի Հանրապետության նախագահ Արայիկ Հարությունյանը փետրվարի 7-ին ստորագրել է հրամանագիր Լուսինե Ղարախանյանին Արցախի Հանրապետության կրթության, գիտության, մշակույթի և սպորտի նախարարի պաշտոնից ազատելու մասին ՝ իր դիմումի համաձայն: Այս մասին հաղորդում է Արցախի նախագահի աշխատակազմը: Արցախի Հանրապետության կրթության, գիտության, մշակույթի և սպորտի նախարար է նշանակվել Անահիտ Հակոբյանը: Երկրի ղեկավարի մեկ այլ հրամանագրով՝ Լուսինե Ղարախանյանը նշանակվել է Արցախի Հանրապետության նախագահի խորհրդական:</p>', '', '/upload/784009.jpg', 'https://blognews.am/arm/news/784009/arayik-harutyunyany-nshanakel-e-arcakhi-hanrapetutyan-krtutyan-gitutyan-mshakuyti-ev-sporti-nor-nakharar.html', 'Արցախի Հանրապետության նախագահ Արայիկ Հարությունյանը փետրվարի 7-ին ստորագրել է հրամանագիր Լուսինե Ղարախանյանին ...', '7 февраля Президент Республики Арцах Араик Арутюнян подписал указ Лусине Караханян...', 0, 0, 2, '2022-05-09 17:24:19', '2022-05-10 16:59:29'),
(46, 7, 'Թշնամին ավերել է Մադաթաշենի դպրոցը, մշակույթի տունը, հուշարձանը, վնասել Սբ․ Աստվածածին եկեղեցին (Photo)', '', '<div><img src=\"https://lurer.com/disc/06-11-21/035895fcd2e0f3b08f21de25d3d1ce13.jpg\" /></div>\n<p>Hraparak.am-ը գրում է. Թշնամին ավերել է Մադաթաշեն գյուղի դպրոցը, մշակույթի տունը, հուշարձանն ու վնասել Սբ․ Աստվածածին եկեղեցին: Այս մասին գրում է Արցախի մշակութային ժառանգության մշտադիտարկում իրականացնող monumentwatch.org կայքը։&laquo;Ըստ &nbsp;2021 թվականի հոկտեմբերի 10-ի &nbsp;արբանյակային լուսանկարների՝ թշնամին քանդել է Ասկերանի շրջանի Մադաթաշեն գյուղի դպրոցը &nbsp;և հասել դպրոցին կից եկեղեցու պատին, որը, փաստորեն վտանգի տակ է։ &nbsp;Դպրոցը 2015 թվականից վերանվանվել էր &laquo;Մադաթաշեն&raquo; դպրոց ՊՈԱԿ, որի շենքում &nbsp;էին &nbsp;տեղակայված նաև &nbsp;գյուղի գյուղապետարանն ու մշակույթի տունը։Եկեղեցու կողքին կար Արցախյան ազատամարտի զոհերին նվիրված հուշարձան, որը թշնամին նույնպես ոչնչացրել է գյուղի օկուպացիայից անմիջապես հետո։ Ադրբեջանական կողմի տարածած տեսանյութում ակնհայտ երևում է, թե ինչպես է զինված ադրբեջանցի զինվորականը ջարդում, քանդում և ավերում արցախյան ազատամարտի հերոսների հուշարձանը, կոտրում նրանց նկարներն ու հրճվանքով լուսանկարվում ավերված մշակութային ժառանգության ֆոնին։ &nbsp;Հավելենք, որ մինչ ադրբեջանական օկուպացիան արված տեսանյութը փաստում է, որ կանգուն &nbsp;էին դպրոցն ու նրան կից հուշարձանը։Մադաթաշեն գյուղի պատմությունը սկսվում &nbsp;է 17-րդ դարի կեսերից, իսկ անունը ստացել է ռուսական բանակի գեներալ Վալերիան Մադաթովի անունից, ում անասնագոմերը հենց այս տարածքում էին գտնվում։ Գյուղական համայնքը տեղաբաշխված է Արցախի Հանրապետության հարավային հատվածում։ Ասկերան շրջկենտրոնից գտնվում է 50 կմ հեռավորության վրա, իսկ մայրաքաղաք Ստեփանակերտից՝ 24 կմ հեռավորութան վրա։ Գյուղի &nbsp;Սբ. Աստվածածին եկեղեցի &nbsp;կառուցվել է 1904 թվականին։ Եկեղեցու պատը վնասված էր, տանիքը՝ նույնպես։Մադաթաշենի դպրոցը, մշակույթի տունն ու եկեղեցին ադրբեջանական նոր ճանապարհաշինության ուղղության մեջ չեն մտնում և չի բացառվում, որ մշակութային արժեքների ավերման հետևանքով ստացված շինաքարերը ճանապարհների հենապատերի համար են օգտագործվում&raquo;։</p>', '', '/upload/778834.jpg', 'https://blognews.am/arm/news/778834/tshnamin-averel-e-madatasheni-dprocy-mshakuyti-tuny-hushardzany-vnasel-sb%E2%80%A4-astvatsatsin-ekexecin-photo.html', 'Թշնամին ավերել է Մադաթաշեն գյուղի դպրոցը, մշակույթի տունը, հուշարձանն ու վնասել Սբ․ Աստվածածին եկեղեցին: ...', 'Противник разрушил школу, дом культуры, памятник села Мадаташен и повредил церковь Св. Аствацацин церкви. ...', 0, 0, 4, '2022-05-09 17:24:55', '2022-05-10 16:59:08'),
(47, 7, 'Կադիրովի ավագ դուստրը Չեչնիայի մշակույթի նախարար է նշանակվել', '', '<p>Armeniasputnik.am-ը գրում է․ Չեչնիայի նախագահ Ռամզան Կադիրովի ավագ դուստրը՝ Այշատը, որը զբաղեցնում էր Չեչնիայի մշակույթի առաջին փոխնախարարի պաշտոնը, գլխավորել է այդ գերատեսչությունը։ Տեղեկությունը հայտնում է&nbsp;ՌԻԱ Նովոստին՝ հղում անելով Չեչնիայի խորհրդարանի խոսնակ Մագոմեդ Դաուդովին &laquo;Ես վստահ եմ, որ Չեչնիայի Հանրապետության մշակույթի նախարարի պատասխանատու պաշտոնում Այշատը կհասնի ավելի նշանակալի արդյունքների և հաջողությամբ կկատարի իր առջև դրված խնդիրները&raquo;,- գրել է Դաուդովը Instagram-ի իր պաշտոնական էջում: Նրա խոսքով` Այշատ Կադիրովան իր աշխատանքի նկատմամբ պատասխանատու վերաբերմունքի շնորհիվ կարողացել է հսկայական ներդրում ունենալ հանրապետության գրադարանների, մշակույթի տների և թատրոնների զարգացման գործում: Ամբողջական հոդվածը կարող եք կարդալ այստեղ</p>', '', '/upload/775450.jpg', 'https://blognews.am/arm/news/775450/kadirovi-avag-dustry-chechniayi-mshakuyti-nakharar-e-nshanakvel.html', 'Չեչնիայի նախագահ Ռամզան Կադիրովի ավագ դուստրը՝ Այշատը, որը զբաղեցնում էր Չեչնիայի մշակույթի առաջին փոխնախարարի ...', 'Старшая дочь президента Чечни Рамзана Кадырова Айшат, занимавшая пост первого заместителя министра культуры Чечни...', 0, 0, 3, '2022-05-09 17:26:00', '2022-05-10 16:58:44'),
(48, 10, 'Խոշոր ավտովթար՝ Կոտայքի մարզում. բախվել են Toyota Ipsum-ն ու ոստիկանության Չարենցավանի բաժնի նախկին պետի Lexus-ը, վերջինս գլխիվայր հայտնվել է դաշտում, կա վիրավոր. ՖՈՏՈՌԵՊՈՐՏԱԺ', '', '<p>Այսօր՝ մայիսի 9-ին, խոշոր ավտովթար է տեղի ունեցել Կոտայքի մարզում։ Ժամը 14։00-ի սահմաններում Երևան-Սևան ավտոճանապարհից դեպի Չարենցավան քաղաք տանող հատվածի 1-ին կմ-ում բախվել են&nbsp;Lexus և&nbsp;&nbsp;Toyota Ipsum մակնիշի ավտոմեքենաները։ Ինչպես դեպքի վայրից հայտնում է ֆոտոլրագրող&nbsp;Գագիկ Շամշյանը,&nbsp; վթարի հետևանքով Lexus-ը դուրս է եկել ճանապարհի երթևեկելի գոտուց և գլխվայր շրջվելով հայտնվել դաշտում։Ըստ նախնական տեղեկությունների՝ վթարի հետևանքով 1 հոգի բժշկական օգնություն ստանալու նպատակով տեղափոխվել է &laquo;Չարենցավան&raquo; բժշկական կենտրոն։Օպերատիվ են գործել արտակարգ իրավիճակների նախարարության փրկարար ծառայության Չարենցավանի թիվ 47 հրշեջ փրկարարական ջոկատից 1 մարտական հաշվարկ, ինչպես նաև Չարենցավանի հիվանդանոցի բժիշկների հերթապահ խումբը։&nbsp;Օպերատիվորեն ժամանել են ճանապարհային ոստիկանության 2-րդ սպայական գումարտակի 3-րդ վաշտի 1-ին դասակի ծառայողները՝ վաշտի հրամանատար Արթուր Պետրոսյանի, դասակի հրամանատար Սարգիս Բագրատյանի գլխավորությամբ։&nbsp;Տեղում հետաքննական աշխատանքները ղեկավարում է նույն վաշտի ավագ հետաքննիչ Մայիս Բադոյանը, ում կարգադրությամբ նույն վաշտի ավագ հետաքննիչ Կառլեն Բաղյանը վարորդներից սթափության փորձաքննություն է վերցնում։&nbsp;Վթարի մասին ՃՈ 1 77 օպերատիվ կառավարման կենտրոնի պատասխանատու հերթապահ Երվանդ Ալեքսանյանը տեղեկությունը փոխանցել է ոստիկանության Չարենցավանի բաժին, որտեղից ժամանել է օպերատիվ խումբը՝ բաժնի պետ Արտակ Ասատրյանի գլխավորությամբ։Ճանապարհային ոստիկանությունը&nbsp;պարզում է վարորդների և վիրավորի ինքնությունը։Lexus-ը վարել է ոստիկանության Չարենցավանի բաժնի նախկին պետ Հայկ Մանուկյանը։&nbsp;Ծանուցում. Ենթադրյալ հանցանքի մեջ կասկածվողը կամ մեղադրվողը համարվում է անմեղ, քանի դեռ նրա մեղավորությունն ապացուցված չէ ՀՀ քրեական դատավարության օրենսգրքով սահմանված կարգով` դատարանի` օրինական ուժի մեջ մտած դատավճռով:Լուսանկարները՝ Գագիկ Շամշյանի</p>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/7c328ffb03135bf28e8340189a949580.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/a7c83483286840cb148382a5a604dd1f.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/c47556f7389283e08aced5714d3c8626.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/8be8404cbe250a352eff7c0d0775bece.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/567179b0b5ab5230d6c37703949e48c5.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/6009e1d92ee35be84b6c9e992256fe10.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/1446785980919993d1eb4550a5596353.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/1f61ec047c8bef74f67206d5e02b2975.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/a009c684eaee0741e754498ebe55848a.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/94b1add854239190c445672ab8835132.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/9316d84ba0f3795941390442567765c9.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/e9aecfc20daa0ef216b265e30cdba97d.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/f688c8f523cd19c179238c702dea0885.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/4fb541871e75ad2563e92b2b738d42ba.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/224af84f7cf47bcf12d75d000435c324.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/47f35c1a2cb7400b107012f4ec9ef7ca.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/1727e05e3b65a141c7a6b0e1c0e62cf1.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/e38b332703869a2bbfd6f4a69e47012e.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/9f663a94b2e6e67f0b896c7dec950684.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/5f4228ffbcf24f489a9b84a4c08340c8.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/ade62b95fa103755b0d57bc2192d8b5f.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/fc7ba4c39677fe7bb5a76ce0110ecef0.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/2ec5179b33e312013d7a585b75c90da7.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/2d4a2ba2c2eed11c93313ed155de1401.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/df2a851d743a4b846a2710c4bce2c97c.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/09/f5bc04723fe1e90bae6457387b62a402.jpg\" /></div>', '', '/upload/09d9e5e410767b6aa55bed9718a75712.jpg', 'https://shamshyan.com/hy/article/2022/05/09/1213081/', 'Խոշոր ավտովթար՝ Կոտայքի մարզում. բախվել են Toyota Ipsum-ն ու ոստիկանության Չարենցավանի բաժնի նախկին պետի Lexus-ը, վերջինս գլխիվայր հայտնվել է դաշտում, կա վիրավոր. ՖՈՏՈՌԵՊՈՐՏԱԺ', 'Крупное ДТП в Котайкской области. Столкнулись Toyota Ipsum и Lexus бывшего начальника Чаренцаванского РОВД, последний оказался в поле вверх ногами, есть раненый. ФОТООТЧЕТ:', 0, 0, 9, '2022-05-09 17:28:07', '2022-05-12 09:48:54'),
(49, 10, 'Խոշոր ավտովթար Արարատի մարզում. УАЗ-ը մոտ 600 մետր գլորվելով, հայտնվել է ձորում. 4 վիրավորներին, որոնցից մեկը էլցանցի մասնաճյուղի տնօրեն է, ձորից մեծ դժվարությամբ դուրս են բերել փրկարարները. ՖՈՏՈՌԵՊՈՐՏԱԺ', '', '<p>Այսօր՝ մայիսի 8-ին, խոշոր ավտովթար է տեղի ունեցել Արարատի մարզում:&nbsp;Ժամը 15:30-ի սահմաններում, Արարատի մարզի բնակիչ, 46-ամյա Եղիշե Կարապետյանը, իր վարած УАЗ մակնիշի ավտոմեքենայով, Լանջանիստ գյուղի վարչական տարածքում գտնվող &laquo;Աղքիլիսա&raquo; կոչվող բարձունքի հատվածում, դեռևս չպարզված հանգամանքներում, կորցրել է ավտոմեքենայի կառավարումը, դուրս եկել ճանապարհի երթևեկելի գոտուց և մոտ 600 մետր մի քանի պտույտ գլորվելով, հայտնվել է ձորում:&nbsp;Ինչպես հայտնում է ֆոտոլրագրող Գագիկ Շամշյանը, վթարի հետևանքով վարորդ Ե. Կարապետյանն ու ուղևորներ, Արարատի մարզի բնակիչներ, 35-ամյա Յուրա Սողոմոնյանը, 35-ամյա Գրիշա Մանուկյանը և 43-ամյա Գարեգին Եփրեմյանը մարմնական վնասվածքներով տեղափոխվել են Վեդու բժշկական կենտրոն, որտեղ անհրաժեշտ բուժօգնություն ստանալուց հետո տեղափոխվել են &laquo;Էրեբունի&raquo; բժշկական կենտրոն:&nbsp;Օպերատիվորեն վթարի վայր են ժամանել Արտակարգ իրավիճակների նախարարության Փրկարար ծառայության Արարատի մարզային փրկարարական վարչության թիվ 26 հրշեջ-փրկարարական ջոկատից 1 մարտական հաշվարկ, ովքեր վիրավորներին մեծ դժվարությամբ ճոպաններով և փրկարարական ամենագնացով դուրս են բերել վթարի վայրից և մոտեցրել օպերատիվորեն ժամանած Վեդու հիվանդանոցի բժիշկների հերթապահ խմբին, ովքեր մինչև հիվանդանոց տեղափոխելը տեղում առաջին բուօգնությունն են ցուցաբերել:&nbsp;Օպերատիվորեն ժամանել են Ճանապարհային ոստիկանության 2-րդ սպայական գումարտակի 1-ին վաշտի 2-րդ դասակի ծառայողները՝ վաշտի հրամանատար Գրիգոր Պողոսյանի, դասակի հրամանատար Կարապետ Հարությունյանի գլխավորությամբ:&nbsp;Տեղում հետաքննական աշխատանքները ղեկավարել է նույն վաշտի հետաքննիչ Թադևոսյանը, ում կողմից փաստի առթիվ նյութեր են նախապատրաստվում:&nbsp;Օպերատիվորեն ժամանել է նաև ոստիկանության Արարատի բաժնի հերթապահ խումբը՝ բաժնի պետի տեղակալ Անտոնյանի գլխավորությամբ:&nbsp;Գ. Շամշյանի տեղեկություններով, վիրավորներից Յու. Սողոմոնյանը աշխատում է &laquo;Հայաստանի էլեկտրական ցանցեր&raquo; ՓԲԸ-ի &laquo;Արաքս&raquo; մասնաճյուղի տնօրեն:&nbsp;Լուսանկարները՝ Մհեր Ղալեչյանի&nbsp;Ծանուցում. Ենթադրյալ հանցանքի մեջ կասկածվողը կամ մեղադրվողը համարվում է անմեղ, քանի դեռ նրա մեղավորությունն ապացուցված չէ ՀՀ քրեական դատավարության օրենսգրքով սահմանված կարգով` դատարանի` օրինական ուժի մեջ մտած դատավճռով:</p>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/672194b932330a55b5e79b20747fe9d0.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/acb4d9a447dbd18b32afb0e6437ed671.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/d7291c50e692d2086bc0ee4a6c118496.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/bee226291351f95ddf1e3f7bc393272a.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/1fa0af42a44a6eb7265fbbcfe1a7e96b.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/9e1d89fd9cfae56919ea2acba573ab30.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/8581de4350c7a683cb17ab610776570c.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/28fd237605b0b4280f6928a908b491ac.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/fd94728972bbc20849dfa33c5734a256.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/b23746b1e3b1f9ca539148bbe26de1dd.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/8541878df6afd40e029f1392c033b765.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/932c86985827668cf2042c5d7752a248.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/0bab6a23eddbd29c8096ce855934d7d9.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/586a47ae320471ca4b87ec9c291924cf.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/e9038ade9eaf79555ebf1207cb748498.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/1feabfd49cb2ba7e499b8926ee6ef3e5.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/ec963d10311293379d34ad2f2639e5ad.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/d523f8ccd21198e925960c1644f7a6b1.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/cd87fe8b656ca774e31ccdcf2bb9cf7f.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/8ec1c4410747f14d71671adc4ab27c4f.jpg\" /></div>\n<div><img src=\"https://img.shamshyan.com/bphotos/2022/05/08/47989908931a7d339b9e652f39d1322c.jpg\" /></div>', '', '/upload/735d353d888d05d41f4db75b475b2281.jpg', 'https://shamshyan.com/hy/article/2022/05/08/1213043/', 'Խոշոր ավտովթար Արարատի մարզում. УАЗ-ը մոտ 600 մետր գլորվելով, հայտնվել է ձորում. 4 վիրավորներին, որոնցից մեկը էլցանցի մասնաճյուղի տնօրեն է, ձորից մեծ դժվարությամբ դուրս են բերել փրկարարները. ՖՈՏՈՌԵՊՈՐՏԱԺ', 'Крупная автомобильная авария в Араратской области. УАЗик откатился метров на 600 и оказался в ущелье. Четверо раненых, один из которых – директор филиала электросетей, спасатели с большим трудом вытащили из ущелья. ФОТООТЧЕТ:', 0, 0, 166, '2022-05-09 17:28:57', '2022-05-12 06:49:12'),
(50, 10, 'Մեծամորում մահվան ելքով ճանապարհատրանսպորտային պատահարի առիթով հարուցվել է քրեական գործ. ՔԿ', '', '<p>ՀՀ քննչական կոմիտեի Արմավիրի մարզային քննչական վարչությունում ՀՀ Քրեական օրենսգրքի 242-րդ հոդվածի 2-րդ մասի հատկանիշներով հարուցվել է քրեական գործ, որի նախաքննության ընթացքում կատարվում են քննչական և դատավարական այլ գործողություններ` Մեծամոր քաղաքում տեղի ունեցած մահվան ելքով վրաերթի հանգամանքները պարզելու ուղղությամբ:</p>\n<p>Նախնական տվյալներով, 2022թ. մայիսի 2-ին՝ ժամը 17:00-ի սահմաններում, Մեծամոր քաղաքի բնակչուհին իր կողմից վարած &laquo;NISSAN BLUEBIRD SYLPHY 1.5&raquo; մակնիշի ավտոմեքենայով վրաերթի է ենթարկել 2008թ. ծնված տղայի, ով գիտակցության չգալով՝ մահացել է։</p>\n<p>Նշանակվել են մի շարք փորձաքննություններ։</p>\n<p>Կատարվում է քննություն: Ձեռնարկվում են համապատասխան միջոցառումներ՝ ճանապարհատրանսպորտային պատահարի հանգամանքները պարզելու ուղղությամբ:</p>', '', '/upload/16515801332674-1536x1152.jpg', 'https://armlur.am/1183877/', 'Մեծամորում մահվան ելքով ճանապարհատրանսպորտային պատահարի առիթով հարուցվել է քրեական գործ. ՔԿ', 'По факту ДТП со смертельным исходом в Мецаморе возбуждено уголовное дело. пенитенциарная', 0, 0, 3, '2022-05-09 17:31:37', '2022-05-10 16:57:24'),
(51, 10, 'Պատահարների հետևանքով զոհերի թիվն աճել է. մտահոգիչ թվեր', '', '<p>Այս տարվա փետրվարին պաշտոնական վիճակագրությունը ցույց է տալիս, որ հունվար ամսվա հետ համեմատած` պակասել է ճանապարհատրանսպորտային պատահարների ու վիրավորների թիվը: Բայց նույն ժամանակահատվածում աճել է զոհերի թիվը:</p>\n<p>ArmLur.am-ը տեղեկացավ, որ այս տարվա հունվար-փետրվար ամիսներին մեր հանրապետությունում արձանագրվել է 575 ճանապարհատրանսպորտային պատահար, որի հետեւանքով զոհվել է 31, վիրավորվել՝ 336 մարդ:</p>\n<p>Պաշտոնապես, այս տարվա երկու ամիսներին նախորդ տարվա նույն ժամանակահատվածի հետ համեմատ ճանապարհատրանսպորտային պատահարների թիվը պակասել է 11.7 տոկոսով:</p>\n<p>ArmLur.am-ը տեղեկացավ, որ 2022 թվականի փետրվարին մեր հանրապետությունում արձանագրվել է 252 ՃՏՊ, որի հետեւանքով զոհվել է 31, վիրավորվել՝ 336 մարդ: Իսկ ահա հունվար ամսին ՃՏՊ-ների քանակը կազմել է 322, որի հետեւաքնով զոհվել է 21, վիրավորվել 522 մարդ:</p>\n<p>Ս.Հ.</p>', '', '/upload/vtar-eghvardi-xtchughum-varord-n137597-1.png', 'https://armlur.am/1178831/', 'Իսկ ահա հունվար ամսին ՃՏՊ-ների քանակը կազմել է 322, որի հետեւաքնով զոհվել է 21, վիրավորվել 522 մարդ', 'В январе количество дорожно-транспортных происшествий составило 322, в результате которых погиб 21 человек и 522 получили ранения.', 0, 0, 6, '2022-05-09 17:32:18', '2022-05-10 16:56:36'),
(52, 10, 'Ճանապարհատրանսպորտային պատահարները Հայաստանում', '', '<p>Մարտի 18-ին, ժամը 06.00-ին, &laquo;ԲՄՎ&raquo; մակնիշի մեքենայի վարորդը Երևանի Ռոստովյան փողոցում վրաերթի է ենթարկել 72-ամյա տղամարդու, որը մարմնական վնասվածքներով բուժօգնության է տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 12.00-ին, &laquo;Մերսեդես&raquo; մակնիշի մեքենան Երևանի Էրեբունու փողոցում վրաերթի է ենթարկել 38-ամյա կնոջ, որը մարմնական վնասվածքներով բուժօգնության է տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 22.30-ին, &laquo;Շևրոլե&raquo; մակնիշի մեքենայի վարորդը Երևանի Նուբարաշենի փողոցում վրաերթի է ենթարկել 58-ամյա տղամարդու, որը մարմնական վնասվածքներով բուժօգնության է տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 18.50-ի սահմաններում, Մարգարա-Վանաձոր-Տաշիր ճանապարհի 43-րդ կմ-ին իրար են բախվել &laquo;ՎԱԶ-2106&raquo; և &laquo;Կիա&raquo; մակնիշի մեքենաներ. &laquo;ՎԱԶ-2106&raquo;-ի վարորդը մարմնական վնասվածքներով բուժօգնության է տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 22.15-ի սահմաններում, Մարգարա-Վանաձոր-Տաշիր ճանապարհի 63-րդ կմ-ին իրար են բախվել &laquo;ՎԱԶ-21099&raquo;, &laquo;Մերսեդես&raquo; և &laquo;Հունդաի&raquo; մակնիշի մեքենաներ. չորս ուղևոր և &laquo;Հունդաիի&raquo; վարորդը մարմնական վնասվածքներով բուժօգնության են տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 18.00-ի սահմաններում, Երևան-Մեղրի ճանապարհի 41-րդ կմ-ին՝ Ավշար գյուղի հատվածում, իրար են &laquo;ՎԱԶ-21014&raquo; և &laquo;ՎԱԶ-21011&raquo; մակնիշի մեքենաներ. &laquo;ՎԱԶ-21014&raquo;-ի վարորդը և &laquo;ՎԱԶ-21011&raquo;-ի երկու ուղևորը մարմնական վնասվածքներով բուժօգնության են տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 20.50-ի սահմաններում, &laquo;Շեվրոլե&raquo; մակնիշի մեքենայի վարորդը Արարատի մարզի Հովտաշատ գյուղում վրաերթի է ենթարկել 7-ամյա տղայի և իր մեքենայով տեղափոխել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 08.05-ի սահմաններում, &laquo;Մերսեդես&raquo; մակնիշի մեքենայի վարորդը Էջմիածին քաղաքի Մաշտոց-Սպանդարյան փողոցների խաչմերուկում վրաերթի է ենթարկել 66-ամյա տղամարդու, որը մարմնական վնասվածքներով բուժօգնության է տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 18.20-ի սահմաններում, &laquo;Օպել Վեկտրա&raquo; մակնիշի մեքենայի վարորդը Վանաձոր քաղաքի Նարեկացու փողոցում վրաերթի է ենթարկել 12-ամյա տղայի, որը մարմնական վնասվածքներով բուժօգնության է տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 10.50-ի սահմաններում, &laquo;Միցուբիշի Պաջերո&raquo; մակնիշի մեքենան Վանաձոր-Դիլիջան ճանապարհի 14-րդ կմ-ին՝ թունելի մեջ, կողաշրջվել է. երկու ուղևորները մարմնական վնասվածքներով բուժօգնության են տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 17.00-ի սահմաններում, Լոռու մարզի Սարչապետ գյուղում իրար են բախվել &laquo;Մերսեդես C&raquo; և &laquo;Ֆոլքսվագեն Պասատ&raquo; մակնիշի մեքենաներ, ինչի հետևանքով վերջինիս երկու ուղևորները մարմնական վնասվածքներով բուժօգնության են տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>\n<p>*** Մարտի 18-ին, ժամը 15.10-ի սահմաններում, &laquo;Տոյոտա Կորոլա&raquo; մակնիշի մեքենայի վարորդը Եղեգնաձոր քաղաքի Միկոյան փողոցում վրաերթի է ենթարկել 12-ամյա տղայի, որը մարմնական վնասվածքներով բուժօգնության է տեղափոխվել հիվանդանոց: Կատարվում է քննություն:</p>', '', '/upload/vtar%20(1).png', 'https://armlur.am/1173834/', 'Ճանապարհատրանսպորտային պատահարները Հայաստանում', 'ДТП в Армении', 0, 0, 9, '2022-05-09 17:33:04', '2022-05-11 14:42:38'),
(53, 6, '«5+6 կետերի» շուրջ բանակցություններ սկսելու հարցում ըմբռնում ունեն և´ Ադրբեջանը, և´ միջազգային գործընկերները. ԱԽ քարտուղար', '', '<p>Անվտանգության խորհրդի քարտուղար Արմեն Գրիգորյանը հայտարարեց, թե չի տեսել, որ Ադրբեջանը հրապարակավ մերժի Հայաստանի 6-կետանոց առաջարկը։</p>\n<p>Ինչպես տեղեկացնում է &laquo;Արմենպրես&raquo;-ը, Կառավարության նիստից հետո լրագրողների հետ զրույցում Գրիգորյանը հիշեցրեց, որ Ադրբեջանի 5-կետանոց առաջարկին հաջորդել էր Հայաստանի 6-կետանոց պատասխանը։</p>\n<p>&laquo;Հայաստանի համար Ադրբեջանի առաջարկած կետերը անընդունելի չեն եղել։ Հայաստանն իր 6 կետերում առաջարկել է, որ Լեռնային Ղարաբաղի հիմնախնդրին ևս լուծում պետք է տրվի, որպեսզի հնարավոր լինի համապարփակ խաղաղություն ունենալ։ Այս պահին մեր մոտեցումն այն է, որ այդ երկու փաթեթները՝ 5+6 կետերը պետք է միացնել իրար և սկսել խաղաղության պայմանագրի շուրջ բանակցությունները, որպեսզի գտնենք Լեռնային Ղարաբաղի հիմնախնդրի երկարաժամկետ լուծումը&raquo;,- ասաց Գրիգորյանը։</p>\n<p>ԱԽ քարտուղարի խոսքով՝ Հայաստանը հայտարարել է, որ առաջնային է Արցախում բնակվող հայրենակիցների անվտանգության, պաշտպանության և իրավունքների հարցերին լուծումը գտնելը և ըստ դրա պետք է ձևավորվի Լեռնային Ղարաբաղի կարգավիճակը։</p>\n<p>&laquo;Այդ փաթեթին հրապարակային մերժում չեմ տեսել։ Մեր քննարկումներում, որ այդ երկու փաթեթը պետք է միացնել և բանակցություններ սկսել, ըմբռնում կա։ Այդ ըմբռնումը կա և&acute; ադրբեջանական կողմում, և&acute; մնացած բոլոր միջազգային գործընկերների մոտ։ &laquo;5+6 կետերի&raquo; շուրջ բանակցություններ սկսելը լեգիտիմ մոտեցում է, և մենք տեսել ենք այդ ըմբռնումը&raquo;,- եզրափակեց Գրիգորյանը։</p>', '', '/upload/Screen%20Shot%202022-05-10%20at%2019-54-43.png', 'https://armlur.am/1184284/', 'ԱԽ քարտուղարի խոսքով՝ Հայաստանը հայտարարել է, որ առաջնային է Արցախում բնակվող հայրենակիցների անվտանգության, պաշտպանության և իրավունքների հարցերին լուծումը գտնելը և ըստ դրա պետք է ձևավորվի Լեռնային Ղարաբաղի կարգավիճակը։', 'По словам секретаря Совета безопасности, Армения заявила о приоритетности решения вопросов безопасности, обороны и прав проживающих в Арцахе соотечественников, в соответствии с которыми должен формироваться статус Нагорного Карабаха.', 0, 0, 64, '2022-05-09 17:34:31', '2022-05-12 06:14:26'),
(54, 6, 'Իտալիայի արտաքին գործերի և միջազգային համագործակցության նախարարը կայցելի Հայաստան', '', '<p>Ապրիլի 2-3-ը աշխատանքային այցով Հայաստան կժամանի Իտալիայի արտաքին գործերի և միջազգային համագործակցության նախարար Լուիջի դի Մայոն:&nbsp; &laquo;Արմենպրես&raquo;-ի փոխանցմամբ՝ այս մասին հայտնում է ՀՀ ԱԳՆ մամուլի խոսնակի&nbsp;&laquo;Ֆեյսբուք&raquo;-ի էջը։&nbsp; Ապրիլի 3-ին ՀՀ ԱԳՆ-ում կկայանա Հայաստանի և Իտալիայի ԱԳ նախարարների հանդիպումը, որին կհետևեն մամուլի համար հայտարարությունները: Այցի շրջանակներում նախատեսվում են նաև Լուիջի դի Մայոյի հանդիպումները ՀՀ նախագահի, ՀՀ վարչապետի և ՀՀ ԱԺ նախագահի հետ:</p>', '', '/upload/790006.jpg', 'https://blognews.am/arm/news/790006/italiayi-artaqin-gortseri-ev-mijazgayin-hamagortsakcutyan-nakharary-kayceli-hayastan.html', 'Ապրիլի 2-3-ը աշխատանքային այցով Հայաստան կժամանի Իտալիայի արտաքին գործերի և միջազգային համագործակցության ...', '2-3 апреля Италия посетит Армению с рабочим визитом...', 0, 0, 7, '2022-05-09 17:36:12', '2022-05-10 22:51:33'),
(55, 6, 'Վրաստանը հավանություն է տվել Ուկրաինային աջակցող բոլոր բանաձեւերին․ Վրաստանի նախագահ', '', '<p>Վրաստանը միացել է բոլոր միջազգային ֆինանսական սանկցիաներին, որոնք սահմանված են ընդդեմ Ռուսաստանի՝ Ուկրաինայի պատերազմի հետեւանքով։ Այս մասին հայտարարել է Վրաստանի նախագահ Սալոմե Զուրաբիշվիլին՝ CNN-ի հետ զրույցում։ Նա նաեւ ասել է, որ Վրաստանը հավանություն է տվել Ուկրաինային աջակցող բոլոր միջազգային բանաձեւերին։Նախագահ Զուրաբիշվիլին նկատել է, որ այն, որ Վրաստանը միացել է հակառուսական սանկցիաներին, որոնք սահմանվել են Ուկրաինայում ընթացող պատերազմի պատճառով, արդեն նկատելիորեն իրենց զգացնել են տալիս վրացական ֆինանսական համակարգում։</p>', '', '/upload/789920.jpg', 'https://blognews.am/arm/news/789920/vrastany-havanutyun-e-tvel-ukrainayin-ajakcox-bolor-banadzeverin%E2%80%A4-vrastani-nakhagah.html', 'Վրաստանը միացել է բոլոր միջազգային ֆինանսական սանկցիաներին, որոնք սահմանված են ընդդեմ Ռուսաստանի՝ Ուկրաինայի ...', 'Грузия присоединилась ко всем международным финансовым санкциям, наложенным на Россию, Украину...', 0, 0, 5, '2022-05-09 17:36:40', '2022-05-10 22:52:39'),
(56, 6, 'Մոսկվայի և Պեկինի հարաբերությունները նոր փորձությունների են ենթարկվել. Չինաստանի ԱԳՆ-ի ղեկավար', '', '<p>ՌԴ-ի եւ ՉԺՀ-ի հարաբերությունները տարեսկզբից ի վեր ենթարկվել են փոփոխվող միջազգային իրադրության նոր փորձությունների եւ պահպանել ճիշտ ուղղությունը: Այդ մասին, Չինաստանի արտաքին գործերի նախարար Վան Ին մարտի 30-ին հայտարարել է ՌԴ ԱԳՆ-ի ղեկավար Սերգեյ Լավրովի հետ կայացած հանդիպմանը: &laquo;Այս տարվա սկզբից չին-ռուսական հարաբերությունները ենթարկվել են միջազգային իրադրության լուրջ փոփոխությունների նոր փորձություններին, պահպանել ճիշտ ուղղությունը եւ զարգացման կայուն միտումը&raquo;,-հայտարարել է Վան Ին: Լավրովը երկօրյա այցով գտնվում է Չինաստանում, որի շրջանակներում նա մասնակցելու է Աֆղանստանի հարեւան երկրների եւ Աֆղանստանի գծով ընդլայնված եռյակի (Ռուսաստան, Չինաստան, ԱՄՆ եւ Պակիստան) երրորդ նախարարական խորհրդաժողովին, հաղորդել է ՌԻԱ Նովոստին:</p>', '', '/upload/789678.jpg', 'https://blognews.am/arm/news/789678/moskvayi-ev-pekini-haraberutyunnery-nor-pordzutyunneri-en-entarkvel-chinastani-agn-i-xekavar.html', 'ՌԴ-ի եւ ՉԺՀ-ի հարաբերությունները տարեսկզբից ի վեր ենթարկվել են փոփոխվող միջազգային իրադրության նոր փորձությունների ...', 'Отношения России и Китая с начала года подверглись новым испытаниям меняющейся международной обстановкой...', 0, 0, 7, '2022-05-09 17:37:41', '2022-05-11 09:02:49'),
(57, 6, 'ՄԱԿ-ում Թուրքիայի ներկայացուցիչը ՌԴ-ից պահանջել է դադարեցնել ռազմագործողություններն Ուկրաինայում', '', '<p>ՄԱԿ-ում Թուրքիայի մշտական ներկայացուցիչ Ֆերիդուն Սինիրլիօղլուն Ռուսաստանի կողմից Ուկրաինայում իրականացվող ռազմական գործողությունները որակել է &laquo;միջազգային իրավունքի կոպտագույն խախտում&raquo;։&nbsp;Այս մասին տեղեկացնում է ermenihaber.am-ը։ Նա նման հայտարարություն է արել ՄԱԿ-ի Անվտանգության խորհրդի նիստի ժամանակ՝ կապված &laquo;Ուկրաինայի հումանիտար աղետի հետ&raquo;։ Ըստ Սինիրլիօղլուի` Ուկրաինայի դեմ Ռուսաստանի Դաշնության լայնամասշտաբ պատերազմը պիտի դադարեցվի օր առաջ: Նա ընդգծել է, որ &laquo;միջազգային հանրությունը չի կարող համակերպվել Ռուսաստանի գործողությունների հետ և պետք է ամեն ինչ անի Ուկրաինայի ժողովրդին օգնելու համար&raquo;։ Թուրք պաշտոնյան նաև շեշտել է, որ հրադադարի հաստատման համար Թուրքիան գործի է դնելու իր դիվանագիտական բոլոր լծակները։</p>', '', '/upload/789044.jpg', 'https://blognews.am/arm/news/789044/mak-um-turqiayi-nerkayacucichy-rd-ic-pahanjel-e-dadarecnel-razmagortsoxutyunnern-ukrainayum.html', 'ՄԱԿ-ում Թուրքիայի մշտական ​​ներկայացուցիչ Ֆերիդուն Սինիրլիօղլուն Ռուսաստանի կողմից Ուկրաինայում իրականացվող ...', 'Постоянный представитель Турции при ООН Феридун Синирлиоглу ...', 0, 0, 6, '2022-05-09 17:38:16', '2022-05-11 16:52:34'),
(58, 8, 'Հյութեղ մսով կարկանդակ. արագ և հագեցնող անուշաբույր ուտեստ', '', '<div><img src=\"https://baxadratomser.am/wp-content/uploads/2022/02/%D4%B1%D5%B2%D5%A1%D6%81%D5%A1%D5%AE-%D5%B4%D5%BD%D5%B8%D5%BE-%D5%BB%D5%A5%D5%BC%D5%A5%D6%83%D5%B8%D6%82%D5%AF1.jpg\" /></div>\n<p>Հյութեղ մսով կարկանդակը հագեցնող և անչափ համեղ ուտեստ է: Այն Իսկական գտածո է&nbsp; ցանկացած տանտիրուհու համար:Ուտեստը բացարձակապես բոլորի ճաշակով կլինի, բոլոր հյուրերը հիացած կլինեն դրանով: Իսկ այն, որ ուտեստն արագ և հեշտ է պատրաստվում, կլինի դրա ընտրության մեծ հաղթաթուղթը:Հյութեղ մսով կարկանդակ. Անհրաժեշտ բաղադրիչներըԽմորի համար_Շաքարավազ 1 ճաշի գդալ_Չոր խմորիչ 10 գր_Աղ 0,5 թեյի գդալ_Ջուր 1,5 բաժակ_Ցորենի ալյուր 4 բաժակԼցոնի համար_Տավարի աղացած միս 800 գր_Սոխ 2 հատ_Սխտոր 2 պճեղ_Բուսական յուղ 0,5 բաժակ_Կանաչ պղպեղ 3 հատ_Լոլիկ 3 հատ_Տոմատի մածուկ 1 ճաշի գդալ_Աղ, պղպեղ՝ ըստ ճաշակի_Անանուխ՝ ըստ ցանկության_Սև պղպեղ՝ ըստ ճաշակի_Ուրց՝ ըստ ցանկության_Տաք ջուր 300 մլ Հյութեղ մսով կարկանդակ. Պատրաստման եղանակը_Խմորի համար խորը ամանի մեջ լցնել շաքարավազը, չոր խմորիչը, աղը, ջուրը, խառնել։_Այնուհետև ավելացնել ալյուրը և զանգվածը ձեռքերով հունցել։_Թողնել թաղանթով ու սրբիչով ծածկված մնա տաք տեղում, կես ժամ։_Թեթևակի յուղած թավայի մեջ լցնել աղացած միսը, տապակել 5 րոպե։_Ապա ավելացնել մանր կտրատած սոխը, մանր կտրատած սխտորը, լցնել մնացած բուսայուղը և խառնել։_Տապակել 5 րոպե, ավելացնել մանր կտրատած կանաչ պղպեղն ու լոլիկը, տոմատի մածուկը, ավելացնել համեմունքները, աղը, պղպեղը, անանուխը, սև պղպեղը և ուրցը։_Ամեն ինչ մանրակրկիտ խառնել և կափարիչով ծածկված եփել 10 րոպե:_Դրանից հետո ավելացնել տաք ջուր և եփել ևս 2 րոպե, դրանից հետո անջատել կրակը։Շարունակությունն՝ այստեղ</p>', '', '/upload/785238.jpg', 'https://blognews.am/arm/news/785238/hyutex-msov-karkandak-arag-ev-hagecnox-anushabuyr-utest.html', 'Հյութեղ մսով կարկանդակը հագեցնող և անչափ համեղ ուտեստ է', 'Это очень вкусное блюдо, которое наполняет пирог сочным мясом.', 0, 0, 1, '2022-05-09 17:39:01', '2022-05-10 16:44:41');
INSERT INTO `posts` (`id`, `category_id`, `hy_title`, `ru_title`, `hy_content`, `ru_content`, `image`, `source`, `hy_description`, `ru_description`, `is_general`, `has_video`, `viewed`, `created_at`, `updated_at`) VALUES
(59, 8, 'Կզարմանաք, թե ինչեր կարող է անել կիվին օրգանիզմի հետ․ ձեր իմունիտետը շնորհակալություն կհայտնի', '', '<div><img src=\"https://dashboard.iammedia.am/assets/uploads/posts/thumbnails/image-1592304569737.jpg\" /></div>\n<div><img src=\"https://sotskiy.am/static/articles/21/03/xl-xK0yovqpwe.jpg\" /></div>\n<div><img src=\"https://dashboard.iammedia.am/assets/uploads/posts/thumbnails/image-1593412717965.jpg\" /></div>\n<p>Երկու հոգու համար օգտակար և շատ համեղ ըմպելիք պատրաստելու համար վերցրեք մեկ հասած կիվի, մեկ բաժակ թարմ կեֆիր ձեր նախընտրած յուղայնությամբ, մի պտղունց աղացած դարչին։ Կիվին մաքրել և կտրատել, լցնել կեֆիրի մեջ և ավելացնել դարչինը, թույլ հարել։ Նման ըմպելիքի բուժիչ հատկությունները բացատրվում են նրա բաղադրությունը կազմող բոլոր բաղադրիչների օգտակարությամբ։ Նորվեգացի գիտնականները վերջերս պարզել են, որ կիվին արյան անոթները արյան մակարդուկներից մաքրելու հատկություն ունի։ Նորվեգացի գիտնականների կողմից անցկացված վերջին հետազոտությունները ցույց են տվել, որ այս միրգն ունակ է այրել ճարպերը, որոնք արգելափակում են զարկերակները, ինչը նվազեցնում է արյան մակարդման վտանգը։ Այս մրգի կանոնավոր օգտագործումն օգնում է կանխել աթերոսկլերոզի, Պարկինսոնի և Ալցհեյմերի հիվանդությունների, սրտի անբավարարության և նույնիսկ ուռուցքաբանության զարգացումը։ Կեֆիրն ապահովում է մարդու օրգանիզմը կալցիումով, սպիտակուցներով, արժեքավոր վիտամիններով։ Նրանց առավելագույն յուրացման համար ըմպելիքի իդեալական ջերմաստիճանը սենյակային ջերմաստիճանն է։ Դարչինը, ընդհակառակը, ունի ընդգծված հակասեպտիկ ազդեցություն, բացի այդ, այն ունակ է խթանել ինսուլինի արտադրությունը և նվազեցնել արյան շաքարը։ Կարևոր է, որ այս հնագույն համեմունքի կանոնավոր օգտագործումը բարելավում է ուղեղի աշխատանքը և լավացնում տեսողությունը։</p>', '', '/upload/783483.jpg', 'https://blognews.am/arm/news/783483/kzarmanaq-te-incher-karox-e-anel-kivin-organizmi-het%E2%80%A4-dzer-imunitety-shnorhakalutyun-khaytni.html', 'Երկու հոգու համար օգտակար և շատ համեղ ըմպելիք պատրաստելու համար վերցրեք մեկ հասած կիվի, մեկ բաժակ թարմ կեֆիր ...', 'Полезно для двоих համար Для приготовления очень вкусного напитка возьмите один спелый киви, стакан свежего кефира...', 0, 0, 4, '2022-05-09 17:39:35', '2022-05-11 01:21:28'),
(60, 8, 'Ձեզ ենք ներկայացնում ամենահամեղ էկլերների պատրաստման այս հրաշք եղանակը', '', '<p>Շատերին է թվում, թե այսպիսի ախորժելի և համեղ էկլերներ պատրաստելու համար մեծ ջանքեր են անհրաժեշտ: Սակայն մենք ձեզ կներկայացնենք շատ պարզ և մատչելի ձևով ու համոզված ենք, որ կկարողանանք այս հրաշալի քաղցրավենիքը պատրաստել ՝ շատ հեշտությամբ։ Դիտեք պատրաստման եղանակը՝&nbsp;</p>\n<p><iframe src=\"https://www.youtube.com/embed/AsGi-G8rsRw\" width=\"840\" height=\"480\"></iframe></p>', '', '/upload/782724.jpg', 'https://blognews.am/arm/news/782724/dzez-enq-nerkayacnum-amenahamex-eklerneri-patrastman-ays-hrashq-exanaky.html', 'Շատերին է թվում, թե այսպիսի ախորժելի և համեղ էկլերներ պատրաստելու համար մեծ ջանքեր են անհրաժեշտ', 'Многие думают, что для приготовления таких аппетитных և вкусных эклеров нужно приложить немало усилий.', 0, 0, 4, '2022-05-09 17:40:10', '2022-05-10 22:51:00'),
(61, 8, 'Հրաշալի քաղցրավենիք Ֆանտազիա. Նրբահամ գեղեցկություն', '', '<p>Այսօր մենք ձեզ հետ պատրաստելու ենք հրաշալի քաղցրավենիք Ֆանտազիա:&nbsp; Կարո՞ղ եք պատկերացնել, որ տորթի համար ձեզ պետք կգա մսաղաց: Բաց այս աղանդերի դեպքում դա իրականություն է: Սա իսկական համի ֆանտազիա է: Եվ այսպես, եկեք զբաղվենք տորթի պատրաստմամբ: Որքան պարզ, գեղեցիկ և համեղ է ստացվում: Հրաշալի քաղցրավենիք Ֆանտազիա. Անհրաժեշտ բաղադրիչները _Ձու 2 հատ _Աղ մի պտղունց _Շաքար 1-2 ճաշի գդալ _Վանիլային շաքար 1 թեյի գդալ _Եգիպտացորենի օսլա 2 ճաշի գդալ _Կաթ 500 մլ _Կարագ 40 գ _Շոկոլադ 100 գ Շերտերի համար _Ընկույզ 100 գր _Սալորաչիր 100 գր _Ծիրանաչիր 100 գր _Թխվածքաբլիթներ 100 գր _Կարագ 50 գր Հրաշալի քաղցրավենիք Ֆանտազիա. Պատրաստման եղանակը _Տարայի մեջ հարել 2 ձուն, ավելացնել մի պտղունց աղը և շաքարը, վանիլային շաքարը և օսլան: _Հարիչով հարել այնպես, որ գնդիկներ չլինեն: _Լցնել կաթը խառնուրդի մեջ և դնել կրակի վրա: Եփել մինչև թանձրանա: Եփելիս անընդհատ խառնել, որպեսզի զանգվածը չայրվի: _Վերցնել թանձրացած զանգվածը կրակից, անմիջապես ավելացնել կարագը և մանր կտրատած սև շոկոլադը, խառնել և կրեմը պատրաստ է: _Ծածկել սննդի թաղանթով այնպես, որ կպչի մակերեսին և առայժմ մի կողմ դնել: _Այժմ պատրաստել շերտերը. Դրա համար մսաղացով մանրացնել թխվածքաբլիթները, ընկույզը, սալորաչիրը, ծիրանի չիրը: _Այժմ մանրացված զանգվածին ավելացնել հալված կարագը (ի դեպ, բլենդերով էլ կարող եք ամեն ինչ մանրացնել) և խառնել: _Բացվող օղակը դնել ափսեի վրա, ավելի լավ է յուղաթուղթ դնել կողքերով: _Ափսեի վրա լցնել մի բաժին փշրանք, այնուհետև մի քանի ճաշի գդալ կրեմ քսել, նորից շաղ տալ փշրանքներ և նորից կրեմով և այսպես կրկնել մինչև վերջ: Պատրաստի աղանդերը զարդարեք շոկոլադի քերուկով: Տորթը դնել սառնարանում, որպեսզի ամբողջ գիշեր սառչի: Երբ սառչի, կտրեք այն կտորներով և մատուցեք: Բարի ախորժակ: Առավել շատ համեղ և հետաքրքիր բաղադրատոմսեր կգտնեք այստեղ՝&nbsp;Baxadratomser.am&nbsp;կայքում &nbsp;</p>', '', '/upload/778509.jpg', 'https://blognews.am/arm/news/778509/hrashali-qaxcraveniq-fantazia-nrbaham-gexeckutyun.html', 'Հրաշալի քաղցրավենիք Ֆանտազիա. Նրբահամ գեղեցկություն', 'Замечательные конфеты Фантазия. Нежная красота', 0, 0, 4, '2022-05-09 17:40:49', '2022-05-10 22:53:58'),
(62, 8, 'Իդեալական սպիտակ ջնարակ՝ վանիլային տորթերի համար', '', '<p>Բաղադրիչներ 1. Ժելատին &ndash; 0.5 թ/գ2. Ջուր &ndash; 3 ճ/գ3. Կիտրոնաթթու4. Շաքարավազ &ndash; 100 գ Պատրաստման եղանակը 1. Ժելատինը խառնել ջրի հետ: Թողնել, որ ուռչի: 2. Կաթսայի մեջ լցնել շաքարավազը, ավելացնել ջուր և կիտրոնաթթու: Հասցնել եռման և եփել 1 րոպե: 3. Վերցնել կրակից և ավելացնել ժելատինը: Հինգ րոպե հարել: 4. Պետք է ստացվի ձյան նման սպիտակ ջնարակ:</p>', '', '/upload/705122.jpg', 'https://blognews.am/arm/news/705122/idealakan-spitak-jnarak-vanilayin-torteri-hamar.html', 'Ժելատինը խառնել ջրի հետ: Թողնել, որ ուռչի:', 'Смешайте желатин с водой. Дать набухнуть.', 0, 0, 4, '2022-05-09 17:41:25', '2022-05-11 07:49:54'),
(63, 8, 'Խոնավ ու ախորժելի տորթ, որը հալվում է բերանում․ պատրաստվում է բավականին հեշտ ու պարզ եղանակով', '', '<div><img src=\"https://www.gustoase.net/wp-content/uploads/2020/01/77080e823e397c0dcf72c26270b215bc.jpg\" /></div>\n<div><img src=\"https://www.gustoase.net/wp-content/uploads/2020/01/maxresdefault-1-2-1024x624-1.jpg\" /></div>\n<div><img src=\"http://onwaves.su/wp-content/uploads/2020/06/7-2-500x321.jpg\" /></div>\n<p>Պատրաստեք այս հիասքանչ տորթը՝ ամենահասարակ բաղադրիչներից, և ուրախացրեք ձեր մտերիմներին։ Պահպանեք բաղադրատոմսը։ Անհրաժեշտ բաղադրիչներ կորժի համար․ շաքարավազ՝ 200գ ձու՝ 4 հատ կակաո՝ 3ճ․գ․ ալյուր՝ 200գ սերուցք 10%յուղ․՝ 200մլ, կորժը թրջելու համար կրեմի համար․ կաթ՝ 250մ; կոկոսի քերուկ՝ 200-250գ կարագ՝ 150գ շաքարավազ՝ 150գ Պատրաստման եղանակ․ Կորժ․&nbsp;սկզբում խորը տարայի մեջ 15 րոպե հարել ձուն՝ ավելացնելով շաքարավազը։ Պիտի սպիտակ փքուն զանգված ստացվի։ Ալյուրը միացնել կակաոյի հետ և փոքր բաժիններով ավելացնել ձվային խառնուրդին։ Խառնել փայտե գդալով։ Տապակին ձեթ քսել, խմորը լցնել մեջը և ուղարկել մինչ 180 ջերմաստիճանի տաքացրած ջեռոցը։ Պատրաստ լինելը ստուգել չոփիկով։ Թխվելու ժամանակը կախված է տապակի չափից։ Հանել կորժը ջեռոցից և թողնել, որ սառի։ Կրեմ․&nbsp;փոքր կաթսայի մեջ լցնել կաթը, կարագը և շաքարավազը։ Դնել կրակին և խառնել այնքան, որ կարագը հալվի։ Քիչ-քիչ լցնել կոկոսի քերուկը՝ անդադար խառնելով։ Եռալու պահից սկսած եփել կրեմը 15 րոպե՝ թույլ կրակի վրա։ Հաճախ խառնել։ Տորթ հավաքել․&nbsp;կորժը կտրել մեջտեղից, յուրաքանչյուր մասը թրջել սերուցքով, սակայն կարող եք չանել դա։ Մի կորժի վրա լցնել կրեմի կեսը, հավասարաչափ բաշխել, ծածկել մյուս կորժով, և քսել մնացած կրեմը։ Կարող եք զարդարել և հալացրած, և քերած շոկոլադով։ Բարի ախորժակ։</p>', '', '/upload/704865.jpg', 'https://blognews.am/arm/news/704865/khonav-u-akhorzheli-tort-ory-halvum-e-beranum%E2%80%A4-patrastvum-e-bavakanin-hesht-u-parz-exanakov.html', 'Պատրաստեք այս հիասքանչ տորթը՝ ամենահասարակ բաղադրիչներից, և ուրախացրեք ձեր մտերիմներին։ Պահպանեք բաղադրատոմսը։', 'Приготовьте этот чудесный торт из самых простых ингредиентов, և порадуйте своих близких. Сохраните рецепт.', 0, 0, 4, '2022-05-09 17:41:52', '2022-05-10 22:51:28'),
(64, 8, 'Խնձորով և նարինջով թխվածք', '', '<p>Բաղադրիչներ կարագ&nbsp;- 100գ ձու&nbsp;- 2 հատ շաքարավազ&nbsp;- 80գ փխրեցուցիչ&nbsp;- 1թ/գ &nbsp;ալյուր&nbsp;- 300գ կոկոսի քերուկ&nbsp;- 50գ խնձոր&nbsp;- 3 հատ նարինջ&nbsp;- 1 հատ շաքարի փոշի&nbsp;- 1 ճ/գ Պատրաստման եղանակը Ձուն հարել շաքարավազի հետ, ավելացնել հալեցրած կարագը և կոկոսի քերուկը։ Պատրաստել փափուկ խմոր ավելացնելով ալյուրն ու փխրեցուցիչը։ Խմորը տեղավորել թխման տարայի մեջ, բարձրացնել կողքերը։ Մաքրել և խորանարդաձև կտրատել խնձորն ու նարինջը, լցնել խմորի վրա, ցանել շաքարի փոշին։ Թխել 30-35 րոպե 180 աստիճանում։</p>', '', '/upload/704692.jpg', 'https://blognews.am/arm/news/704692/khndzorov-ev-narinjov-tkhvatsq.html', 'Խնձորով և նարինջով թխվածք', 'Яблочно-апельсиновый пирог', 0, 0, 4, '2022-05-09 17:42:30', '2022-05-10 22:54:13'),
(66, 5, 'Հետաքրքիր է ինչպե՞ս է թրքական բայրաքթարն առանց խոչընդոտների ՀՀ օդային տարածքով անցել հասել ադրբեջան', '', '<div><img src=\"https://scontent.fevn5-1.fna.fbcdn.net/v/t39.30808-6/277677097_1018398319035476_5183959978195949544_n.jpg?_nc_cat=105&amp;ccb=1-5&amp;_nc_sid=8bfeb9&amp;_nc_ohc=Gv8vwRPp7hMAX8Drkun&amp;_nc_ht=scontent.fevn5-1.fna&amp;oh=00_AT9AqizDOgRiRDSuMbmgpXiCO31F9elfLWfWDKdlXo4HJw&amp;oe=62488537\" /></div>\n<p>Երևանի երկնքում անընդհատ լսվում է ռազմական ինքնաթիռների ձայները։ Հետաքրքիր է ինչպե՞ս է թրքական բայրաքթարն առանց խոչընդոտների ՀՀ օդային տարածքով անցել հասել ադրբեջան։ Երևի սրանց ասած խաղաղության դարաշրջանն է սա։ Դե որ պաշտպանության նախարար կոչվածը նախընտրական քարոզչությամբ զբաղված լինի, ԳՇ պետ էլ չունենանք, պաշտպանվածության մակարդակն էլ սա պետք է լիներ։</p>', '', '/upload/1B90DD4E-B584-4CB7-949A-DE3B84ABE35F.png', 'https://blognews.am/arm/news/789732/hetaqrqir-e-inchpes-e-trqakan-bayraqtarn-aranc-khochyndotneri-hh-odayin-taratsqov-ancel-hasel-adrbejan.html', 'Երևանի երկնքում անընդհատ լսվում է ռազմական ինքնաթիռների ձայները։', 'В небе постоянно слышны звуки военных самолетов.', 0, 0, 12, '2022-05-09 17:44:14', '2022-05-12 09:03:01'),
(67, 5, '«Երազումս տեսել էի հարսանիքս ու Նրան». Անուշկա Մարգարյանը՝ աղի բլիթ ոտելու ավանդույթի ու իր հետաքրքիր երազների մասին', '', '<div><img src=\"https://style.news.am/static/add/dec4/247873740_488462962906296_8533190491938384743_n.jpg\" /></div>\n<p>News.am-ը գրում է. Դերասանուհի Անուշկա Մարգարյանն ամեն տարի՝ Սուրբ Սարգսի տոնի նախօրեին, &nbsp;ուտում է աղի բլիթ՝ հույսով, որ երազում կտեսնի իր միակին, սակայն ասպետի փոխարեն Անուշկան&nbsp;այլ&nbsp;հետաքրքիր երազներ է տեսնում, որոնց մասին&nbsp;կիսվել է&nbsp;NEWS.am STYLE-ի հետ: Անուշկան նշեց, որ այս տարի էլ բացառություն չի արել ու կերել է մայրիկի պատրաստա բլիթը. &laquo;Երազումս տեսել եմ բարեկամներիս, որոնք եկել էին մեր տուն՝ ինձ հետ կապված ինչ-որ առիթի, թե ինչ էր կոնկրետ՝ չգիտեմ&nbsp;(ժպտում է): Սեղանին ջուր կար դրված, ինքս մի բաժակ լցրի ու խմեցի&nbsp;(ծիծաղում&nbsp;է):&nbsp;Ինչպես միշտ՝ հույսս ինձ վրա եմ դնում&raquo;,-ասում է դերասանուհին՝ այնուհետեւ հավելելով,-&laquo;Մի օր առաջ, սակայն, երազումս տեսել էի հարսանիքս ու Նրան&raquo;: Դերասանուհին ասում է՝ ամեն տարի մեկի փոխարեն երկու անգամ է աղի բլիթ ուտում՝ ե&acute;ւ Սուրբ Սարգսի, ե&acute;ւ Սուրբ Վալենտինի տոներին:&nbsp;Նա հավելում է, որ դա իր համար զվարճալի խաղի պես մի բան է: &laquo;Երկու տարի շարունակ, ի դեպ, երազումս նույն մարդուն էի տեսել, բայց համառորեն ինձ ջուր չէր տալիս&raquo;,-կատակում է դերասանուհին,-&laquo;Մտածում էի՝ եթե կրկին նրան տեսնեմ, կսպանեմ, որ էլ հնարավորություն չունենամ տեսնելու, բայց այս, տարի լավ է, երազիս չհայտնվեց&nbsp;(ծիծաղում է): Եղել է տարի, որ համակարգչային խաղից հայտնի Մարիոյին եմ տեսել երազումս, մի տարի էլ՝ փիղ. չգիտեմ՝ ինչ է նշանակում&raquo;: Հարցին, որ տոնին է նախապատվությունը տալիս՝ Սուրբ Սարգսի՞ն, թե՞ Վալենտինին, դերասանուհին անկեղծորեն պատասխանեց, որ երկար տարիներ Սուրբ Վալենտինի օրն է ասոցացրել սիրո տոնի հետ, հիմա հավասարապես երկուսն էլ նշանավորում է. ինչպես ինքն է ասում՝ &laquo;Լավ է մեկի փոխարեն երկու սիրո տոն ունենալը&raquo;:</p>', '', '/upload/49AC6813-91C8-4A6F-B199-70B451CFD96F.png', 'https://blognews.am/arm/news/784662/erazums-tesel-ei-harsaniqs-u-nran-anushka-margaryany-axi-blit-otelu-avanduyti-u-ir-hetaqrqir-erazneri-masin.html', 'Դերասանուհի Անուշկա Մարգարյանն ամեն տարի՝ Սուրբ Սարգսի տոնի նախօրեին,  ուտում է աղի բլիթ՝ հույսով, որ երազում ...', 'Каждый год накануне Дня святого Саркиса актриса Анушка Маргарян ест соленый пончик, надеясь, что во сне...', 0, 0, 9, '2022-05-09 17:45:28', '2022-05-12 09:03:04'),
(68, 9, 'ԱՄՆ-ում վայրի բնություն են բաց թողել գենետիկորեն մոդիֆիկացված առաջին մոծակներին', '', '<p>ԱՄՆ-ում առաջին անգամ վայրի բնություն են բաց թողնվել գենետիկորեն մոդիֆիկացված մոծակներ՝ ստեղծված բիոտեխնոլոգիական Oxitec ընկերության կողմից։ Aedes aegypti տեսակի մոծակներն ունակ են կրել այնպիսի հիվանդություններ, ինչպիսին է, օրինակ, Զիկա վիրուսը, մլակային տենդը, Չիկունգունյա վիրուսը, դեղին տենդը։ Միայն Ֆլորիդա-Քիս կղզախմբում այս միջատների դեմ պայքարի համար յուրաքանչյուր տարի հատկացվում է միլիոն դոլար՝ հիմնականում &nbsp;միջատասպաններ ցողելու համար։ Այժմ կղզախմբում դուրս են թողնվել մոդիֆիկացված Oxitec մոծակներ։ Ընկերությունը մոծակների ձվերով լի տուփեր է տեղադրել վեց վայրում, և հաջորդող 12 շաբաթվա ընթացքում տուփերից պետք է դուրս գա շուրջ 12,000 արու մոծակ։ Այս մոծակները կրում են մահացու գեն։ Երբ մոդիֆիկացված մոծակները խաչասերվում են վայրի էգերի հետ, մահացու գենը փոխանցվում է նրանց ու սերունդներին։ Ընդհանրապես, միայն էգ մոծակներն են մարդկանց խայթում, այդ իսկ պատճառով գենետիկորեն մոդիֆիկացված արու մոծակները մարդուն հիվանդություններ փոխանցել չեն կարողանա։ Oxitec-ը նախկինում իր մոդիֆիկացված մոծակներին դուրս էր թողել Բրազիլիայում, Պանամայում և Մալայզիայում։ Ըստ ընկերության հաղորդման՝ տեղի պոպուլյացիաներն այս մոծակները կրճատել են 90%-ով։ ԱՄՆ-ում մոդիֆիկացված մոծակների դուրս թողումը փորձի առաջին փուլը կդառնա։ Ողջ ընթացքում մոծակները վերահսկողության տակ կգտնվեն, որպեսզի պարզ դառնա, թե որքան են դրանք հեռանում արկղերից, որքան են ապրում, իսկապես թունավորում են էգերին, թե ոչ։ Որպեսզի հեշտ լինի մոծակներին հետևելը, Oxitec-ը ստեղծել է գեն, որը թույլ է տալիս դրանց լուսավորվել հատուկ գույնով։</p>', '', '/upload/756749.jpg', 'https://blognews.am/arm/news/756749/amn-um-vayri-bnutyun-en-bac-toxel-genetikoren-modifikacvats-arajin-motsaknerin.html', 'ԱՄՆ-ում առաջին անգամ վայրի բնություն են բաց թողնվել գենետիկորեն մոդիֆիկացված մոծակներ՝ ստեղծված բիոտեխնոլոգիական ...', 'Впервые в США генетически модифицированных комаров, созданных в биотехнологии, выпустили на волю...', 0, 0, 11, '2022-05-09 17:46:43', '2022-05-11 14:01:56'),
(69, 2, '«Գեղեցիկ բնություն եւ գեղեցկուհի». Անահիտ Կիրակոսյանի լուսանկարը (Photo)', '', '<div><img src=\"https://blognews.am/static/000001/105936586_2188338991312138_47834014477296326_n.jpg\" /></div>\n<p>Դերասանուհի Անահիտ Կիրակոսյանը նոր լուսանկար է հրապարակել՝ ներկայացնելով իր հմայիչ կերպարը։</p>', '', '/upload/692036.jpg', 'https://blognews.am/arm/news/692036/gexecik-bnutyun-ev-gexeckuhi-anahit-kirakosyani-lusankary-photo.html', 'Դերասանուհի Անահիտ Կիրակոսյանը նոր լուսանկար է հրապարակել՝ ներկայացնելով իր հմայիչ կերպարը։', 'Актриса Анаит Киракосян опубликовала новое фото, продемонстрировав свой очаровательный образ.', 0, 0, 7, '2022-05-09 17:47:12', '2022-05-10 22:56:27'),
(70, 9, 'Տասնամյակում առաջին անգամ Հնդկաստանից տեսանելի են դարձել Հիմալայները. բնությունը շարունակում է մաքրվել', '', '<p>Նոր կորոնավիրուսի համաճարակը հարվածներ է հասցնում աշխարհի բոլոր պետությունների տնտեսություններին, սոցիալական վիճակին, ինչպես նաև, բնականաբար, մարդկանց առողջությանն ու կյանքին: Կարանտինային սահմանափակումները, հարկադիր ինքնամեկուսացումը փողոցներից հավաքեցին տրանսպորտն ու քաղաքացիներին: Գործարանները դադարեցին աշխատելուց: Արդյունաբերական գործունեությունը կանգ առավ: Այս ամենը միայն դրական ազդեցություն կարող է ունենալ մեր մոլորակի բնության համար: Բազմաթիվ ուսումնասիրություններ ցույց են տալիս, որ օդն ավելի մաքուր է դառնում, իսկ կենդանիները վերադառնում են այնտեղ, որտեղ մենք նրանց վաղուց չենք տեսել: Ուսումնասիրենք մի քանի թիվ, որոնք ներկայացրել է Աբդուլ իբն Ալ-Ատիայի անվան էներգիայի և կայուն զարգացման միջազգային ֆոնդը: Այսպես, Օսլոյի կլիմայի ուսումնասիրության միջազգային կենտրոնը նշել է, որ Չինաստանի տարածքի մեծ մասում փետրվար-մարտ ամիսներին օդի աղտոտվածությունը կրճատվել է 20-30%-ով՝ համեմատած նախորդ տարվա հետ: Եթե աղտոտվածության մակարդակը շարունակի որոշ ժամանակ ցածր մնալ, ապա միայն Չինաստանում կկանխվի 50,000-100,000 մարդու վաղ մահացության դեպք: Առողջապահության համաշխարհային կազմակերպության տվյալներով ամբողջ աշխարհում օդի աղտոտվածության պատճառով տարեկան մահանում է մոտ 7 միլիոն մարդ: Շնորհիվ համաճարակի՝ բնապահպանական ու առողջապահական կազմակերպությունները հնարավորություն կստանան գործնականում գնահատելու մարդու ազդեցությունը բնության վրա: Անգամ Անտարկտիդայի պինգվինները &laquo;ազատ շունչ են քաշել&raquo;, երբ այս տարի իրենց բնակավայրերում չեն հայտնվել կրուիզներ ու ուսումնասիրող արշավախմբեր: Հնդկաստանից հայտնում են, որ տասնամյակում առաջին անգամ կարողացել են տեսնել Հիմալայները: Կարանտինն ու դրա հետևանքով մաքրված օդը թույլ են տվել նկատել հսկայական Հիմալայները հնդկական հյուսիսային նահանգ Փենջաբում: Նույն իրավիճակն է նաև Իսպանիայում, Իտալիայում, ԱՄՆ-ում, Մեծ Բրիտանիայում և այլ երկրներում ու խոշոր քաղաքներում: Արգոնայի ազգային լաբորատորիայի մասնագետները հայտնել են, որ Նյու Յորքում օդի աղտոտվածության մակարդակը նախորդ տարվա համեմատ նվազել է 50%-ով: Համաձայն IQAir-ի տվյալների՝ Լոս Անջելեսը, որը, որպես կանոն, ԱՄՆ-ում ամենավատ որակի օդն ունի, վերածվել է ամենամաքուր քաղաքներից մեկի՝ համեմատած երկրի մյուս խոշոր քաղաքների հետ: Ցավալին այն է, որ համաճարակի ավարտից հետո այս ամենը ևս կդադարի: Հիմալայները կծածկվեն ծխով, և այս մաքրությունը միայն մեր հիշողությունների մեջ կմնա:</p>', '', '/upload/680197.jpg', 'https://blognews.am/arm/news/680197/tasnamyakum-arajin-angam-hndkastanic-tesaneli-en-dardzel-himalaynery-bnutyuny-sharunakum-e-maqrvel.html', 'Նոր կորոնավիրուսի համաճարակը հարվածներ է հասցնում աշխարհի բոլոր պետությունների տնտեսություններին, սոցիալական ...', 'Эпидемия нового коронавируса бьет по экономике всех стран мира, социальной...', 0, 0, 9, '2022-05-09 17:51:14', '2022-05-11 14:42:57'),
(71, 5, 'նիկոլը մի ամբողջ բանակով գնացելա մեր սրբավայր ԵՌԱԲԼՈՒՐ. այ չուլ, էսօր դու գիտես ի՞նչ օրա. Շուշին հանձնեցիր, հաղթանակը տանուլ տվեցիր, տղերքին սպանեցիր', '', '<p>նիկոլը մի ամբողջ բանակով գնացելա մեր սրբավայր ԵՌԱԲԼՈՒՐ այ չուլ էսօր դու գիտես ին՞չ օրա, դու ինչ՞ի համար ես գնացել ու ուր ես գնացել... Շուշիի ազատագրման օրնա... (ՇՈՒՇԻՆ հանձնեցիր) Հաղթանակի օրնա... ( տանուլ տվեցիր) ՏՂԵՐՔԻՆ սպանեցիր... (բայց գողեգող ու վախեցած հետդ տարել ես ազգի կեսին որին ոստիկան ես սարքել թու՜ քո մոր արգանդին )</p>', '', '/upload/1B90DD4E-B584-4CB7-949A-DE3B84ABE35F.png', 'https://www.blognews.am/arm/news/794517/nikoly-mi-amboxj-banakov-gnacela-mer-srbavayr-erablur-ay-chul-esor-du-gites-inch-ora-shushin-handznecir-haxtanaky-tanul-tvecir-txerqin-spanecir.html', 'բայց գողեգող ու վախեցած հետդ տարել ես ազգի կեսին որին ոստիկան ես սարքել', '', 0, 0, 172, '2022-05-09 18:52:56', '2022-05-12 09:14:00'),
(72, 5, '«Պատերազմն իրեն բերել էր, պատերազմն էլ տարավ». Հրայր Ասրյանը կամավոր է մեկնել մարտի դաշտ', '', '<p>&laquo;Հրոն մեզ համար չբացահայտված ընկեր է եղել&raquo;,-ասում է Արցախյան 44-օրյա պատերազմում զոհված Հրայր Ասրյանի ընկերը՝ Գևորգ Գևորգյանը:</p>\n<p>Հրայրը սովորել է Ճարտարապետության և շինարարության պետական համալսարանի ճարտարապետություն և դիզայն բաժինը՝ ստանալով ճարտարապետ-դիզայների որակավորում:</p>\n<p>Քույրը՝ Նարինե Խանիկյանը հիշում է, որ եղբայրն անընդհատ շտապում էր:</p>\n<p>&laquo;Ասում էր՝ կյանքում ամենաթանկը ժամանակն է: Չէր սիրում իր ժամանակն անիմաստ անցկացնել: Ինչ-որ մի գործ պետք է աներ, նկարեր, գծեր, ընկերների հետ լավ ժամանակ անցկացներ: Անընդհատ մատիտը ձեռքին էր&raquo;,-ասում է քույրը:</p>\n<p>Ընկերոջ խոսքով՝ Հրայրի երազանքը եղել է Արցախը շենացնելը, ծաղկեցնելը. &laquo;Իր երազանքն է եղել Արցախը կանչող տեղ դարձնելը&raquo;:</p>\n<p>2018թ. առաջարկ է ստացել Արցախի Հանրապետությունից՝ ստանձնել Շահումյանի շրջանի քաղաքաշինության և ճարտարապետության բաժնի պետի պաշտոնը:</p>\n<p>&laquo;Ոստիկանության շենքն է նախագծել, մշակույթի շենքն է նախագծել&raquo;,-ասում է քույրը:</p>\n<p>Նոյեմբերի 2-ին ԱՀ նախագահ Արայիկ Հարությունյանի կողմից հետմահու պարգևատրվել է &laquo;Արիության համար&raquo; մեդալով:</p>\n<p>Հրայր Ասրյանը ծնվել է 1988թ. ապրիլի 14 -ին: Հայրենիքի պաշտպանության համար մղվող մարտերում զահվել է հոկտեմբերի 31-ին:</p>\n<p>Մանրամասները՝ տեսանյութում:</p>', '', '/upload/Screen%20Shot%202022-05-10%20at%2019-34-48.png', 'https://iravaban.net/382082.html', '«Հրոն մեզ համար չբացահայտված ընկեր է եղել»,-ասում է Արցախյան 44-օրյա պատերազմում զոհված Հրայր Ասրյանի ընկերը՝ Գևորգ Գևորգյանը: Հրայրը սովորել է Ճարտարապետության և շինարարության պետական համալսարանի ճարտարապետություն և դիզայն բաժինը՝ ստանալով ճարտարապետ-դիզայների որակավորում: Քույրը՝ Նարինե Խանիկյանը հիշում է, որ եղբայրն անընդհատ շտապում էր: «Ասում էր՝ կյանքում ամենաթանկը ժամանակն է: Չէր սիրում իր ժամանակն անիմաստ անցկացնել: Ինչ-որ...  Read more &raquo;', '', 0, 0, 113, '2022-05-09 19:03:43', '2022-05-12 09:25:52'),
(73, 10, 'Հաղորդավարուհին գետն է ընկել ռետինե նավով նավարկելիս․ ԱԻՆ-ը նոր մանրամասներ է հայտնում', '', '<p>Արտակարգ իրավիճակների նախարարությունից նոր մանրամասներ են հայտնել հաղորդավարուհի Արմինե Ճաղարյանի մահվան վերաբերյալ։</p>\n<p>Գերատեսչության հայտարարության մեջ նշվում է, որ Արմինեն Դեբեդ գետում ռետինե նավով նավարկելիս է եղել, երբ ընկել է գետը։ Հաղորդավարուհի անգիտակից է եղել, երբ վերջինիս դուրս են բերել գետից։</p>\n<p>Ավելի վաղ հայտնել էինք, որ Արմինե Ճաղարյանի դին փրկարարները դուրս են բերել Ալավերդի քաղաքի &laquo;Սանահին&raquo; կայարանի մոտակայքից և մոտեցրել շտապօգնության ավտոմեքենային։</p>\n<p>Ալավերդու ԲԿ-ում արձանագրվել է վերջինիս մահը։</p>\n<p>Մահացածը &laquo;Շանթ&raquo; հեռուստաընկերությամբ հեռարձակվող &laquo;Ճամպրուկս ու ես&raquo; հաղորդման վարողն էր։</p>\n<p>&nbsp;</p>', '', '/upload/armine-tsagharyan-1-e1652108985208.jpg', 'https://iravaban.net/382140.html?fbclid=IwAR1He1BmfKoxoOFQI-TQJ0947OSHHhT0Xl0FR8awOm9PZXMbGiU-iDK1rOY', 'Արտակարգ իրավիճակների նախարարությունից նոր մանրամասներ են հայտնել հաղորդավարուհի Արմինե Ճաղարյանի մահվան վերաբերյալ։ Գերատեսչության հայտարարության մեջ նշվում է, որ Արմինեն Դեբեդ գետում ռետինե նավով նավարկելիս է եղել, երբ ընկել է գետը։ Հաղորդավարուհի անգիտակից է եղել, երբ վերջինիս դուրս են բերել գետից։ Ավելի վաղ հայտնել էինք, որ Արմինե Ճաղարյանի դին փրկարարները դուրս են բերել  Ալավերդի քաղաքի «Սանահին»...  Read more &raquo;', '', 0, 0, 771, '2022-05-09 20:11:36', '2022-05-12 08:39:53'),
(74, 1, 'Նիկոլ Փաշինյան կամ դու անցնցում հրաժարական ես տալիս, կամ մենք երկիրը կանգնացնում ենք. Իշխան Սաղաթելյան', '', '<p>Ֆրանսիայի հրապարակում մեկնարկել է &laquo;Դիմադրություն&raquo; շարժման մասնակիցների հանրահավաքը:</p>\n<p>Հանրահավաքին ելույթ ունեցավ նաև Աժ փոխխոսնակ Իշխան Սաղաթելյանը:</p>\n<p>&laquo;Համաշխարհային պատմության մեջ նման ֆանտաստիկ դեպքեր քիչ են լինում: Երբ պատերազմից հետո ծնկի եկած իշխանության պարագայում նման ծանր կորուստներից հետո ազգը ոտքի է կանգնում: Ժողովուրդ, սա բացառիկ դեպք է: Մեկ շաբաթվա ընթացքում մենք կարողացանք մեր ազգին ոտքի հանել: Դա փաստ է և իրականություն&raquo;,- ասաց նա:</p>\n<p>Դիմելով Նիկոլ Փաշինյանին Իշխան Սաղաթելյանն ասաց, որ նա ոչ միայն Ալիևին է պարտվել, այլև՝ սեփական ժողովրդին: Այն ժողովրդին, ովքեր 4 տարի առաջ այս օրերին իրեն բերեց իշխանության:</p>\n<p>&laquo;Այդ ժողովուրդը ոտքի է կանգնել և ասում է՝ հեռացիր: Մեր բազմահազարանոց երթը ինձ իրավունք է տալիս այս հրապարակից հայտարարելու. Նիկոլ Փաշինյան կամ դու անցնցում հրաժարական ես տալիս, կամ մենք երկիրը կանգնացնում ենք:</p>\n<p>Բանաձևը շատ հստակ է, պետական կառավարման համակարգը պետք է կանգնեցնենք: Իշխանության հեռացնելու միայն մի ճանապարհ գոյություն ունի. պետք է գործնականորեն փաստենք, որ իշխանությունը փողոցում է&raquo;,- ասաց ԱԺ փոխխոսնակը:</p>', '', '/upload/ishxan-saxatelyan-1-e1651774667114.jpg', 'https://iravaban.net/382216.html?fbclid=IwAR1rfLb86KuFpnyEowzOegOKhke9Kz4oHzma4O0VLoLZ3B28Z8UdhXoxW_g', 'Ֆրանսիայի հրապարակում մեկնարկել է «Դիմադրություն» շարժման մասնակիցների հանրահավաքը: Հանրահավաքին ելույթ ունեցավ նաև Աժ փոխխոսնակ Իշխան Սաղաթելյանը: «Համաշխարհային պատմության մեջ նման ֆանտաստիկ դեպքեր քիչ են լինում: Երբ պատերազմից հետո ծնկի եկած իշխանության պարագայում նման ծանր կորուստներից հետո ազգը ոտքի է կանգնում: Ժողովուրդ, սա բացառիկ դեպք է: Մեկ շաբաթվա ընթացքում մենք կարողացանք մեր ազգին ոտքի հանել: Դա...  Read more &raquo;', '', 0, 0, 280, '2022-05-09 20:26:51', '2022-05-12 07:23:09'),
(75, 1, 'Ներողություն եմ խնդրում այս օրերին տեղի ունեցած ցանկացած միջադեպի համար․ Իշխան Սաղաթելյան', '', '<p>Դատապարտում եմ բռնության ցանկացած դրսևորում և անձամբ ներողություն խնդրում այս օրերին տեղի ունեցած ցանկացած միջադեպի համար, որն անգամ փոքր անհարմարություն է պատճառել մեր հայրենակիցներից որևէ մեկին։ Այս մասին ֆեյսբուքյան իր էջում գրել է ԱԺ պատգամավոր Իշխան Սաղաթելյանը։</p>\n<p>&laquo;Գյուրմրիի դեպքերի առիթով միայն հարկ եմ համարում արձանագրել, որ միջադեպն արձանագրվել է իշխանության կազմակերպած սադրանքի և ՀՀ ոստիկանության հանցավոր անգործության արդյունքում։ Ի դեպ, չնայած այսօր վաղ առավոտյան Ոստիկանության կազմակերպած դիմակավորված շոուին, միջադեպի մասնակից երիտասարդներն ինքնակամ ներկայացել են ոստիկանական բաժանմունքներ, որովետև նրանք հայի արժանապատիվ տեսակն են։</p>\n<p>Սիրելի՛ հայրենակիցներ, այս իշխանությունը կեղծել է պատերազմը, թաքուն է պահել զոհերին, չի վերադարձնում գերիներին, հանձնում է հայրենիքը, սադրանքն ինչ է, որ այս մարդիկ չանեն։ Մենք գիտենք՝ ում դեմ ենք պայքարում, այս ամենը սպասելի և կանխատեսելի է։</p>\n<p>Սակայն, օգտվելով առիթից, կոչ եմ անում մեր երթերի և հավաքների բոլոր մասնակիցներին՝ դրսևորել զսպվածություն ու մեծահոգություն։ Մեր շարժման ամենասկզբում մենք պայմանավորվել ենք, որ սա իրական համերաշխություն բերող շարժում է, և որ այս պայքարը մեր բոլոր հայրենակիցների համար է։</p>\n<p>Պայքարը շարունակվում է և այն անկասելի է&raquo;,-գրել է նա։</p>\n<p>Ավելի վաղ Քննչական կոմիտեից հայտնել էին, որ Շիրակի մարզային քննչական վարչությունում քննվող քրեական գործի շրջանակներում կատարված քննչական և դատավարական այլ գործողությունների, ինչպես նաև ՀՀ ոստիկանության աշխատակիցների հետ համատեղ ձեռնարկված անհրաժեշտ միջոցառումների արդյունքում պարզվել է Գյումրի քաղաքի Երևանյան խճուղում, քաղաքական ընդդիմության կողմից կազմակերպված ավտոերթի ընթացքում, տարեց տղամարդկանց նկատմամբ բռնություն գործադրած երիտասարդներից երեքի ինքնությունը:</p>\n<p>Ինչպես արդեն տեղեկացրել ենք, 2022 թվականի մայիսի 8-ին մի խումբ երիտասարդներ, իջնելով ավտոմեքենայից, մոտեցել էին Գյումրու Երևանյան խճուղում գտնվող հասարակական տրանսպորտի կանգառում կանգնած տարեց տղամարդկանց և սկսել հարվածներ հասցնել նրանց մարմնի տարբեր մասերին: Պարզվել էր, որ վերոնշյալ երիտասարդների հանցավոր արարքներից տուժած տղամարդիկ 74, 56, 72 և 84 տարեկան քաղաքացիներ են:</p>\n<p>Նախաքննության ընթացքում ձեռնարկված միջոցառումների արդյունքում պարզվել է ենթադրյալ հանցագործությունը կատարած անձանցից երեքի ինքնությունը. նրանցից մեկը Արագածոտնի մարզի՝ 1991թ. ծնված բնակիչ Գոռ Սարգսյանն է, մյուսը՝ Երևան քաղաքի 1999թ. ծնված բնակիչ Տարոն Մանուկյանը, իսկ երրորդը՝ Արարատի մարզի բնակիչ, 1990թ. ծնված Արսեն Մարտոյանն է:</p>\n<p>Գոռ Սարգսյանը և Տարոն Մանուկյանը ձերբակալվել են ՀՀ քրեական դատավարության օրենսգրքի 129-րդ հոդվածի կարգով, իսկ Արսեն Մարտոյանը ներգրավվել է որպես մեղադրյալ և նրա նկատմամբ կալանավորումը որպես խափանման միջոց ընտրելու համար քննիչը որոշում է կայացրել՝ վերջինիս ձերբակալելու մասին:</p>\n<p>Նախաքննությունը շարունակվում է: Միջոցներ են ձեռնարկվում՝ միջադեպին մասնակից մյուս անձանց ինքնությունը պարզելու և նրանց արարքներին համապատասխան քրեաիրավական գնահատական տալու ուղղությամբ:</p>', '', '/upload/20220510141813557860.jpg', 'https://iravaban.net/382327.html', 'Դատապարտում եմ բռնության ցանկացած դրսևորում և անձամբ ներողություն խնդրում այս օրերին տեղի ունեցած ցանկացած միջադեպի համար, որն անգամ փոքր անհարմարություն է պատճառել մեր հայրենակիցներից որևէ մեկին։ Այս մասին ֆեյսբուքյան իր էջում գրել է ԱԺ պատգամավոր Իշխան Սաղաթելյանը։ «Գյուրմրիի դեպքերի առիթով միայն հարկ եմ համարում արձանագրել, որ միջադեպն արձանագրվել է իշխանության կազմակերպած սադրանքի և ՀՀ ոստիկանության...  Read more &raquo;', '', 0, 0, 104, '2022-05-10 11:19:05', '2022-05-12 06:47:31'),
(76, 5, 'Գյումրիում տարեց տղամարդկանց նկատմամբ բռնություն գործադրելու դեպքով կան ձերբակալվածներ', '', '<p>ՀՀ քննչական կոմիտեի Շիրակի մարզային քննչական վարչությունում քննվող քրեական գործի շրջանակներում կատարված քննչական և դատավարական այլ գործողությունների, ինչպես նաև ՀՀ ոստիկանության աշխատակիցների հետ համատեղ ձեռնարկված անհրաժեշտ միջոցառումների արդյունքում պարզվել է Գյումրի քաղաքի Երևանյան խճուղում, քաղաքական ընդդիմության կողմից կազմակերպված ավտոերթի ընթացքում, տարեց տղամարդկանց նկատմամբ բռնություն գործադրած երիտասարդներից երեքի ինքնությունը:</p>\n<p>Ինչպես արդեն տեղեկացրել ենք, 2022 թվականի մայիսի 8-ին մի խումբ երիտասարդներ, իջնելով ավտոմեքենայից, մոտեցել էին Գյումրու Երևանյան խճուղում գտնվող հասարակական տրանսպորտի կանգառում կանգնած տարեց տղամարդկանց և սկսել հարվածներ հասցնել նրանց մարմնի տարբեր մասերին: Պարզվել էր, որ վերոնշյալ երիտասարդների հանցավոր արարքներից տուժած տղամարդիկ 74, 56, 72 և 84 տարեկան քաղաքացիներ են:</p>\n<p>Նախաքննության ընթացքում ձեռնարկված միջոցառումների արդյունքում պարզվել է ենթադրյալ հանցագործությունը կատարած անձանցից երեքի ինքնությունը. նրանցից մեկը Արագածոտնի մարզի՝ 1991թ. ծնված բնակիչ Գոռ Սարգսյանն է, մյուսը՝ Երևան քաղաքի 1999թ. ծնված բնակիչ Տարոն Մանուկյանը, իսկ երրորդը՝ Արարատի մարզի բնակիչ, 1990թ. ծնված Արսեն Մարտոյանն է:</p>\n<p>Գոռ Սարգսյանը և Տարոն Մանուկյանը ձերբակալվել են ՀՀ քրեական դատավարության օրենսգրքի 129-րդ հոդվածի կարգով, իսկ Արսեն Մարտոյանը ներգրավվել է որպես մեղադրյալ և նրա նկատմամբ կալանավորումը որպես խափանման միջոց ընտրելու համար քննիչը որոշում է կայացրել՝ վերջինիս ձերբակալելու մասին:</p>\n<p>Նախաքննությունը շարունակվում է: Միջոցներ են ձեռնարկվում՝ միջադեպին մասնակից մյուս անձանց ինքնությունը պարզելու և նրանց արարքներին համապատասխան քրեաիրավական գնահատական տալու ուղղությամբ:</p>\n<p>Ծանուցում. Ենթադրյալ հանցանքի մեջ կասկածվողը կամ մեղադրվողը համարվում է անմեղ, քանի դեռ նրա մեղավորությունն ապացուցված չէ ՀՀ քրեական դատավարության օրենսգրքով սահմանված կարգով` դատարանի` օրինական ուժի մեջ մտած դատավճռով:</p>', '', '/upload/20220510142002685152.jpg', 'https://iravaban.net/382325.html', 'ՀՀ քննչական կոմիտեի Շիրակի մարզային քննչական վարչությունում քննվող քրեական գործի շրջանակներում կատարված քննչական և դատավարական այլ գործողությունների, ինչպես նաև ՀՀ ոստիկանության աշխատակիցների հետ համատեղ ձեռնարկված անհրաժեշտ միջոցառումների արդյունքում պարզվել է Գյումրի քաղաքի Երևանյան խճուղում, քաղաքական ընդդիմության կողմից կազմակերպված ավտոերթի ընթացքում, տարեց տղամարդկանց նկատմամբ բռնություն գործադրած երիտասարդներից երեքի ինքնությունը: Ինչպես արդեն տեղեկացրել ենք, 2022 թվականի մայիսի...  Read more &raquo;', '', 0, 0, 61, '2022-05-10 11:20:44', '2022-05-12 09:53:49'),
(77, 1, 'Ինձ թվում է՝ թուրքերի ու ՔՊ-ի տեքստերը նույն տեղում են գրվում. Իշխան Սաղաթելյան', '', '<p>Այս պահին Երևանի տարբեր ծայրամասերից չորս ուղղություններով շարունակվում են անհնազանդության բողոքի ակցիաները։</p>\n<p>Գայի պողոտայից դեպի Ֆրանսիայի հրապարակ շարժվող երթի ժամանակ ԱԺ փոխնախագահ Իշխան Սաղաթելյանն անդրադարձավ Ադրբեջանի ԱԳ նախարարի հայտարարություններին։</p>\n<p>&laquo;Քիչ առաջ Ադրբեջանի ԱԳ նախարարը հայտարարությամբ է հանդես եկել և ասում է նույն տեքստերը, ինչ Նիկոլը, Քոչարյան Անդոն, Ալեքսանյան Վահագը, Թորոսյան Արսենը ու ի շարք ՔՊ-ական պատգամավորներ&raquo;,- հայտարարեց Իշխան Սաղաթելյանը։</p>\n<p>Նրա խոսքով՝ Ադրբեջանի ԱԳ նախարարն ասում է, որ Հայաստանում ընդամենը 5-6 հազար մարդ է պայքարում Արցախի համար, այդ հարցը փակված է և դա կեղծ օրակարգ է։ &laquo;Ինձ թվում է՝ թուրքերի ու ՔՊ-ի տեքստերը նույն տեղում է գրվում&raquo;։</p>', '', '/upload/20220510142319234449.jpg', 'https://iravaban.net/382324.html', 'Այս պահին Երևանի տարբեր ծայրամասերից չորս ուղղություններով շարունակվում են անհնազանդության բողոքի ակցիաները։ Գայի պողոտայից դեպի Ֆրանսիայի հրապարակ շարժվող երթի ժամանակ ԱԺ փոխնախագահ Իշխան Սաղաթելյանն անդրադարձավ Ադրբեջանի ԱԳ նախարարի հայտարարություններին։ «Քիչ առաջ Ադրբեջանի ԱԳ նախարարը հայտարարությամբ է հանդես եկել և ասում է նույն տեքստերը, ինչ Նիկոլը, Քոչարյան Անդոն, Ալեքսանյան Վահագը, Թորոսյան Արսենը ու ի շարք ՔՊ-ական...  Read more &raquo;', '', 0, 0, 63, '2022-05-10 11:24:50', '2022-05-12 06:17:16'),
(78, 6, 'Ադրբեջանի նախարարը Հայաստանի առաջարկներն առաջարկ չի համարում', '', '<p>Դեռ փետրվարին ադրբեջանական կողմը ներկայացրեց Հայաստանին խաղաղության պայմանագրի 5 հիմնարար սկզբունքները. Հայաստանը հայտարարեց, որ այդ սկզբունքներն իրենց հարմար չեն։ Այս մասին, ըստ ԱՊԱ-ի, հայտարարել է Ադրբեջանի արտգործնախարար Ջեյհուն Բայրամովը։</p>\n<p>Նրա խոսքով՝ ավելի ուշ տեղեկություններ հայտնվեցին Հայաստանի 6 առաջարկների մասին. &laquo;Բայց ամենահետաքրքիրն այն է, որ բոլորը տեսան Հայաստանի ընդունած փաստաթուղթը։ Սակայն եթե նայեք փաստաթղթին, ապա այն առաջարկներ կոչել չի կարելի։ Ներկայացման պահին այդ փաստաթուղթն Ադրբեջանի առաջարկներին հայկական կողմի արձագանքի տեսքով էր։ Ես կարող եմ թվարկել արձագանքի այդ 6 կետերից մի քանիսը, եւ դուք եւս կարող եք եզրակացություն անել, թե որքանով դրանք կարելի է առաջարկություն անվանել&raquo;:</p>\n<p>Նախարարը նշել է, որ կետերից մեկն այն է, թե ինչու է ադրբեջանական կողմը փետրվարի 21-ի հիմնական առաջարկները մարտի սկզբին ներկայացրել&nbsp;Հայաստանին. &laquo;Ադրբեջանն առաջին իսկ օրվանից նշել է, որ հիմնական առաջարկները ներկայացվել են միջնորդների միջոցով։ Դրա մասին պետք է հարցնել միջնորդներին: Եթե ​​Ադրբեջանը նշել է փետրվարի 21-ը, ապա ներկայացրել է հենց այդ օրը։ Հաջորդ կետն այն է, որ Հայաստանը որեւէ տարածքային պահանջ չունի Ադրբեջանից։ Դեռ 1992-ին երկու երկրներն էլ, լինելով ԱՊՀ անդամ, ճանաչել են միմյանց տարածքային ամբողջականությունը։ Եւ դա ինչ-որ չափով Ադրբեջանի հիմնարար սկզբունքի կրկնությունն է&raquo;։</p>\n<p>&nbsp;</p>', '', '/upload/20220510142617938695.jpg', 'https://news.am/arm/news/700969.html', 'Ներկայացման պահին այդ փաստաթուղթն Ադրբեջանի առաջարկներին հայկական կողմի արձագանքի տեսքով էր...', '', 0, 0, 49, '2022-05-10 11:26:29', '2022-05-12 06:17:33'),
(79, 6, 'Ադրբեջանը հույս ունի Տավուշի եւ Արարատի մարզերի 7 գյուղ ստանալ', '', '<p>Սահմանագծման արդյունքներով Ադրբեջանը հույս ունի&nbsp; Տավուշի եւ Արարատի մարզերի գյուղեր ստանալ։</p>\n<p>&laquo;Ղազախի եւ Քյարքիի 7 գյուղերի վերադարձը կիրականացվի սահմանագծման գործընթացի պահանջին համապատասխան&raquo;,- ասել է Ադրբեջանի փոխարտգործնախարար Խալաֆ Խալաֆովը, տեղեկացրել է APA-ն։</p>\n<p>Խալաֆ Խալաֆովը նշել է, որ &laquo;Ադրբեջանի կազմ այդ տարածքների վերադարձի հետ կապված հարցը լուծվելու է անցկացվող բանակցությունների արդյունքում&raquo;։</p>\n<p>&laquo;Հայաստանը երբեք չի կարողանա հայտարարել, որ այդ տարածքները Ադրբեջանի տարածք չեն։ Այդ տարածքներն Ադրբեջանի տարածքներն են։ Քանի որ այդ տարածքների վերադարձն Ադրբեջանի կազմ պահանջում է սահմանագծման գործընթացը, հարցը կդիտարկվի այդ շրջանակում։ Այդ տարածքների վերադարձն Ադրբեջանի կազմ կքննարկվի եւ լուծում կստանա&raquo;,- համոզված է փոխարտգործնախարարը։</p>', '', '/upload/20220510142818051922.jpg', 'https://news.am/arm/news/700956.html', 'Սահմանագծման արդյունքներով Ադրբեջանը հույս ունի գյուղեր ստանալ Տավուշի եւ Արարատի մարզերից...', '', 0, 0, 129, '2022-05-10 11:29:48', '2022-05-12 07:53:22'),
(80, 5, '«Իրեն մի կերպ դուրս է հանել հաստոցից ու քայլել դեպի հարեւան տեխնիկաները»․ պատմում է Սոթքում վիրավորվածի հորեղբայրը', '', '<p>Սոթքի հանքավայրում մայիսի 7-ին թշնամու կրակոցից վիրավորված 35-ամյա հորատող Արմեն Մաթեւոսյանը այսօր դուրս է գրվել հիվանդանոցից, նրա վիճակը կայուն է։ Այս մասին NEWS.am-ի հետ զրույցում ասաց վիրավորի հորեղբայրը՝ Զինավուր Մաթեւոսյանը։</p>\n<p>&laquo;Գնդակը վնասել էր ուսը, ներքին օրգանները չէր վնասել, սակայն եթե գնդակը միլիմետրի տարբերությամբ աջ գնար, կկպներ սրտին, եթե մեկ միլիմետր ձախ շարժվեր, կվնասեր զարկերակը։ Բժիշկներն ապշած էին, թե ինչպես գնդակը չի վնասել իր ներքին օրգանները։ Բավականին բարդ վիրահատություն եղավ, Աստծու կամոք փրկվել է, կարելի է ասել հրաշք է տեղի ունեցել&raquo;,- նշեց Զինավուր Մաթեւոսյանը։</p>\n<p>Հորեղբայրն ասաց, որ դեռ հայտնի չէ՝ երբ կկազդուրվի իր զարմիկը, քանի որ վերքը շատ խորն է։</p>\n<p>Վիրավորի հորեղբայրը մեզ հետ զրույցում մանրամասնեց, թե ինչպես է դեպքը տեղի ունեցել։</p>\n<p>Նրա խոսքով՝ իր զարմիկը վիրավորվել է 15։30-ի սահմանում, սակայն մինչ այդ էլ հանքավայրի ուղղությամբ կրակոցներ հնչել։</p>\n<p>&laquo;Իրենք ընդմիջում են արել, հետո եղբորս տղան գնացել է իր տեխնիկայի դիմապակիները մաքրելու, որ վերսկսի աշխատանքը։ Աշխատանքները սկսվել են, ինքը կանգնած է եղել՝ ուշադրությունն ամբողջությամբ կենտրոնացրած հորատաձողի վրա, ու այդ պահին իրեն կրակել են&raquo;,- պատմեց վիրավորի հորեղբայրը։</p>\n<p>Հորեղբոր խոսքով՝ նա գիտակցությունը չի կորցրել, ուժեղ հարված է զգացել ուսին եւ արյունը տեսնելով է հասկացել է, որ վիրավորվել է։</p>\n<p>&laquo;Ինքը հասցրել է իրեն մի կերպ դուրս հանել հաստոցից, որ արնաքամ չլինի ու մի քանի քայլ է արել դեպի հարեւան տեխնիկաները, այդտեղից էլ արդեն նկատել են իրեն, բարձրացրել մեքենան ու հեռացրել տարածքից&raquo;,- նշեց Զինավուր Մաթեւոսյանը։</p>\n<p>Զինավուր Մաթեւոսյանը եւս աշխատում է Սոթքի հանքավայրում։ Հիմա նրա խոսքով՝ հայկական կողմի հատվածն ամբողջությամբ գտնվում է ադրբեջանական տեսադաշտում եւ շատ մոտ է նրանց։</p>\n<p>&laquo;Հատկապես, որ մեր աշխատանքները տեղի են ունենում մութ փոսի մեջ, իսկ իրենք էլ բարձունքներում են, առավելություն է իրենց համար&raquo;,- ասաց նա։</p>\n<p>Հիշեցնենք, որ մայիսի 7-ին թշնամին ինտենսիվ կրակ էր բացել Սոթքի հանքի ուղղությամբ։ Դրա հետեւանքով վիրավորել է հանքավայրի աշխատակիցը։</p>\n<p>Սոթքում վիրավորվածը հանքավայրի 35-ամյա հորատողն է․ նրա վիճակը կայուն է</p>', '', '/upload/20220510210919404687.jpg', 'https://news.am/arm/news/701040.html', 'Գնդակը վնասել էր ուսը, ներքին օրգանները չէր վնասել, սակայն եթե գնդակը․․․', '', 0, 0, 32, '2022-05-10 18:10:05', '2022-05-12 06:17:33'),
(81, 5, 'Ծեծի են ենթարկել նիկոլական ակտիվիստ ռուստամ ավագյանին, ով առավել աչքի է ընկնում արցախցիների հանդեպ իր լկտի ու անհանդուրժող կեցվածքով (Video)', '', '<div class=\"telegram-embed-content\"><iframe src=\"https://t.me/blognewsarm/7485?embed=1\" width=\"400\" height=\"900\" data-mce-fragment=\"1\"><span id=\"mce_marker\" data-mce-type=\"bookmark\">﻿</span></iframe></div>', '', '/upload/20220510211239982652.jpg', 'https://www.blognews.am/arm/news/794703/tsetsi-en-entarkel-nikolakan-aktivist-rustam-avagyanin-ov-aravel-achqi-e-ynknum-arcakhcineri-handep-ir-lkti-u-anhandurzhox-kecvatsqov-video.html', 'Video', '', 0, 0, 892, '2022-05-10 18:14:33', '2022-05-12 08:22:59'),
(82, 1, 'Այսօր պարզվում է, որ Իլհամն ունի համախոհներ Հայաստանի ներսում… Հմայակ Հովհաննիսյան', '', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 20px; color: #222222; font-family: \'work sans\', sans-serif; font-size: 16px; background-color: #ffffff;\">Մեջբերենք Իշխան Սաղաթելյանի ելույթը՝</p>\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 20px; color: #222222; font-family: \'work sans\', sans-serif; font-size: 16px; background-color: #ffffff;\">Նիկոլ Փաշինյանը կա՛մ անցնցում հրաժարական է տալիս, կամ &laquo;Դիմադրություն&raquo; շաժումը երկիրը կանգնեցնում է, այս մասին Ֆրանսիայի հրապարակում հայտարարեց ԱԺ փոխնախագահ Իշխան Սաղաթելյանը։</p>\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 20px; color: #222222; font-family: \'work sans\', sans-serif; font-size: 16px; background-color: #ffffff;\">&laquo;Ակնհայտ փաստ է, Նիկոլը այլևս Հայաստանում իշխանություն չունի, մեր հետևողական քայլերի արդյունքում, մեր համախմբվածության, ճիշտ գործողությունների արդյունքում մեր պայքարը րոպե առ րոպե թափ է հավաքում։ Ուրեմն, ի՞նչ պետք է անենք, բանաձևը շատ հստակ է․ պետական կառավարման համակարգը պետք է կանգնեցնենք։ Իշխանությանը հեռացնելու մեկ ճանապարհ գոյություն ունի, որ գոծնականորեն փաստենք, որ իշխանությունը փողոցում է, մենք ենք որոշում։ Աթոռներին նստածներն այլևս ի վիճակի չեն մեր երկրում որևէ բան որոշելու։ Դրա համար ինչ պետք է անենք, պետք է ավելի լայն տարածումով շարունակենք մեր բողոքի, անհնազանդության ակցիաները։ Սիրելի՛ հայրենակիցներ, վաղը շատ կարևոր օր է բոլորիս համար։ Եթե այնքան մարդ, որքան մասնակցեց երթին, վաղը դուրս գա փողոց կոնկրետ անհնազանդության գործողություններ և ակցիաներ անելու, ձեզ վստահեցնում եմ՝ հաշված օրերի ընթացքում հեռացնելու ենք&raquo;,&ndash; ասաց նա։</p>\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 20px; color: #222222; font-family: \'work sans\', sans-serif; font-size: 16px; background-color: #ffffff;\">Իշխան Սաղաթելյանը նշեց, որ վաղը հայտարարում են դասադուլ և գործադուլ և յուրաքանչյուր ցուցարար վաղը իր հետ պետք է փողոց է դուրս բերի ևս մեկ քաղաքցու։ &laquo;Եթե մենք այդքանով սկսենք ամբողջ Երևանում մեր անհնազանդության ակցիաները անհրաժեշտության դեպքում շրջափակենք պետական, կառավարական շենքերը, մենք վերջնականապես ծնկի ենք բերելու իշխանություններին&raquo;,&ndash; նշեց նա՝ հավելելով, որ բոլորը պետք է հասկանան, որ Նիկոլ Փաշինյանի ամեն ավել օր իշխանության մնալը չի բխում երկրի շահերից և այդ իսկ պատճառով պետք է միանան ընդդիմության պայքարին։</p>\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 20px; color: #222222; font-family: \'work sans\', sans-serif; font-size: 16px; background-color: #ffffff;\">ԱԺ փոխնախագահը նշեց, որ պետք է կրկնապատկեն ուժերը և շարունակեն անհնազանդության ակցիաները, մինչև որ ուժային կառույցները վարչապետին կտեղեկացնեն, որ ինքն այլևս անելիք չունի։</p>\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 20px; color: #222222; font-family: \'work sans\', sans-serif; font-size: 16px; background-color: #ffffff;\">Իշխան Սաղաթելյանը ներկայացրեց վաղվա անելիքները՝ նշելով, որ վաղը՝ ժամը 12-ին, չորս ուղղությունով սկսելու են անհնազանդության երթեր և ակցիաներ։ Առաջին երթը կմեկնարկի Գայի պողոտայից՝ &laquo;Մեգամոլ&raquo; առևտրի կենտրոնի մոտից, երկրորդը Րաֆֆու փողոցից՝ Անդրանիկի արձանի մոտից, երրորդը՝ 15 թաղամասից՝ Չաուշի արձանի մոտից և չորրորդը՝ Սասունցի Դավթի արձանի մոտից։</p>\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 20px; color: #222222; font-family: \'work sans\', sans-serif; font-size: 16px; background-color: #ffffff;\">Իշխան Սաղաթելյանը նաև տեղեկացրեց, որ առավոտյան նույնպես տեղի կունենան անհնազանդության տարբեր ակցիաներ։ Ժամը 19.00-ին Ֆրանսիայի հրապարակում տեղի կունենա ամփոփիչ հանրահավաք։</p>\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 20px; color: #222222; font-family: \'work sans\', sans-serif; font-size: 16px; background-color: #ffffff;\">Գրառումը՝</p>\n<p><iframe style=\"box-sizing: border-box; max-width: 100%; color: #222222; font-family: \'work sans\', sans-serif; font-size: 16px; background-color: #ffffff; border-width: initial; border-style: none; overflow: hidden;\" src=\"https://www.facebook.com/plugins/post.php?href=https%3A%2F%2Fwww.facebook.com%2F100005017092773%2Fposts%2F2179851192192096%2F%3Fd%3Dn&amp;width=500&amp;show_text=true&amp;appId=850716548468324&amp;height=523\" width=\"500\" height=\"523\" frameborder=\"0\" scrolling=\"no\" allowfullscreen=\"allowfullscreen\" loading=\"lazy\"></iframe></p>', '', '/upload/3AFE1FD4-CAEB-45F4-B798-B94A378890A9-scaled.jpg', '', '', '', 0, 0, 567, '2022-05-10 20:02:48', '2022-05-12 08:59:52'),
(83, 5, 'Քրիստոսի արձան-համալիրի մրցույթում հաղթել է քանդակագործ Արմեն Սամվելյանի առաջարկը', '', '<p>&laquo;Բարգավաճ Հայաստան&raquo; կուսակցության ղեկավար Գագիկ Ծառուկյանը ֆեյսբուքյան իր էջում գրել է.</p>\n<p>&laquo;Հանձնախումբը և ժողովուրդը համակարծիք էին: Շնորհավորում եմ տաղանդավոր քանդակագործ Արմեն Սամվելյանին և շնորհակալ եմ բոլորին՝ պատասխանատու և պրոֆեսիոնալ մոտեցման համար: Ուրախ եմ, որ Փրկչի մոնումենտալ արձան-համալիր ունենալու գաղափարը համախմբել էր քանդակագործների՝ Հայաստանից, Արցախից և Սփյուռքից:</p>\n<p>Շնորհակալ եմ հանձնախմբին՝ ավելի քան 4 ամիս շարունակ սրտացավ աշխատանքի համար:</p>\n<p>Հայաստանը պետք է կարողանա նորովի ներկայանալ աշխարհին. մենք հին մշակույթով, հին գրականությամբ, հին պատմությամբ ազգ ենք, և աշխարհը մեզ այդպես պետք է ճանաչի&raquo;։</p>', '', '/upload/20220510230436930896.png', 'https://iravaban.net/382490.html?fbclid=IwAR0TpgVkWwrFFtrpMC_nO3rV_EkCErORWSSJVPE_0BdhrNq-754xa39UdhY', '«Բարգավաճ Հայաստան» կուսակցության ղեկավար Գագիկ Ծառուկյանը ֆեյսբուքյան իր էջում գրել է. «Հանձնախումբը և ժողովուրդը համակարծիք էին: Շնորհավորում եմ տաղանդավոր քանդակագործ Արմեն Սամվելյանին և շնորհակալ եմ բոլորին՝ պատասխանատու և պրոֆեսիոնալ մոտեցման համար: Ուրախ եմ, որ Փրկչի մոնումենտալ արձան-համալիր ունենալու գաղափարը համախմբել էր քանդակագործների՝ Հայաստանից, Արցախից և Սփյուռքից: Շնորհակալ եմ հանձնախմբին՝ ավելի քան 4 ամիս շարունակ սրտացավ...  Read more &raquo;', '', 0, 0, 47, '2022-05-10 20:05:09', '2022-05-11 19:15:05'),
(84, 5, 'ՏԵՍԱՆՅՈՒԹ ‼️+18‼️ Մեկնաբանություններ թողնում եմ ձեզ։‼️ ՏԵՍԱՆՅՈՒԹ․ Ինչպես են ծեծում «Արցախցին մենակ «Բայրաքթար» կուլ տա» հայտարարություն անողին Տեսանյութում երեվում է թե ինչպես են Արցախի դրոշի վրա թքողին և Արցախցուն, Հային բայրաքթարների բաժին համարողին, Հային հայհոյողին ծեծի ենթարկում...‼️', '', '<p><iframe style=\"border: none; overflow: hidden;\" src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fwatch%2F%3Fv%3D391545509362672%26extid%3DCL-UNK-UNK-UNK-AN_GK0T-GK1C%26ref%3Dsharing&amp;width=500&amp;show_text=false&amp;appId=1435834523503598&amp;height=910\" width=\"500\" height=\"910\" frameborder=\"0\" scrolling=\"no\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/Screen%20Shot%202022-05-10%20at%2023-13-18.png', '', '', '', 0, 1, 495, '2022-05-10 20:13:43', '2022-05-12 09:08:45'),
(85, 1, 'ՏԵՍԱՆՅՈւԹ. Վանաձորի ժողովուրդը բառի բուն իմաստով լարեց Քոչարյանասերժքկաններին', '', '<p><iframe style=\"border: none; overflow: hidden;\" src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fararatnews.am%2Fvideos%2F706822543852522%2F&amp;width=500&amp;show_text=false&amp;appId=850716548468324&amp;height=281\" width=\"500\" height=\"281\" frameborder=\"0\" scrolling=\"no\" allowfullscreen=\"allowfullscreen\" loading=\"lazy\"></iframe></p>', '', '/upload/896AD216-4B77-4AC2-9D99-FD7E1B4AECDA.jpg', '', '', '', 0, 1, 375, '2022-05-10 20:30:18', '2022-05-12 09:52:11'),
(86, 11, 'Պատրաստ են կալանավորել պատերազմում տունը կորցրած մեր հայրենակցին՝ պատճառաբանելով, որ ՀՀ-ում չունի մշտական բնակության վայր․ Թովմասյանը շտապ գրություն է ուղարկել դատախազին', '', '<p>ԱԺ Մարդու իրավունքների պաշտպանության և հանրային հարցերի մշտական հանձնաժողովի նախագահ Թագուհի Թովմասյանը շտապ գրություն է ուղարկել ՀՀ Գլխավոր դատախազ Արթուր Դավթյանին։</p>\n<p>Ֆեյսբուքյան էջում Թովմասյանը տեղեկացրել է՝ ահազանգ է ստացել, որ ոստիկանության աշխատակից ներկայացած, դիմակավորված, առանց տարբերանշանների և ինչ-որ անհասկանալի համազգեստով անձինք խաբեությամբ՝ խուզարկություն կատարելու պատրվակով մուտք են գործել ՀՀԿ երիտասարդական կազմակերպության նախագահ Հենրիխ Դանիելյանի բնակարան և նրան բերման ենթարկել ՀՀ ոստիկանության Կումայրիի բաժին, որտեղ վերջինս հարցաքննվել է, որից հետո՝ ազատ արձակվել։</p>\n<p>&laquo;Օրեր առաջ էլ ձերբակալվել, ապա կալանավորվել է &laquo;Դիմադրության&raquo; շարժման մասնակից Դավիթ Ավագյանը այն հիմքով, որ վերջինս ՀՀ-ում չունի մշտական բնակության վայր, այնինչ, Դավիթ Ավագյանը Հադրութից է, պատերազմի արդյունքում կորցրել է իր տունն ու տեղափոխվել Երևան: Նա նաև նշել էր, որ վերջինիս խնամքին են գտնվում երեք անչափահաս որդիները: Սակայն ոստիկանությունը, հաշվի չառնելով այս հանգամանքը, ձերբակալել է վերջինիս, ապա բաժանմունքում փոխել զեկուցագրի բովանդակությունը և Դավիթ Ավագյանին վերագրել ենթադրյալ խուլիգանություն:</p>\n<p>Այսինքն, ստացվում է՝ քաղաքական այլախոհությունը պատժելու համար իշխանությունը պատրաստ է դիմել ցանկացած ոչ իրավաչափ գործողության՝ կալանավորելով անգամ պատերազմի արդյունքում իր տունը կորցրած մեր հայրենակցին՝ պատճառաբանելով, որ վերջինս ՀՀ-ում չունի մշտական բնակության վայր:</p>\n<p>Այս դեպքերի առթիվ շտապ գրություն եմ ուղարկել ՀՀ Գլխավոր դատախազ Արթուր Դավթյանին և խնդրել վերջինիս հանձնարարականը՝ հնարավոր իրավախախտում կատարած ոստիկանության աշխատակիցների շրջանակը պարզելու համար, միաժամանակ, ուսումնասիրության ենթարկելու նրանց գործողությունների իրավաչափությունը և դիտարկելու նրանց արարքներին համապատասխան իրավական գնահատական տալու հարցը, ինչպես նաև ընթացքի և արդյունքների մասին տրամադրել տեղեկություն&raquo;, &ndash; նշել է պատգամավորը։</p>', '', '/upload/20220511112242154824.jpg', 'https://iravaban.net/382531.html', 'ԱԺ Մարդու իրավունքների պաշտպանության և հանրային հարցերի մշտական հանձնաժողովի նախագահ Թագուհի Թովմասյանը շտապ գրություն է ուղարկել ՀՀ Գլխավոր դատախազ Արթուր Դավթյանին։ Ֆեյսբուքյան էջում Թովմասյանը տեղեկացրել է՝ ահազանգ է ստացել, որ ոստիկանության աշխատակից ներկայացած, դիմակավորված, առանց տարբերանշանների և ինչ-որ անհասկանալի համազգեստով անձինք խաբեությամբ՝ խուզարկություն կատարելու պատրվակով մուտք են գործել ՀՀԿ երիտասարդական կազմակերպության նախագահ Հենրիխ Դանիելյանի բնակարան...  Read more &raquo;', '', 0, 0, 32, '2022-05-11 08:24:09', '2022-05-12 07:04:50'),
(87, 10, 'Հյուրանոցում 49-ամյա կինը դանակով խփել է 40-ամյա տղամարդուն, նա էլ խլելով դանակը՝ խփել է կնոջ որովայնին. ոստիկանություն', '', '<p>Մայիսի 10-ին, ժամը 18.50-ին, ոստիկանություն զանգած քաղաքացին հայտնել է, որ Դավիթ Անհաղթի փողոցի հասցեներից մեկում անհայտ քաղաքացին դանակահարել է իր ընկերուհուն: Րոպեներ անց երկու տարբեր հիվանդանոցներից է հաղորդում ստացվել. կտրած-ծակած վերքերով բուժօգնության էին տեղափոխվել 40-ամյա տղամարդ և 49-ամյա կին:</p>\n<p>Ոստիկանության Քանաքեռ-Զեյթունի բաժնի ծառայողները պարզել են, որ ժամը 18.30-ին Դավիթ Անհաղթի փողոցի հյուրանոցում տեղի ունեցած վիճաբանության ժամանակ 49-ամյա կինը դանակով խփել էր 40-ամյա տղամարդուն, նա էլ խլելով դանակը՝ խփել է կնոջ որովայնին՝ պատճառելով վնասվածքներ:</p>\n<p>Հանցագործության գործիք դանակը հայտնաբերվել և առգրավվել է:</p>\n<p>Նշանակվել են փորձաքննություններ:</p>\n<p>Կատարվում է նախաքննություն:</p>\n<p>Ծանուցում. Ենթադրյալ հանցանքի մեջ կասկածվողը կամ մեղադրվողը համարվում է անմեղ, քանի դեռ նրա մեղավորությունն ապացուցված չէ ՀՀ քրեական դատավարության օրենսգրքով սահմանված կարգով` դատարանի` օրինական ուժի մեջ մտած դատավճռով:</p>', '', '/upload/20220511112450565529.jpg', 'https://iravaban.net/382530.html', 'Մայիսի 10-ին, ժամը 18.50-ին, ոստիկանություն զանգած քաղաքացին հայտնել է, որ Դավիթ Անհաղթի փողոցի հասցեներից մեկում անհայտ քաղաքացին դանակահարել է իր ընկերուհուն: Րոպեներ անց երկու տարբեր հիվանդանոցներից է հաղորդում ստացվել. կտրած-ծակած վերքերով բուժօգնության էին տեղափոխվել 40-ամյա տղամարդ և 49-ամյա կին: Ոստիկանության Քանաքեռ-Զեյթունի բաժնի ծառայողները պարզել են, որ ժամը 18.30-ին Դավիթ Անհաղթի փողոցի հյուրանոցում տեղի ունեցած վիճաբանության...  Read more &raquo;', '', 0, 0, 322, '2022-05-11 08:28:08', '2022-05-12 10:01:36');
INSERT INTO `posts` (`id`, `category_id`, `hy_title`, `ru_title`, `hy_content`, `ru_content`, `image`, `source`, `hy_description`, `ru_description`, `is_general`, `has_video`, `viewed`, `created_at`, `updated_at`) VALUES
(88, 1, 'Վազգեն Մանուկյանը կրկին վերադարձել է պայքարի կիզակետ. «Հրապարակ»', '', '<p>&laquo;Հրապարակ&raquo; թերթը գրում է.</p>\n<p>&nbsp;</p>\n<p>&laquo;Նախկին վարչապետ, մեկ տարի առաջ Հայրենիքի փրկության շարժման վարչապետի թեկնածու Վազգեն Մանուկյանը, որը շարժումը մարելուց հետո նեղսրտել էր ընդդիմությունից՝ պայքարը մինչեւ վերջ չտանելու եւ արտահերթ ընտրություններին համաձայնելու համար, տեւական ժամանակ մեկուսացել էր, եւ նրա նեղսրտվածությունը ժամանակ առ ժամանակ երեւում էր հարցազրույցներից, որոնք, որպես կանոն, իր կնոջ կայքին է տալիս՝ ankakh.com-ին, նախօրեին Ֆրանսիայի հրապարակում էր։ Դավիթ Շահնազարյանի հետ նա տեղավորվել էր &laquo;Ազատագրում&raquo; վրանի ներսում եւ զրուցում էր։</p>\n<div class=\"text-center\">\n<div class=\"text-center\">\n<div class=\"text-center\">\n<div class=\"ads-el-container d-inline-block mceNotEditable\" style=\"height: 120px;\"><span class=\"ads-element mceNotEditable Uxankyun\">Գովազդ</span></div>\n</div>\n</div>\n</div>\n<p>Մենք Մանուկյանից փորձեցինք հետաքրքրվել՝ կրկին վերադարձել է պայքարի կիզակե՞տ, կատակեցինք՝ գուցե ժամանակավոր վարչապետի թեկնածուի խնդի՞ր է դարձյալ առաջացել։ Սակայն Մանուկյանն ասաց, որ ինքն առաջին իսկ օրվանից հրապարակում է։</p>\n<p>&nbsp;</p>\n<p>&laquo;Միգուցե վրան առաջին անգամ եմ եկել, բայց ես այստեղ եմ ամեն օր&raquo;,- ասաց նա եւ հրաժարվեց այլ հարցերի պատասխանել&raquo;:</p>\n<p>&nbsp;</p>\n<p>Առավել մանրամասն՝ թերթի այսօրվա համարում</p>', '', '/upload/vazgen-manukyan.jpg', '', '', '', 0, 0, 58, '2022-05-11 08:35:12', '2022-05-12 08:36:06'),
(89, 1, 'ԱԺ մեծամասնությունը ՔՊ-ական, թուրքական անկլավ են․ վերցնենք՝ շպրտենք իրենց', '', '<p>Ադրբեջանցիները միակ անկլավները, որ ունեն Հայաստանում՝ նիկոլական անկլավներն են: ԱԺ մեծամասնությունը ՔՊ-ական, թուրքական անկլավ են: Վերցնենք՝ շպրտենք իրենց: Կառավարությունում ծվարած ոչնչությունները ադրբեջանական-թուրքական անկլավներ են:Վերցնենք՝ շպրտենք իրենց: Ոստիկանների մեջ, դատախազությունում կան մեկումեջ անկլավներ: Դրանք էլ պետք է ՝ վերցնենք՝ շպրտենք իրենց:</p>\n<p><iframe src=\"https://www.facebook.com/plugins/video.php?height=314&amp;href=https%3A%2F%2Fwww.facebook.com%2Farmen.ashotyan%2Fvideos%2F3211406272430786%2F&amp;show_text=false&amp;width=560&amp;t=0\" width=\"560\" height=\"314\"></iframe></p>', '', '/upload/20220511113810241996.jpg', 'https://www.blognews.am/arm/news/794730/adrbejancinery-miak-anklavnery-or-unen-hayastanum-nikolakan-anklavnern-en.html', 'Կառավարությունում ծվարած ոչնչությունները ադրբեջանական-թուրքական անկլավներ են:Վերցնենք՝ շպրտենք իրենց:', '', 0, 0, 164, '2022-05-11 08:39:16', '2022-05-12 09:49:41'),
(90, 2, 'Հայհոյանքներ, սադրանք, քաշքշուկ՝ ՔՊ-ական դերասան Գնել Սարգսյանի կատարմամբ (Video)', '', '<p>&nbsp;</p>\n<p><iframe src=\"https://www.youtube.com/embed/I5PkkL8Msdc\" width=\"560\" height=\"315\"></iframe></p>', '', '/upload/20220511114038518918.jpg', 'https://www.blognews.am/arm/news/794715/hayhoyanqner-sadranq-qashqshuk-qp-akan-derasan-gnel-sargsyani-katarmamb-video.html', 'Տեսանյութ', '', 0, 1, 102, '2022-05-11 08:41:00', '2022-05-12 08:45:30'),
(91, 5, 'Հայդուկներ. ովքե՞ր էին նրանք և ի՞նչ կյանքով էին ապրում (Photo)', '', '<div>Հայդուկներ: Ովքե՞ր են նրանք, ի՞նչ գործունեություն են ծավալել և&nbsp; ի՞նչ ազդեցություն են ունեցել հայոց պատմության էջերում: Հայդուկը առավելապես Բալկաններում և նախկին Օսմանյան կայսրության այլ տարածքներում պարտիզանական պայքար մղող ազատամարտիկների տարածված անվանումն է։ Ո՞վ էր հայդուկը Հայդուկը հարստություն և ունեցվածք ոչ միայն չուներ, այլև ունենալու իրավունք էլ չուներ։ Նրա կյանքը նվիրաբերված էր հայրենիքի ազատությանը և պատկանում էր միայն ու միայն հայրենիքին։</div>\n<div>&nbsp;</div>\n<div>Այդ մարդիկ անընդհատ շարժման մեջ էին։ Դեռ լույսը չբացված՝ նրանք հեռանում էին, և ոչ ոք չպետք է իմանար, թե որ ուղղությամբ և ուր են գնում նրանք։ Հայդուկների բնակարանը քարայրն էր, ձնե տնակը, լեռներն ու կիրճերը, իսկ գյուղերում՝ անասնագոմի կամ մարագի մութ անկյունը։ Գիշերը հայդուկի սեփականությունն էր, գիշերը հայդուկն էր դրության տերը, իսկ ցերեկը նրա գլխավոր հոգսը թաքնվելն ու չնկատվելն էր։ Նա տեղափոխվում, տեղից տեղ էր անցնում գիշերը՝ ապավինելով մթությանը։ Գնում էր սարերով, կիրճերով ու ձորերով, որպեսզի մարդու չհանդիպեր, իսկ երբ լույսը բացվում էր, մտնում էր թաքստոց։ Հայդուկը մահվան դեմ չէր մաքառում։ Նրա կռիվը թշնամու, իր հայրենիքը բռնադատողների դեմ էր։ Նրա օրերն անցնում էին ձյան ու բքի մեջ, անձրևի ու կարկուտի տակ։ Պետք էր միշտ աչալուրջ լինել, որ եթե թշնամի հանդիպեր, առաջինը հայդուկը պետք է կրակեր։ Յուրաքանչյուր հայդուկ պետք է կարողանար կռվել ու հաղթել 10-20 թշնամու։ Եթե ռազմական գործողությունների կարիք չկար, հայդուկների մի մասին թույլատրում էին գնալ իրենց գյուղերը, կազմակերպել պաշտպանական ջոկատներ։ Հայ հայտնի հայդուկներ Հայդուկների շարժումը Հայաստանում նույնպես մեծ տեղ է գտել: Այն լայն տարածում է ստացել 19-րդ դարից սկսած: Պետք է նշենք, որ պատմության մեջ նրանք իրենց ուրույն տեղն են զբաղեցրել: Հայ հայդուկների շարքում կարևոր է նշել Արաբոյին, Աղբյուր Սերոբին, Անդրանիկին, Դժոխք Հրայրին, Գալեին, Մախլուտոյին, Գևորգ Չաուշին։</div>', '', '/upload/20220511114901646993.jpg', 'https://www.blognews.am/arm/news/725638', 'Հայ մեծերի պատմությունը', '', 0, 0, 62, '2022-05-11 08:49:49', '2022-05-12 09:06:00'),
(92, 1, 'ՏԵՍԱՆՅՈՒԹ․ Թող ոչ մեկը Փաշինյանին ու իր «բանդա»-ին չշփոթի այլախոհության հետ․ Աննա Մկրտչյան', '', '<p>Ֆրանսիայի հրապարակում քիչ առաջ ելույթ ունեցավ Ազգային ժողովի &laquo;Պատիվ ունեմ&raquo; խմբակցության պատգամավոր Աննա Մկրտչյանը։ Նա հայտարարեց, թե ՀՀ արժանապատիվ քաղաքացիներն այլախոհության հետ խնդիր չունեն։</p>\n<p>Մանրամասները՝ տեսանյութում:</p>\n<p><iframe src=\"https://www.youtube.com/embed/HDRpTWvOdfg\" width=\"640\" height=\"360px\"></iframe></p>', '', '/upload/20220511121633948295.jpeg', 'https://armlur.am/1185255/', 'Ելույթ ունեցավ Աննա Մկրտչյան', 'Анна Мкртчян выступила с речью', 0, 1, 173, '2022-05-11 09:17:57', '2022-05-12 08:59:51'),
(93, 5, 'Ինչու է գեներալ Օգանովսկին կրակել երիտասարդների մեքենայի ուղղությամբ. կրակոցների հետքերով', '', '<p>ArmLur.am-ին մանրամասներ են հայտնի դարձել Արցախի պաշտպանության բանակի հրամանատարի նախկին տեղակալ, գեներալ-մայոր Սամվել Կարապետյանի կողմից հնչած կարկոցների մասին: Մինչ կրակոցները վեճ է եղել գեներալի և մի քանի երիտասարդների միջև: Մասնավորապես՝ Սամվել Կարապետյանը մեքենան կայանել է մի վայրում եւ խնդրել է մի քանի երիտասարդների իրենց մեքենան մի փոքր այն կողմ տանել կայանել: Երիասարդները սկսել են կոպիտ խոսել, թե &laquo;մենք ոնց ուզենք այդպես կանենք&raquo;, որից հետո Կարապետյանն իջել է մեքենայից՝ խոսելու երիտասարդների հետ, սակայն խոսակցությունները վերածվել են վեճի եւ ինչպես ArmLur.am-ի հետ զրույցում նշեցին վերջինիս մտերիմները, գեներալի նյարդերը տեղին են տվել և ընդամենը վախեցնելու համար, նա կրակել է երիտասարդների մեքենայի ուղղությամբ:</p>\n<p>Հիշեցնենք, որ մայիսի 10-ին ժամը 19:00-ի սահմաններում Ստեփանակերտի &laquo;Վերածննդի&raquo; հրապարակում հնչել են կրակոցներ, որի հետևանքով վնասվել էր Ստեփանակերտ քաղաքի բնակչին պատկանող ավտոմեքենան</p>\n<p>Դեպքի առթիվ հարուցվել է քրեական գործ Քրեական օրենսգրքի 269-րդ հոդվածի 4-րդ մասով (խուլիգանություն, որը կատարվել է զենքի կամ որպես զենք օգտագործվող առարկաների գործադրմամբ) և 245-րդ հոդվածի 1-ին մասով (ապօրինի կերպով հրազեն ձեռք բերելը, պահելը, փոխադրելը կամ կրելը) նախատեսված հանցանքների հատկանիշներով: Դատախազությունից հայտնել էին, որ ենթադրյալ հանցագործության կատարման կասկածանքով ձերբակալվել է Արցախի Հանրապետության քաղաքացի Ս.Կ.-ն՝ Արցախի պաշտպանության բանակի հրամանատարի նախկին տեղակալ, գեներալ-մայոր Սամվել Կարապետյանը։</p>', '', '/upload/20220511122110741967.jpeg', 'https://armlur.am/1185359/', 'Նոր մանրամասներ', '', 0, 0, 96, '2022-05-11 09:21:51', '2022-05-12 09:21:30'),
(94, 6, 'Բանակցությունները խափանման եզրի՞ն են. Բաքուն նոր «փաստարկներ» է hանում', '', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #333333; font-family: AriaArmenianD_U-Regular; font-size: 14px; background-color: #ffffff;\">Արդեն մի քանի օր է, ինչ Բաքվի լրատվամիջոցները, հղում անելով պաշտոնական աղբյուրներին ու փորձագետներին, գրում են այն մասին, որ Հայաստանը մերժել է Ադրբեջանի ծրագիրը, և բանակցությունները խաթարվել են։ Բաքվի փորձագետները նշում են նաև, որ հայ-թուրքական վերջին հանդիպման ժամանակ ոչ մի բանի շուրջ համաձայնության չեն եկել։</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #333333; font-family: AriaArmenianD_U-Regular; font-size: 14px; background-color: #ffffff;\">Մինչ այդ Բաքուն ասում էր, որ Հայաստանը համաձայն է իրենց բոլոր պայմաններին։ Բրյուսելում Ալիևի վարչակազմի ղեկավարի տեղակալ Հիքմեթ Հաջիևի հանդիպումից հետո ՀՀ ԱԽ քարտուղար Արմեն Գրիգորյանն ասաց, որ Բաքուն, ըստ էության, ընդունել է Հայաստանի առաջարկած պատասխան 6 կետը, և բանակցությունները տեղի կունենան 5 + 6 բանաձեւով։ Բաքուն մի քանի օր անց հերքել է Գրիգորյանի խոսքերը․ հայտարարվել է, որ Գրիգորյանը արտահոսք է թույլ տվել, Բաքուն չի ընդունել Հայաստանի առաջարկը։ Մոսկվան իր հերթին հայտարարել է, որ բանակցություններն ընթանալու են բացառապես Բաքվի առաջարկների հիման վրա, &laquo;որը Երևանն ընդունելի է համարել&raquo;։</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #333333; font-family: AriaArmenianD_U-Regular; font-size: 14px; background-color: #ffffff;\">Հայկական կողմը հայտարարել է, որ կշարունակի բանակցությունները, եթե հաշվի առնվեն 6 հայկական առաջարկները՝ կապված Ղարաբաղի բնակչության իրավունքների և անվտանգության հետ, որոնք, ինչպես խորհրդարանում ասաց Նիկոլ Փաշինյանը, առնչվում են Արցախի կարգավիճակին։</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #333333; font-family: AriaArmenianD_U-Regular; font-size: 14px; background-color: #ffffff;\">Դատելով Բաքվի ԱԳ նախարարի և նրա տեղակալի հայտարարություններից՝ Հայաստանի ներկայացրած կետերն անընդունելի են Բաքվի համար։ &laquo;Եթե դուք նայեք այդ փաստաթղթին, ապա այն ընդհանրապես առաջարկներ չեք կարող անվանել։ Ներկայացման պահին այս փաստաթուղթը հայկական կողմի արձագանքի տեսքով էր Ադրբեջանի առաջարկներին&raquo;, ասել է նախարար Բայրմովը։</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #333333; font-family: AriaArmenianD_U-Regular; font-size: 14px; background-color: #ffffff;\">Նշվել է նաեւ, որ թեև Բրյուսելում համաձայնություն կար սահմանազատման և խաղաղության պայմանագրի նախապատրաստման երկկողմ հանձնաժողովի ստեղծման մասին, իսկ Մոսկվայում պայմանավորվածություն կար, որ այդ հանձնաժողովը կստեղծվի մինչև ապրիլի վերջ, հայկական կողմը դեռ վերջնական պատասխան չի տվել։</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #333333; font-family: AriaArmenianD_U-Regular; font-size: 14px; background-color: #ffffff;\">Հատուկ հանձնարարություններով դեսպան Էդմոն Մարուքյանն ասել է, որ ադրբեջանական կողմը, որին ապրիլի 6-ին փոխանցվել են հայկական առաջարկները, համաձայնել է շարունակել քննարկումներն այդ հիմքով։</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: #333333; font-family: AriaArmenianD_U-Regular; font-size: 14px; background-color: #ffffff;\">Մոսկվա կատարած այցից հետո Նիկոլ Փաշինյանը խորհրդարանում հայտարարեց, որ համաձայն է Բաքվի առաջարկած կետերի հետ, ավելին, նա մի շարք աղմկահարույց հայտարարություններ արեց, որոնք հավանության արժանացան Բաքվում և Մոսկվայում, որոնք ամեն ինչ անում են, որ Հայաստանը դուրս չգա համաձայնեցված ուղուց։ Բաքուն որպես փաստարկ առաջ է քաշում անկլավների հարցը։ &laquo;Այդ տարածքները Ադրբեջանին հանձնելու հարցը կքննարկվի, և որոշում կգտնվի&raquo;,- ասել է փոխարտգործնախարար Խալաֆովը։ Հայկական կողմն ասում է, որ անկլավները Ադրբեջանին պատկանելը հաստատող փաստաթղթեր չկան։</p>', '', '/upload/mos.jpg', 'https://www.lragir.am/2022/05/11/716434/', '', '', 0, 0, 86, '2022-05-11 09:27:10', '2022-05-12 09:25:04'),
(95, 10, '«Գետը արագ էր հոսում, վայրկյանների ընթացքում հեռվացնում էր Արմինեին». ռեժիսորը՝ Արմինե Ճաղարյանի մահվան դեպքի մասին', '', '<p>&laquo;Շանթ&raquo; հեռուստաընկերությամբ հեռարձակվող&nbsp; &laquo;Ճամպրուկս ու ես&raquo; հաղորդումը&nbsp; վարող Արմինե Ճաղարյանը նկարահանող խմբի եւ &laquo;Ռաֆթինգ ին Արմենիա&raquo; ընկերության աշխատակիցների հետ Դեբեդ գետում էր ռաֆթինգի մասին թողարկում պատրաստելու նպատակով։</p>\n<p>NEWS.am-ի հետ զրույցում հաղորդման ռեժիսոր Հակոբ Կարանֆիլյանը պատմեց, որ իրենց&laquo;Ռաֆթինգ ին Արմենիա&raquo; ընկերությունից նախօրոք զգուշացրել էին եղանակային վատ պայմանների մասին, սակայն հետո նշել, թե կարող են գալ.</p>\n<p>&laquo;Նավակում 7 հոգով էինք, նկարահանող խմբից 3 հոգով էինք, 4-ը ռաֆթինգի աշխատակիցներն էին։ Գետի վրա էինք մոտ 15 րոպե, գետի մեջտեղում քար կար, նավարկության ընթացքում այդ քարին կպանք, նավակը թեքվեց, Արմինեն ընկավ ջուրը, հետո ես ընկա ջուրը, իրեն միանգամից գետը տարավ, ես կարողացա մի կերպ դուրս գալ, ամուսինը թռավ գետը, որ փրկի Արմինեին, հետեւից թռան հրահանգավորող աշխատակիցները, բայց նրանց այդպես էլ չհաջողվեց փրկել Արմինեին։ Գետը շատ արագ էր հոսում, վայրկյանների ընթացքում հեռվացնում էր Արմինեին։ Մոտ 10 կիլոմետր քշել էր, նրան Ալավերդում փրկարարները հանեցին ջրից&raquo;։</p>\n<p>Նշենք, որ դեպքի առնչությամբ Քննչական կոմիտեում նյութեր են նախապատրաստվում, դատաբժշկական փորձաքննությունից հետո հայտնի կլինի՝ Քրեական օրենսգրքի որ հոդվածով քրեական գործ կհարուցվի։</p>', '', '/upload/20220511131729507719.jpg', 'https://news.am/arm/news/701109.html', 'Մոտ 10 կիլոմետր քշել էր…', '', 0, 0, 87, '2022-05-11 10:17:53', '2022-05-12 09:53:19'),
(96, 1, 'Աննա Մկրտչյան, թալանչին քո պապան ա, քո հերը․․․ Հերդ էլ թալանի համար հենց հիմա ԱԱԾ մեկուսարանում ա', '', '<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px; color: #3a3a3a; font-family: Lato, sans-serif; font-size: 18px; background-color: #ffffff;\">Սերժ Սարգսյանը մի սեփական պատգամավորուհի ունի՝ Աննա Մկրտչյանը։ Օրեր առաջ լրագրողը մոտեցել ու հարց էր տալիս, իսկ հարցին ի պատասխան այս անկիրթ աղջիկը գոռում էր ու գործարարին մեղադրում թալանի մեջ։</p>\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px; color: #3a3a3a; font-family: Lato, sans-serif; font-size: 18px; background-color: #ffffff;\">Հիմա հատուկ Աննա Մկրտչյանի համար․</p>\n<div class=\"text-center\">\n<div class=\"text-center\">\n<div class=\"ads-el-container mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n</div>\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px; color: #3a3a3a; font-family: Lato, sans-serif; font-size: 18px; background-color: #ffffff;\">Անգրագետ տիկին, թալանը անում են պետական պաշտոնյաները, ոչ թե գործարարները: Գործարարները բարիք են ստեղծում, հարկ են վճարում, աշխատատեղեր են ապահովում։ Թալանն անում էր քո հայրը, որը հիմա բանտարկված է, թալանն անում էր քո քաղաքական հայր Սերժիկը, որը հավանաբար կնստի, թալանն անում էին քո կուսակիցները։ Գործարարը, ում թալանի մեջ էիր մեղադրում, տարիներ շարունակ միլիոնավոր դոլարների հարկ է վճարել, որից քո հայրեր Սերժիկն ու Արան թալանել են։</p>\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px; color: #3a3a3a; font-family: Lato, sans-serif; font-size: 18px; background-color: #ffffff;\">Թալանչի ասելուց կնայես հայելու մեջ ու լավ կտեսնես քեզ։ Թալանչին հայրդ ա, քո պապան․․․</p>', '', '/upload/anna.png', 'https://havasti.info/2022/05/08/%d5%a1%d5%b6%d5%b6%d5%a1-%d5%b4%d5%af%d6%80%d5%bf%d5%b9%d5%b5%d5%a1%d5%b6-%d5%a9%d5%a1%d5%ac%d5%a1%d5%b6%d5%b9%d5%ab%d5%b6-%d6%84%d5%b8-%d5%ba%d5%a1%d5%ba%d5%a1%d5%b6-%d5%a1-%d6%84%d5%b8-%d5%b0/?fbclid=IwAR3WIicJagkaUmIp9GvAuR_APUXPBLuhSHF0qkHNW_4wNbGPdmI5AqZNiWM', '', '', 0, 0, 177, '2022-05-11 10:21:46', '2022-05-12 09:21:35'),
(97, 5, 'ՏԵՍԱՆՅՈՒԹ․ Քաղաքացին աղբը իջեցնելու փոխարեն վերևից ցած է նետում․ ահազանգ', '', '<div class=\"post--item post--single post--title-largest\" style=\"box-sizing: border-box; position: relative; z-index: 0;\">\n<div class=\"post--content\" style=\"box-sizing: border-box; margin-top: 14px;\">\n<p style=\"box-sizing: border-box; margin: 0px;\">Ֆեյսբուքյան օգտատեր &laquo;<span style=\"box-sizing: border-box; font-weight: bold;\">Nana Knar</span>&raquo; իր էջում տեսանյութ է հրապարակել Երևանի Ամիրյան փողոցից և կից գրել․ &laquo;<span style=\"box-sizing: border-box; font-weight: bold;\">Ինչ համակեցության կանոն, ինչ էթիկա ? Սրանց վարձել են , որ աղբը դուրս բերեն ու մարդկանց չխանգարեն, իսկ իրենք այ սենց գռդոն են անում &hellip; ու երկու բազմաբնականար շենքից ընդամենը երկու հոգի արձագանքեց, մնացածը լուռ հետևում էին... #կենտրոն #ամիրյան</span>&raquo;։&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 0px;\">&nbsp;</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<p style=\"box-sizing: border-box; margin: 0px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 0px;\">Հրապարակված տեսանյութերից երկրորդում երևում է, որ ներքևում կանգնած տղամարդ կա, որը հավաքում է աղբը և տեղափոխում տարածքում կանգնած մեքենայի մեջ։&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 0px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 0px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 0px;\">Երևանի քաղաքապետարանի լրատվության վարչության պետ Լեւոն Սարդարյանն էլ ֆեյսբուքյան իր էջում անդրադառնալով տարածվող տեսանյութին՝ նշում է․ &laquo;<span style=\"box-sizing: border-box; font-weight: bold;\">Շենքից շպրտվող աղբի հետ կապված մի իրական դեպք հիշեցի։ Մոտ տասնհինգ տարի առաջ Մոսկվայում մի վրացի որոշում ա տան ինտերիերը թարմացնի։ Տունը իններորդ հարկում ա լինում ու որոշում ա, որ բազմոցն առաջին հարկ հասցնելու ամենահեշտ տարբերակը վերևից շպրտելն ա։ Նայում ա, որ բակում մարդ չլինի ու միացնում ա բազմոցի ազատ անկման ռեժիմը։ Պատմությունը ծիծաղելի կլիներ, եթե չավարտվեր ողբերգությամբ։ Թարսի պես, էդ պահին շենքի բնակիչներից մեկը շքամուտքից դուրս ա գալիս...</span></p>\n<p style=\"box-sizing: border-box; margin: 0px;\">&nbsp;</p>\n<div class=\"middle-news mceNotEditable\" data-url=\"/loader/single-related-post?category=5&amp;post_type=popular\">\n<p>Կատեգորիա - 5</p>\n<p>Փոստի տեսակը - popular</p>\n</div>\n<p style=\"box-sizing: border-box; margin: 0px;\">&nbsp;</p>\n<p style=\"box-sizing: border-box; margin: 0px;\"><span style=\"box-sizing: border-box; font-weight: bold;\">Չէի ուզի շատ կոշտ բնորոշումներ տալ, բայց երբ խոզությունն ու դեգեներատիզմը &laquo;համախմբվում&raquo; են, դրա արդյունքում ողբերգությունները դառնում են գրեթե անխուսափելի։ Հուսամ՝ ոստիկանությունում արդեն տեսել են տեսանյութը և համարժեք արձագանքել են</span>&raquo;։</p>\n<p style=\"box-sizing: border-box; margin: 0px;\"><iframe src=\"https://play.radioaurora.am/embed/sTG8iwSfUDiPXXI\" width=\"700\" height=\"400\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n</div>\n</div>', '', '/upload/Screen%20Shot%202022-05-11%20at%2014-35-29.png', '', '', '', 0, 1, 1424, '2022-05-11 11:36:18', '2022-05-12 10:00:15'),
(98, 1, 'Ալեն Սիմոնյանի մոր մասին ցուցանակ պարզած կնոջը բերման ենթարկեցին', '', '<p><iframe title=\"YouTube video player\" src=\"https://www.youtube.com/embed/Ck2KJmrR5kI\" width=\"720\" height=\"360\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/Screen%20Shot%202022-05-11%20at%2014-38-02.png', '', '', '', 0, 1, 286, '2022-05-11 11:38:40', '2022-05-12 09:21:58'),
(99, 7, '«Թեւավորը» ֆիլմն ընդգրկվել է միջազգային անիմացիոն փառատոնի երիտասարդ հանդիսատեսի մրցույթում', '', '<p>&laquo;Թեւավորը&raquo; կարճամետրաժ անիմացիոն ֆիլմն ընդգրկվել է &laquo;ANIBAR&raquo; միջազգային անիմացիոն փառատոնի երիտասարդ հանդիսատեսի մրցույթում։ Այս մասին հայտնում է Հայաստանի ազգային կինոկենտրոնը։</p>\n<p>Ռեժիսորը Նաիրա Մուրադյանն է։ Սյուժեն հետեւյալն է․ մի անշուք փոքրիկ քաղաքում հրաշք է տեղի ունենում՝ հայտնվում է հսկա մի շուն։ Շունը քայլելով չի գալիս, այլ իջնում է երկնքից իր թափանցիկ&nbsp;թևերի շնորհիվ։ Քաղաքի բնակիչները նրան անուն են տալիս՝ Գոդո։</p>\n<p>Գոդոն փոխում է քաղաքի կյանքը։</p>\n<p>&nbsp;</p>\n<p>Հետևեք NEWS.am STYLE-ին Facebook-ում, Twitter-ում և Instagram-ում</p>', '', '/upload/20220511143757441436.jpg', 'https://style.news.am/arm/news/89523/tevavory-filmn-yndgrkvel-e-mijazgayin-animacion-paratoni-eritasard-handisatesi-mrcuytum.html', 'Շունը փոխում է քաղաքի կյանքը․․․', '', 0, 0, 25, '2022-05-11 11:38:58', '2022-05-12 06:17:30'),
(100, 11, 'Արմավիրի ոստիկանները խուզարկությամբ ապօրինի զենք-զինամթերք են հայտնաբերել', '', '<p>Ոստիկանության Արմավիրի բաժնի ծառայողների միջոցառումներով ուսումնասիրվել է լրատվական կայքերից մեկում հրապարակված հեռախոսազրույցի ձայնագրությունը, ըստ որի` խոսողները քննարկում են ինչ-որ գործողություններ կազմակերպելու, անցկացնելու, նաև՝ դրանց դիմաց վճարված գումարի մասին հարցեր։</p>\n<p>Ոստիկանությունից NEWS.am-ին հայտնում են, որ պարզվել է այդ անձանց ինքնությունը և հարուցված քրեական գործի շրջանակներում իրավապահները խուզարկություն են արել Արմավիրի մարզի Այգեշատ համայնքի բնակչի տանն ու օժանդակ շինություններում։</p>\n<p>Խոհանոցում՝ սառնարանի վրա, հայտնաբերվել է առանց գործարանային համարի &laquo;Մաուզեր&raquo; ատրճանակ, իսկ չհրկիզվող պահարանից հայտնաբերվել են 7,62 մմ տրամաչափի ինքնաձիգի 9 փամփուշտ ու սվին-դանակներ։</p>\n<p>Քրեական գործի նախաքննությունը շարունակվում է։</p>\n<p><iframe src=\"https://www.youtube.com/embed/uEjMwvzQ26U\" width=\"620\" height=\"349\"></iframe></p>', '', '/upload/20220511143958044592.jpg', 'https://news.am/arm/news/701190.html', 'Խոհանոցում՝ սառնարանի վրա, հայտնաբերվել է առանց գործարանային համարի «Մաուզեր» ատրճանակ, իսկ չհրկիզվող պահարանից հայտնաբերվել են 7,62 մմ…', 'На кухне на холодильнике был обнаружен пистолет \"Маузер\" без серийного номера, а из сейфа - 7,62 мм.', 0, 0, 69, '2022-05-11 11:41:05', '2022-05-12 06:14:36'),
(101, 9, 'Օդի ջերմաստիճանը աստիճանաբար կբարձրանա', '', '<p>Երևան քաղաքում՝ մայիսի 11-ի երեկոյան ժամերին, 12-ի և 16-ի ցերեկը սպասվում է անձրև և ամպրոպ:</p>\n<p>Ամպրոպի ժամանակ քամու ուժգնացում՝ 15-18 մ/վրկ:</p>\n<p>Մայիսի 13-15-ին, 16-ի գիշերը սպասվում է առանց տեղումների եղանակ:</p>\n<p>&nbsp;</p>\n<p>Հանրապետության տարածքում` մայիսի 11-ի ցերեկը, 12-ի գիշերը, 13-ին, 15-ի ցերեկը առանձին շրջաններում, 12-ի և 16-ի ցերեկը շրջանների զգալի մասում սպասվում է անձրև և ամպրոպ, առանձին հատվածներում հնարավոր է կարկուտ:</p>\n<p>Մայիսի 14-ին, 15-ի և 16-ի գիշերը սպասվում է առանց տեղումների եղանակ:</p>\n<p>Քամին՝ արևմտյան 2-5 մ/վրկ, ամպրոպի ժամանակ սպասվում է քամու ուժգնացում՝ 15-20մ/վրկ արագությամբ:</p>\n<p>Օդի ջերմաստիճանը մայիսի 12-ի ցերեկը կնվազի 2-4, այնուհետև աստիճանաբար կբարձրանա 4-6, Լոռիում, Տավուշում, Սյունիքում և Արցախում 8-10 աստիճանոով։</p>', '', '/upload/20220511144352673221.jpg', 'https://iravaban.net/382557.html', 'Երևան քաղաքում՝ մայիսի 11-ի երեկոյան ժամերին, 12-ի և 16-ի ցերեկը սպասվում է անձրև և ամպրոպ: Ամպրոպի ժամանակ քամու ուժգնացում՝ 15-18 մ/վրկ: Մայիսի 13-15-ին, 16-ի գիշերը սպասվում է առանց տեղումների եղանակ: Հանրապետության տարածքում` մայիսի 11-ի ցերեկը, 12-ի գիշերը, 13-ին, 15-ի ցերեկը առանձին շրջաններում, 12-ի և 16-ի ցերեկը շրջանների զգալի մասում սպասվում է անձրև և ամպրոպ', '', 0, 0, 73, '2022-05-11 11:45:09', '2022-05-12 06:17:33'),
(102, 6, '«Ալ Ջազիրա»-ի լրագրողին գնդակահարել են', '', '<p>&laquo;Ալ Ջազիրա&raquo; հեռուստաալիքի լրագրող Շիրին Աբու Աքլեն մահացել Է վերքերի հետեւանքով, որն ստացել Է Հորդանան գետի Արեւմտյան ափին անվտանգության իսրայելական ուժերի եւ պաղեստինցիների միջեւ տեղի ունեցած փոխհրաձգության ընթացքում: Այդ մասին, ինչպես տեղեկացնում Է &laquo;Արմենպրես&raquo;-ը, հաղորդում են մերձավորարեւելյան ԶԼՄ-ները:</p>\n<p>Նրանց տեղեկատվության համաձայն, 51-ամյա լրագրողը, որը մի քանի տասնամյակ զբաղվում Էր մերձավորարեւելյան հակամարտության լուսաբանմամբ, գլխից վիրավորվել Է մայիսի 11-ի առավոտյան, նրան տեղափոխել են հիվանդանոց, որտեղ նա մահացել Է:</p>\n<p>Նշվում Է, որ Աբու Ակլեն եղել Է սաղավարտով եւ զրահաբաճկոնով՝ &laquo;Մամուլ&raquo; մակագրությամբ:</p>\n<p>Վիրավորվել Է եւս մեկ լրագրող. նա գտնվում Է հիվանդանոցում, նրա վիճակը գնահատվում Է որպես կայուն: &laquo;Ալ Ջազիրա&raquo; հեռուստաալիքը, վկայակոչելով Պաղեստինի իշխանություններին, հաղորդել Է, որ լրագրողուհուն կրակել են իսրայելական ուժերը՝ Ջենին քաղաքի շրջանում:</p>\n<p>Հեռուստաալիքի ղեկավարությունը եւ նրա գործընկերները ցավակցություն են հայտնել զոհվածի ընտանիքին՝ նշելով նրա &laquo;պրոֆեսիոնալիզմն ու տոկունությունը&raquo;: Իր հերթին Իսրայելի պաշտպանության բանակի մամուլի քարտուղար, բրիգադի գեներալ Ռան Կոհավն իսրայելական &laquo;Կան&raquo; ռադիոկայանին հայտարարել Է, որ չի կարծում, թե իսրայելական ուժերն են գնդակահարել Աբու Ակլեին, հաղորդել Է &laquo;Ինտերֆաքս&raquo;-ը:</p>', '', '/upload/20220511144712519266.jpg', 'https://iravaban.net/382554.html', '«Ալ Ջազիրա» հեռուստաալիքի լրագրող Շիրին Աբու Աքլեն մահացել Է վերքերի հետեւանքով, որն ստացել Է․․․', '', 0, 0, 91, '2022-05-11 11:47:53', '2022-05-12 07:15:47'),
(103, 11, 'Կոնդի եկեղեցում երեկոյան հոգեհանգիստ է․ ՀԺՄ անդամն ընդդիմադիրներին խնդրում է Պռոշյանով քայլերթ չանել', '', '<p>Ժուռնալիստների միության անդամ Հասմիկ Պողոսյանը դիմել է դիմադրության շարժման կազմակերպիչ ուժերի առաջնորդներին՝ խնդրելով Պռոշյան փողոցում երեկոյան քայլերթ չկազմակերպել, քանի որ Կոնդի եկեղեցում լուսահոգի Արմինե Ճաղարյանի հոգեհանգստի արարողությունն է։</p>\n<p>&laquo;Երեկ Դեբեդ գետում տեղի ունեցած ողբերգության հետևանքով կյանքից հեռացավ մեր հմայիչ ու ինքնատիպ հաղորդավարուհիներից մեկը՝ Արմինե Ճաղարյանը։</p>\n<p>Պակասեց միշտ ստեղծագործական գաղափարներով լի, գեղեցիկ էություն ունեցող մարդ, ով նույնպես պայքարում էր մարդկային, գեղեցիկ արժեքները պահպանելու համար։</p>\n<p>Որպես լրագրող և ազգային արժեքների համար միշտ պայքարող հայուհի` ուզում եմ դիմել այս օրերին մեր երկրում տեղի ունեցող դիմադրության շարժման կազմակերպիչ ուժերի առաջնորդներին, հարգելի ընդդիմությանը և ասել հետևյալը։</p>\n<p>Հարգելի գործիչներ, վաղը՝ ժամը 18։00-21։00-ն, Կոնդի եկեղեցում տեղի կունենա հաղորդավարուհի Արմինե Ճաղարյանի հոգեհանգստի արարողությունը։</p>\n<p>Խնդրում ենք, որ այդ ժամանակահատվածում Պռոշյան փողոցում քայլերթ չկազմակերպվի։ Դիմադրության շարժման հիմքում առաջին հերթին պայքար է ազգային արժեքների համար։</p>\n<p>Հարգենք մեր գործընկերուհու ընտանիքի վիշտը, որպեսզի նրան ճանաչողներն ու հարազատները կարողանան վերջին հրաժեշտի արարողությունը պատշաճ կազմակերպել և մարդկանց հոսքը չխոչընդոտվի։</p>\n<p>Դիմադրության շարժումը նաև նման քայլերով պիտի տարբերվի։</p>\n<p>Հուսանք, որ պատշաճ արձագանք կունենա իմ և շատերի այսպիսի գրառումը։ Հանգչես խաղաղությամբ, մեր հմայիչ գործընկերուհի, Արմինե Ճաղարյան։</p>\n<p>Նշանակություն չունի, որ քեզ հետ որևէ տեղ աշխատելու առիթ չեմ ունեցել։ Այս ցավը ևս մերն է&raquo;։</p>', '', '/upload/20220511144850113579.jpg', 'https://iravaban.net/382550.html', 'Ժուռնալիստների միության անդամ Հասմիկ Պողոսյանը դիմել է դիմադրության շարժման կազմակերպիչ ուժերի առաջնորդներին՝ խնդրելով․․․', '', 0, 0, 160, '2022-05-11 11:49:39', '2022-05-12 09:54:39'),
(104, 1, 'Հայհոյանքներ, սադրանք, քաշքշուկ՝ ՔՊ-ական դերասան Գնել Սարգսյանի կատարմամբ (Video)', '', '<p>&nbsp;</p>\n<p><iframe src=\"https://www.youtube.com/embed/I5PkkL8Msdc\" width=\"560\" height=\"315\"></iframe></p>', '', '/upload/20220511145253321916.jpg', 'https://blognews.am/arm/news/794715/hayhoyanqner-sadranq-qashqshuk-qp-akan-derasan-gnel-sargsyani-katarmamb-video.html', 'Տեսանյութ', '', 0, 1, 171, '2022-05-11 11:53:48', '2022-05-12 07:53:54'),
(105, 1, 'Թող մենք լինենք մերժված, բայց ոչ երբեք դավաճան. մերժվածներին նորից ընդունում են, իսկ դավաճաններին՝ աշխարհի ամեն անկյունում սյունյաց ողջույն են հղում', '', '<p>Ուրեմն թուրքի հետ խաղաղության ջատագովները, եկել ասում են, որ ընդդիմադիրները մի օր հայերի դեմ ադրբեջանական ու թուրքական բռնությունները կարդարացնեն։ Սրանք, էն մտքից, որ իրենց հաշված օրեր են մնացել նոպայի մեջ են ընկել, չեն հասկանում ինչ են խոսում։ Տղերք, Ադրբեջանի ու Թուրքիայի համար ձեր իշխանությունից ձեռնտու իշխանություն երբեք չի եղել։ Ձեր ակնոցավոր օլիգարխնա հայտարարել, որ թուրքերի հետ ուզումա եղբայրանա, ձեր փայյլուն մտքերի գիտակ Աղազարյան Հովիկն էր ասում &laquo;Է հա,Ցեղասպանության ճանաչումը մեզ ինչա՞ տալու որ&raquo;, հայի տականք տեսակի ամենավառ դրսևորմամբ ձեր պատգամավոր Մելքոնյան Գագիկն էր ասում &laquo;100 տարի մեր ուղեղի մեջ մտցրել են, թե թուրքերը ձեր թշնամիներն են, հա՜, լա՜վ, հասկացանք, թշնամու հետ չի՞ կարելի բան անել&raquo;։ Ու էսպիսի հազարավոր օրինակներ կարող եմ բերել։ Եթե չգիտեք, ասեմ՝ իմացեք։ Ընդդիմությունը դրսում է, որ ձեր թեթև ձեռքով թուրքը չգա մեր կողքը ապրի։ Ընդդիմությունը դրսում է, որ վաղը դրամի փոխարեն մանաթով առևտուր չանենք։ Ընդդիմությունը դրսում է, որ ՀԱՅ մնանք։ Ընդդիմությունը դրսում է, որ օր առաջ ազատվի ազգային արժեքներից, արժանապատվությունից ու հայի պատվից զուրկ ներքին թուրքերից։ Չփորձեք կեղծ հայրենասերներ ու պահանջատերներ խաղալ, չի՛ անցնելու։ Իսկ ինչ վերաբերում է մեր մերժված լինելուն։ Էդ &laquo;մերժվածները&raquo; ամեն օր դուրս են գալիս հազարավոր մարդկանց մեջ, զրուցում, նկարվում, ի վերջո փողոց փակում))) Էդ &laquo;մերժվածները&raquo; հազարավոր մարդկանց հետ, առանց վախենալու ու կաշկանդվելու հերթ են կանգնում։ Էնպես, որ էն մի ասածս ուժի մեջա դեռ։ Թող մենք լինենք մերժված, բայց ոչ երբեք դավաճան, որովհետև մերժվածներին նորից ընդունում են, հարգում ու պատվում, իսկ դավաճաններին աշխարհի ամեն անկյունում սյունյաց ողջույն են հղում, քանզի երկրիի հանդեպ դավաճանությունը երբեք չի ներվում։ #առանց_թուքրի_Հայաստան</p>', '', '/upload/20220511145518843189.jpg', 'https://blognews.am/arm/news/794789/tox-menq-linenq-merzhvats-bayc-och-erbeq-davatchan-merzhvatsnerin-noric-yndunum-en-isk-davatchannerin-ashkharhi-amen-ankyunum-syunyac-oxjuyn-en-hxum.html', 'Սրանք, էն մտքից, որ իրենց հաշված օրեր են մնացել նոպայի մեջ են ընկել, չեն հասկանում ինչ են խոսում։', '', 0, 0, 124, '2022-05-11 11:55:49', '2022-05-12 08:59:07'),
(106, 2, 'Տեսանյութ։ Վարդան Ղուկասյանը Սիրուշոյի մասին', '', '<p><iframe title=\"YouTube video player\" src=\"https://www.youtube.com/embed/UM6zCGETm_A\" width=\"853\" height=\"480\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/Screen%20Shot%202022-05-11%20at%2014-51-13.png', '', '', '', 0, 1, 81, '2022-05-11 11:58:19', '2022-05-12 09:27:04'),
(107, 5, 'Տեսանյութ «Առանց ջուր կարամ առանց Նիկոլ չեմ կարա...»', '', '<p><iframe style=\"border: none; overflow: hidden;\" src=\"https://www.facebook.com/plugins/video.php?height=290&amp;href=https%3A%2F%2Fwww.facebook.com%2Fpaylak.fahradyan%2Fvideos%2F521559212711559%2F&amp;show_text=false&amp;width=560&amp;t=0\" width=\"560\" height=\"290\" frameborder=\"0\" scrolling=\"no\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/Screen%20Shot%202022-05-11%20at%2015-06-51.png', '', '', '', 0, 1, 147, '2022-05-11 12:07:08', '2022-05-12 08:55:10'),
(108, 5, 'Թուրք մտավորական Ալի Էրթեմը Ծիծեռնակաբերդում . «40 տարի պայքարել եմ Հանուն Հայերի , որպեսզի Թուրքիան ճանաչի Հայոց ցեղաս պшնությունը»', '', '<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\">&laquo;Թուրքիան ոչ միայն հերքում է Հայոց ցեղաս պանության փաստը, այլեւ կե-ղծnւմ է պատմությունը, ինչն ամենամեծ վիրшվnրшնքն է նախ զ-nհերի, ապա զ-nհերի հետնորդների համար: Դա թունավորում է իրականությունը եւ երկու երկրների միջեւ հարաբերություններըե,- Արմե նպրեuի հետ ունեցած զրույցում&nbsp;նման հայտարարություն արեց &laquo;Ցեղաս-պանության հակառակորդների միությանե նախագահ Ալի Էրթեմը, ով ազգությամբ թուրք է, սակայն ներկայում բնակվում է Գերմանիայում:</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\"><img class=\"alignnone wp-image-138762 \" style=\"vertical-align: middle; max-width: 100%; height: auto; border: 0px; box-sizing: border-box; padding: 10px;\" src=\"http://slim.am/wp-content/uploads/2022/05/lecVkla0mWkQeNlcKjmemRm5bgF.jpg\" alt=\"\" width=\"504\" height=\"271\" loading=\"lazy\" /></p>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\">1998-ից գործող այդ միությունն արդեն շուրջ 10 տարուց ի վեր պայքարում է Հայոց ցեղաս-պանության ճանաչման համար` պшհшնջելով Թուրքիայից ի վերջո ընդունել իր կողմից իրականացված մեղuшգnրծnւթյnւնը: Ինչպե՞ս է պատահել, որ ազգությամբ թուրք մտավորականն սկսել է խոսել Հայոց ցեղաս-պանությունից եւ, ի վերջո, զբաղվել դրա ճանաչման խնդրով հարցին ի պատասխան` նա նշեց, որ երկար տարիներ ի վեր զբաղվելով պատմությամբ` ինքն ուսումնասիրել է տարբեր աղբյուրներից ստացված տեղեկությունները Հայոց ցեղաս-պանության մասին, ինչն էլ հիմք է հանդիսացել &laquo;ճշմարտության որոնմանե համար:</p>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\">Թուրքական քաղաքական եւ &yacute;իզիկական ճնշnւմների ներքո նա տեղափոխվել է Գերմանիա, որտեղ եւ ներկայում բնակվում է ու զբաղվում Հայոց ցեղաս-պանության ճանաչման գործով:</p>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\"><img class=\"alignnone wp-image-126973 \" style=\"vertical-align: middle; max-width: 100%; height: auto; border: 0px; box-sizing: border-box; padding: 10px;\" src=\"http://slim.am/wp-content/uploads/2022/01/Untitled-design-7.jpg\" alt=\"\" width=\"512\" height=\"284\" loading=\"lazy\" /></p>\n<div class=\"text-center\">\n<div class=\"ads-el-container mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\">&laquo;Այժմ ես կարողանում եմ ավելի անկախ աղբյուրներից պատմությունն ուսումնասիրել, ցեղաս-պանության մասին բոլոր հրապարակումներին ծանոթանալ եւ սեփական հետազոտություններն իրականացնել այդ փաստացի nճրшգnրծnւթյшն վերաբերյալե,-ասաց նա` տեղեկացնելով նաեւ, որ ինքն արդեն մի շարք այդպիսի աշխատանքների հեղինակ է: Խոսելով Գերմանիայի` Հայոց ցեղաս-պանության վերաբերյալ ունեցած դիրքորոշման մասին` Ա. Էրթեմը նշեց, որ այդ պետության կողմից ընդունված բանաձեւը մի քայլ է իրականության ճանաչման ուղղությամբ, այն դեպքում, երբ Գերմանիան ցեղաս-պանության տարիներին թուրքական կողմին զ-ենքի հիմնական մատակարարներից մեկն է եղել:</p>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\"><img class=\"alignnone wp-image-126974 \" style=\"vertical-align: middle; max-width: 100%; height: auto; border: 0px; box-sizing: border-box; padding: 10px;\" src=\"http://slim.am/wp-content/uploads/2022/01/15620851_403-770x440.jpg\" alt=\"\" width=\"520\" height=\"297\" loading=\"lazy\" /></p>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\">Նրա խոսքով` Գերմանիան պետք է իր հերթին այդ իրադարձությունն իր անունով կոչի եւ համապատասխան վերաբերմունք ցուցաբերի: Այդ երկիրը կարող է նաեւ նպաստել, որպեսզի Թուրքիան եւս ճանաչի ցեղաս-պանությունը, &laquo;որի մասին որեւէ կասկած չկա, եւ որի հիմնական պшտшսխшնшտվությունը կրում է հենց Թուրքիանե: Ա. Էրթեմը վստահ է, որ թուրքական դպրոցներում պատմությունը սխալ է մատուցվում, կեղծված, ինչը պախարակվում է Թուրքիայում գոյություն ունեցող մարդու իրավունքների պաշտպանության մարմինների, ինչպես նաեւ որոշ մտավորականների եւ պատմագետների կողմից, ովքեր դեմ են արտահայտվում պատմության կեղծմանը:</p>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\"><img class=\"alignnone wp-image-126975 \" style=\"vertical-align: middle; max-width: 100%; height: auto; border: 0px; box-sizing: border-box; padding: 10px;\" src=\"http://slim.am/wp-content/uploads/2022/01/43225aba74ba46ba733f83ec5084e73d-770x440.jpg\" alt=\"\" width=\"523\" height=\"299\" loading=\"lazy\" /></p>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\">Թուրք մտավորականը միաժամանակ նաեւ հավատում է, որ հայ-թուրքական սահմանն ի վերջո կբացվի: Նրա դիտարկմամբ` կան արդեն հավատ ներշնչող որոշակի նշաններ: Ա. Էրթեմը երկկողմանի կարեւոր է համարում երկու երկրների միջեւ հարաբերությունների ցանկացած բարելավումը եւ իր հերթին պшհшնջում սահմանների բացում: Սահմանի փակումը, նրա գնահատմամբ, մարդու իրավունքների դեմ կատարված քայլ է: Ա. Էրթեմի դիտարկմամբ` հատկապես Թուրքիայի սահմանամերձ բնակավայրերի բնակիչները ցանկանում են, որպեսզի բացվեն Հայաստանի եւ Թուրքիայի միջեւ սահմանները:</p>\n<p style=\"font-size: 16px; line-height: 25px; box-sizing: border-box; overflow-wrap: break-word; font-family: pragmatica_book, Tahoma, Arial, Helvetica, sans-serif; background-color: #ffffff;\"><img class=\"alignnone wp-image-126976 \" style=\"vertical-align: middle; max-width: 100%; height: auto; border: 0px; box-sizing: border-box; padding: 10px;\" src=\"http://slim.am/wp-content/uploads/2022/01/MurielDenialAli-1024x576-1-770x440.jpg\" alt=\"\" width=\"527\" height=\"301\" loading=\"lazy\" /></p>', '', '/upload/hy.jpg', '', '', '', 0, 0, 70, '2022-05-11 13:24:20', '2022-05-12 08:32:44'),
(109, 2, 'Կլինեմ ձեզ հետ այնքան, մինչև վարչապետի աթոռին նստած վիժվածքը հրաժարական տա․ Շարաֆյանը՝ ցուցարարներին (Տեսանյութ)', '', '<p><iframe title=\"YouTube video player\" src=\"https://www.youtube.com/embed/P1MYPCxYaUQ\" width=\"800\" height=\"560\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/sharafyan.png', '', '', '', 0, 1, 122, '2022-05-11 13:29:08', '2022-05-12 09:28:58');
INSERT INTO `posts` (`id`, `category_id`, `hy_title`, `ru_title`, `hy_content`, `ru_content`, `image`, `source`, `hy_description`, `ru_description`, `is_general`, `has_video`, `viewed`, `created_at`, `updated_at`) VALUES
(110, 1, 'Տարիներ առաջ փող բաժանեցիք,որ իշխանություն պահեք, հիմա փող եք բաժանում,որ ջահելները ձեր համար փողոց փակեն. Անի Սամսոնյան', '', '<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px; color: #3a3a3a; font-family: Lato, sans-serif; font-size: 18px; background-color: #ffffff;\">Ձեզ բավարար չէր, որ սերունդներ փչացրիք՝ քվեի դիմաց մի երկու կոպեկ գցելով, հիմա էլ ուսանողությանն եք կաշառում կոպեկներով, հանում փողոց՝ անունն էլ դնում՝ հանուն հայրենիքի և Արցախի փրկության։ Այս մասին ֆեյսբուքյան իր էջում գրել է &laquo;Լուսավոր Հայաստան&raquo; կուսակցության անդամ, ՀՀ Ազգային ժողովի 7-րդ գումարման պատգամավոր Անի Սամսոնյանը:</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px; color: #3a3a3a; font-family: Lato, sans-serif; font-size: 18px; background-color: #ffffff;\">&laquo;Տարիներ շարունակ փող բաժանեցիք, որ իշխանությունը պահեք, անցած տարի փող բաժանեցիք, որ դառնաք ընդդիմություն, իսկ հիմա փող եք բաժանում, որ ջահելները ձեր համար փողոց փակեն։</p>\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px; color: #3a3a3a; font-family: Lato, sans-serif; font-size: 18px; background-color: #ffffff;\">&nbsp;</p>\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px;\">Այսինքն ձեզ բավարար չէր, որ սերունդներ փչացրիք՝ քվեի դիմաց մի երկու կոպեկ գցելով, հիմա էլ ուսանողությանն եք կաշառում կոպեկներով, հանում փողոց՝ անունն էլ դնում՝ հանուն հայրենիքի և Արցախի փրկության։</p>\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px;\">Ձեզ ոչ միայն չի կարելի իշխանություն տալ, այլ դուք պետք է վերանաք քաղաքական դաշտից՝ որպես ապազգային և անհայրենիք խմբակներ։ Այլ ելք չկա։</p>\n<p style=\"box-sizing: inherit; overflow-wrap: break-word; margin: 0px 0px 10px;\">Մի բան պարզ է՝ հիմա ով ամենաբարձրն է գոռում հայրենիքի մասին, ամենասնանկն է իր հոգու խորքում&raquo;, &mdash; գրել է նա։</p>\n<div class=\"adsb30\" style=\"box-sizing: inherit; margin: 12px 12px 12px 0px;\">\n<div style=\"box-sizing: inherit;\" data-mhvu5ufcsy=\"43f7d97c-02a0-4ae5-b4ad-4032c15593af\">\n<div style=\"box-sizing: inherit;\" data-la-show-block-id-8261b3c5-2644-cd3c-1e31-2a8d625c402e=\"\" data-la-refresh-timeout-15247-1200000=\"\" data-server-rendered=\"true\">\n<div id=\"block-bc6658js1v\" class=\"block-bc6658js1vla-block-wrapper\" style=\"color: initial !important; font-family: initial !important; font-feature-settings: initial !important; font-kerning: initial !important; font-optical-sizing: initial !important; font-palette: initial !important; font-size: initial !important; font-stretch: initial !important; font-style: initial !important; font-synthesis: initial !important; font-variant: initial !important; font-variation-settings: initial !important; font-weight: initial !important; forced-color-adjust: initial !important; text-orientation: initial !important; text-rendering: initial !important; -webkit-font-smoothing: initial !important; -webkit-locale: initial !important; -webkit-text-orientation: initial !important; -webkit-writing-mode: initial !important; writing-mode: initial !important; zoom: initial !important; accent-color: initial !important; place-content: initial !important; place-items: initial !important; place-self: initial !important; alignment-baseline: initial !important; animation: initial !important; app-region: initial !important; appearance: initial !important; aspect-ratio: initial !important; backdrop-filter: initial !important; backface-visibility: initial !important; background-image: initial !important; background-position: initial !important; background-size: initial !important; background-repeat: initial !important; background-attachment: initial !important; background-origin: initial !important; background-clip: initial !important; background-blend-mode: initial !important;