INSERT INTO `posts` (`id`, `category_id`, `hy_title`, `ru_title`, `hy_content`, `ru_content`, `image`, `source`, `hy_description`, `ru_description`, `is_general`, `has_video`, `viewed`, `created_at`, `updated_at`) VALUES
(111, 1, 'ՏԵՍԱՆՅՈՒԹ. Ցուցարարները փորձում են մտնել Երևանի քաղաքապետարան. իրավիճակը լարվեց', '', '<p>Երևանում մեկնարկած &laquo;Դիմադրություն&raquo; շարժման երթը հասավ Երևանի քաղաքաօետարանի մոտ, որտեղ իրավիճակը լարվեց: Ցուցարարները փորձեցին մտնել քաղաքապետարանի շենք:</p>\n<p>Նշենք, որ աշխատանքային ժամն ավարտվել է և քաղաքապետարանի աշխատակիցները չեն կարողանում դուրս գալ շենքից:</p>\n<p>Մանրամասները՝ տեսանյութում</p>\n<p><iframe src=\"https://www.youtube.com/embed/DOTr9l2qx0s\" width=\"640\" height=\"360px\"></iframe></p>', '', '/upload/20220511184839838405.jpg', 'https://armlur.am/1185456/', 'Լարված իրավիճակ', '', 0, 1, 217, '2022-05-11 15:48:56', '2022-05-12 09:55:19'),
(112, 1, '«Դավաճան», «Նիկոլ Վասակ». մի խումբ հոլանդահայեի բողոքի ակցիան՝ Նիկոլ Փաշինյանի հրաժարականի պահանջով', '', '<p style=\"box-sizing: border-box; font-size: 14px; line-height: 20px; color: #222222; font-family: notosans, notosansarm;\">Մի խումբ հայեր Նիդերլանդների Հաագա քաղաքում բողոքի ակցիա են իրականացնում վարչապետ Նիկոլ Փաշինյանի հրաժարականի պահանջով: Նշենք, որ Նիկոլ Փաշինյանը երկօրյա պաշտոնական այցով գտնվում է Նիդերլանդներում:</p>\n<p style=\"box-sizing: border-box; font-size: 14px; line-height: 20px; color: #222222; font-family: notosans, notosansarm;\">Ցուցարարների ձեռքին կան Արցախի ու Հայաստանի դրոշներ, նաեւ պաստառներ՝ &laquo;ապիկարներ, հեռացեք&raquo;, &laquo;Առանց Նիկոլ Հայաստան&raquo;, &laquo;Զարթնիր լաո&raquo;, &laquo;Արցախ հանձնողը Սյունիքն էլ կհանձնի&raquo;, &laquo;Նիկոլ Վասակ&raquo;, &laquo;Կապիտուլյանտ հեռացիր&raquo;. &laquo;Միջանցք = ինքնիշխանության կորուստ&raquo;, &laquo;Հայրենիք չունենք վաճառելու&raquo;, &laquo;Ազգայինը այլընտրանք չունի&raquo;, &laquo;հավերժ անեծք դավաճանին&raquo;:</p>\n<p style=\"box-sizing: border-box; font-size: 14px; line-height: 20px; color: #222222; font-family: notosans, notosansarm;\">Ակցիայի մասնակիցները վանկարկում են՝ &laquo;թուրքեր&raquo;, &laquo;թուրքի լամուկ&raquo;, &laquo;Նիկոլ դավաճան&raquo;, &laquo;Նիկոլ հողատու&raquo;:</p>\n<p style=\"box-sizing: border-box; font-size: 14px; line-height: 20px; color: #222222; font-family: notosans, notosansarm;\">Հնչում են հայրենասիրական երգեր:</p>\n<p style=\"box-sizing: border-box; font-size: 14px; line-height: 20px; color: #222222; font-family: notosans, notosansarm;\">&nbsp;</p>\n<div style=\"box-sizing: border-box; color: #222222; font-family: notosans, notosansarm; font-size: 10px;\" data-oembed-url=\"https://youtu.be/WKQ1ANJduT0\">\n<div style=\"box-sizing: border-box; left: 0px; width: 880px; height: 0px; position: relative; padding-bottom: 495px;\"><iframe style=\"box-sizing: border-box; top: 0px; left: 0px; width: 880px; height: 495px; position: absolute; border-width: 0px; border-style: initial;\" tabindex=\"-1\" src=\"https://www.youtube.com/embed/WKQ1ANJduT0?rel=0\" scrolling=\"no\" allowfullscreen=\"allowfullscreen\"></iframe></div>\n</div>', '', '/upload/2022-05-11-2-880x_-resize.png', '', '', '', 0, 1, 107, '2022-05-11 15:52:06', '2022-05-12 07:47:44'),
(113, 2, 'ՖՈՏՈ. Ուշադրությունից նեղվում եմ, դա խանգարում է իմ բնականությանը. Լիլի Էլբակյանը՝ 36-ամյակի և ամենամեծ երազանքի մասին', '', '<div><img src=\"https://armlur.am/wp-content/uploads/2022/05/272709874_1006249066637249_254919065524105228_n-480x600.jpg\" /></div>\n<div><img src=\"https://armlur.am/wp-content/uploads/2022/05/278734621_1053626485232840_6239001847570025508_n-600x345.jpg\" /></div>\n<div><img src=\"https://armlur.am/wp-content/uploads/2022/05/278382753_1055146941747461_8918151181354626023_n-600x399.jpg\" /></div>\n<div><img src=\"https://armlur.am/wp-content/uploads/2022/05/264072673_977413969520759_8987796038619955860_n-600x400.jpg\" /></div>\n<p>Այսօր դերասանուհի Լիլի Էլբակյանը նշում է ծննդյան 36-րդ ամյակը: Դերասանուհին ArmLur.am-ի հետ զրույցում պատմել է օրվա ծրագրերի եւ տարեդարձի օրը աշխատանքային անցկացնելու մասին:</p>\n<p>&nbsp;</p>\n<p>&laquo;Վաղ առավոտյան ծնողներս տորթով եւ փուչիկներով շնորհավորեցին, որից հետո եկել եմ աշխատանքի: Այս օրերին շատ զբաղված ենք, քանի որ շուտով նոր պրեմիերա ենք ունենալու: Այսօր պլանավորել եմ անցկացնել աշխատանքային միջավայում, եւ սիրում եմ ծննդյանս օրը, երբ համընկնում է աշխատանքային խիստ գրաֆիկիս հետ եւ կարող եմ բոլորին ասել, որ աշխատանքի եմ&raquo;,-կատակում է դերասանուհին:</p>\n<p>&nbsp;</p>\n<p>Անդրադառալով նվերներին եւ անակնկալներին՝ Լիլին մեզ հետ զրույցում ասաց. &laquo;Առհասարակ ես ոչ ծննդյանս օրն եմ սիրում, ոչ էլ անակնկալներ, այդ իսկ պատճառով շատ դժվար է ինձ համար նվեր գնելը կամ անակնկալ մատուցելը: Իսկ, երեխաների կողմից նվեր դեռ չեմ ստացել, գիտեմ, որ պատրաստվում են, քանի որ նրանք նկարչության են հաճախում, նկարներ են պատրաստել ինձ համար&raquo;:</p>\n<p>Դերասանուհին մեզ հետ զրույցում անկեղծանալով ընդգծում է, որ սիրում է տարեդարձն անցկացնել ինքն իր հետ, սիրում է միայնակ քայլել եւ մտածել իր աշխարհի մասին. &laquo;Դա իմ ամենասիրած պահն է եւ այսօր հենց այդպես եմ անցկացնելու օրս&raquo;:</p>\n<p>&nbsp;</p>\n<p>Լիլի Էլբակյանի խոսքով՝ տարիքն ամենեւին նրան չի անհանգստացնում, քանի որ այդ իմաստով նա ինքն իր հետ նեդաշնակ է:</p>\n<p>&laquo;Ես իմ տարիքը չեմ զգում, ինքս ինձ հետ այս իմաստով ներդաշնակ եմ: Կարծում եմ ամեն տարիքի հետ մեծանալով եւս մեկ տարով իմաստանում եմ, ուղղակի չեմ սիրում հատուկ ծնունդս նշել, որովհետեւ շատ ուշադրություն իմ անձի նկատմամբ երբեք չեմ սիրել, դեռ մանկուց: Իրականում ես շատ եմ ուշադրությունից նեղվում, դա խանգարում է իմ բնականությանը&raquo;,-նշեց նա:</p>\n<p>&nbsp;</p>\n<p>Մեր զրուցակցի խոսքով՝ իր երազանքների մեծ մասր իրականացել են, դրանք ավելի շատ կապված են մասնագիտական աճի, նոր ֆիլմերում նկարահանվելու եւ հայրենիքի հետ.&laquo; Ուզում եմ մեր երկրում խաղաղություն լինի, եւ այս խառը վիճակը, որն առկա է մեր երկրում, կարգավորվի. ինձ համար դա ամենամեծ երազանքն է&raquo;:</p>\n<p>Լիդա Եղիազարյան</p>', '', '/upload/20220511185803618564.jpg', 'https://armlur.am/1185367/', 'Որն է ամենամեծ երազանքը', '', 0, 0, 42, '2022-05-11 15:58:14', '2022-05-12 06:17:33'),
(114, 6, 'Ադրբեջանը տպավորություն է ստեղծում, թե սեղանին լինելու են միայն իր առաջարկները. վարչապետ', '', '<p>Միջազգային հարաբերութունների նիդերլանդական ինստիտուտում &laquo;Հայաստանը և Նիդերլանդները. դարավոր բարեկամություն- 30-ամյա գործընկերություն&raquo; թեմայով դասախոսության ժամանակ ՀՀ վարչապետ Նիկոլ Փաշինյանը ներկայացրել է տարածաշրջանային անվտանգության ապահովման ուղղությամբ իրականացվող քայլերը: Մասնավորապես, նա մանրամասներ է ներկայացրել Ադրբեջանի հետ հարաբերությունների կարգավորման գործընթացի վերաբերյալ՝ ընդգծելով Լեռնային Ղարաբաղի իրավունքներն ու վերջին շրջանում ունեցած դժվարությունները:</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<p>&laquo;Պետք է հաջողությամբ իրականացնել տարածաշրջանային կոմունիկացիաների բացումը, որպեսզի Ադրբեջանի հետ մեր երկաթուղիները, ճանապարհները աշխատեն: Մենք խոսակցություն ենք սկսել Թուրքիայի հետ, համաձայնել ենք առանց նախապայմանների սկսել հարաբերությունների կարգավորման փորձ: Առանց միջազգային հանրության ակտիվ ու անկեղծ աջակցության մենք հաջողության հասնելու ավելի քիչ հնարավորություններ կունենանք: Մենք ուզում ենք, որ միջազգային հանրությունը լսի մեր ձայնը, միջազգային հանրությունը տեսնի մարդկանց, որոնք այսօր ապրում են Լեռնային Ղարաբաղում, որոնք ամեն օր՝ ի հեճուկս բազմաթիվ գործոնների, պայքարում են իրենց հայրենիքում ապրելու իրավունքի համար: Մյուս կողմից ունենք տարօրինակ մի փաստ, որ այդ մարդիկ կտրված են միջազգային հանրության հետ հաղորդակցություն իրականացնելու հնարավորությունից: Այսօր բազմաթիվ միջազգային կառույցներ, գործակալություններ՝ ՄԱԿ, ՅՈՒՆԵՍԿՕ, ԵԱՀԿ, զրկված են Լեռնային Ղարաբաղ մուտքի հնարավորությունից, որովհետև Ադրբեջանը արգելափակում է: Եթե չկա իրականությունն այլակերպելու ցանկություն և կանխավարկած, ապա այդ քաղաքականությունն այնքան էլ հասկանալի չէ&raquo;,- &laquo;Արմենպրես&raquo;-ի հաղորդմամբ՝ ասաց Նիկոլ Փաշինյանը:</p>\n<div class=\"middle-news mceNotEditable\" data-url=\"/loader/single-related-post?category=1&amp;post_type=popular\">\n<p>Կատեգորիա - 1</p>\n<p>Փոստի տեսակը - popular</p>\n</div>\n<p>ՀՀ վարչապետը տեղեկացրեց, որ Հայաստանը որդեգրել է խաղաղության օրակարգը, գնում է Ադրբեջանի հետ սահմանների դեմարկացիայի և դելիմիտացիայի ճանապարհով: Նա պարզաբանում ներկայացրեց, պայմանականորեն ասած, խաղաղության բանակցությունների մեկնարկի վերաբերյալ:</p>\n<p>&laquo;Ադրբեջանը ներկայացրեց իր պատկերացումները Հայաստան-Ադրբեջան հարաբերությունների կարգավորման մասին, որտեղ հինգ հիմնական կետ կա: Խոսքը վերաբերում է միմյանց տարածքային ամբողջականությունը փոխադարձաբար ճանաչելուն, սահմանների դեմարկացիային, դելիմիտացիային, միմյանցից տարածքային պահանջների բացակայությանը: Մենք, դիտարկելով այդ առաջարկները, տեսանք, որ մեզ համար, ըստ էության, դրանցում անընդունելի ոչինչ չկա, սակայն արձանագրեցինք, որ այդ կետերը չեն հասցեագրում հարաբերությունների ամբողջ օրակարգը: Լրացրեցինք այդ օրակարգը, որտեղ ներառվում է Լեռնային Ղարաբաղի հայության անվտանգության, իրավունքների և Լեռնային Ղարաբաղի վերջնական կարգավիճակի խնդիրը: Ապրիլի 6-ին ԵՄ խորհրդի նախագահ Շառլ Միշելի միջնորդությամբ, երբ հանդիպում տեղի ունեցավ, մենք քննարկեցինք այդ հարցը Ադրբեջանի նախագահի հետ: Ասացի, որ մեզ համար իրենց առաջարկած կետերի մեջ ոչինչ անընդունելի չկա: Եթե ադրբեջանական կողմը համաձայն է երկու պատկերացումները միացնել իրար ու այդ հենքի վրա սկսել բանակցություններ, ապա մենք պատրաստ ենք դրան: Թեպետ, պետք է նշեմ, որ վերջին շրջանում Ադրբեջանից հնչում են հայտարարություններ, որով փորձ է արվում տպավորություն ստեղծել, թե սեղանին լինելու են միայն Ադրբեջանի առաջարկները: Դա առնվազն չի համապատասխանում այն պայմանավորվածություններին, որոնք ձեռք ենք բերել Բրյուսելում: Այսօր պիտի վերահաստատեմ իմ այն դիրքորոշումը, որ նշված սկզբունքների հիման վրա պատրաստ ենք բանակցությունների, չնայած, այն պրոցեսում, որը պլանավորվել է, որևէ բան չի փոխվել: Առաջիկայում նախատեսվում է մեր ներկայացուցիչների հանդիպումը սահմանազատման, սահմանագծման հարցում, տեղի կունենա արտաքին գործերի նախարարների հանդիպումը: Մենք շատ դաժան փորձությունների միջով ենք անցել, բայց այսօր կարող եմ ասել, որ ունեմ խորը համոզմունք, որ մենք ճիշտ ճանապարհով ենք ընթանում&raquo;,-հավելեց Նիկոլ Փաշինյանը:</p>', '', '/upload/20220511185957888876.jpg', 'https://iravaban.net/382610.html', 'Միջազգային հարաբերութունների նիդերլանդական ինստիտուտում «Հայաստանը և Նիդերլանդները. դարավոր բարեկամություն- 30-ամյա գործընկերություն» թեմայով դասախոսության ժամանակ ՀՀ վարչապետ Նիկոլ Փաշինյանը ներկայացրել է տարածաշրջանային անվտանգության ապահովման ուղղությամբ իրականացվող քայլերը: Մասնավորապես, նա մանրամասներ է ներկայացրել Ադրբեջանի հետ հարաբերությունների կարգավորման գործընթացի վերաբերյալ՝ ընդգծելով Լեռնային Ղարաբաղի իրավունքներն ու վերջին շրջանում ունեցած դժվարությունները: «Պետք է հաջողությամբ իրականացնել տարածաշրջանային կոմունիկացիաների բացումը, որպեսզի Ադրբեջանի...  Read more &raquo;', '', 0, 0, 61, '2022-05-11 16:00:28', '2022-05-12 09:51:13'),
(115, 6, 'Քոչարյանը պետք է ընդմիշտ վերանա. Հոլանդահայերը ուշադիր հետևում են Հայաստանի իրադարձություններին', '', '<p style=\"-webkit-text-size-adjust: auto; box-sizing: border-box; overflow-wrap: break-word;\">Հայաստանը լեգիտիմ ղեկավար ունի, մարդիկ իրեն 2 անգամ ընտրել են, հիմա ի՞նչ է ուզում ընդդիմությունը, ամեն ամիս պետք է նորից ընտրություն անե՞ն: Այս մասին &laquo;Առաջին լրատվական&raquo;-ի հետ զրույցում նշեցին Հոլանդիայի հայերը:</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container d-inline-block mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<div class=\"clearfix\" style=\"-webkit-text-size-adjust: auto; box-sizing: border-box;\">\n<div id=\"adriver_banner_118919434\" class=\"adriverBanner\" style=\"box-sizing: border-box;\"></div>\n</div>\n<p style=\"-webkit-text-size-adjust: auto; box-sizing: border-box; overflow-wrap: break-word;\">Դիտարկմանը, որ ընդդիմությունը հայտարարում է, որ գալիս են Արցախը փրկելու, հոլանդահայերը նշեցին. &laquo;Թող փրկեին այն ժամանակ, երբ պատերազմ էր: Էլի թող փրկեն, բայց փրկելու համար պետք է Արցախում լինեն: 30 տարի չփրկեցին, հիմա հանկարծ հիշեցին, որ Արցախ ունե՞ն: Փրկողները պետք է Արցախում լինեին, որ կարողանային փրկել: Իսկ իրենք ի՞նչ արեցին, եկան Արցախն էլ քանդեցին, Հայաստանն էլ քանդեցին 30 տարի: Ոչ մի Արցախի փրկություն էլ չկա, իրենք իրենց ունեցվածքը փրկողներն են: Այսինքն պետք է ոչ թե երգեն &laquo;Զարթիր լաո&raquo;, այլ &laquo;Քելե լաո, քելե երթանք մըր էրգիր&raquo;: Թող գնան փրկեն, ամեն ինչ ունեն, միլիոններ ունեն, թող տանեն շենացնեն Արցախը, ապրեն էնտեղ, 1-ի փոխարեն 10 երեխա ունենան: Երևանի կենտրոնում ի՞նչն են փրկում: Բոլորս էլ ամեն ինչ լավ գիտենք, հատկապես դրսի հայությունը: Մենք ապրել ենք իրենց ժամանակ, ինչի՞ ենք այստեղ, ինչի՞ ենք եկել, իրենց պատճառով&raquo;:</p>\n<div class=\"middle-news mceNotEditable\" data-url=\"/loader/single-related-post?category=2&amp;post_type=popular\">\n<p>Կատեգորիա - 2</p>\n<p>Փոստի տեսակը - popular</p>\n</div>\n<p><span style=\"caret-color: #333333; color: #333333; font-family: Mardoto; font-size: 14px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); -webkit-text-size-adjust: 100%; background-color: #ffffff;\">Հոլանդահայերը միակարծիք էին, որ ընդդիմության նպատակն ն իշխանությունը զավթելն է. &laquo;Հիմա էլ ուրիշ լոզունգ են առաջ քաշում, թե իբր աթոռ չենք ուզում, ուզում ենք Արցախը փրկենք: Արցախը տվիք, պրծաք, պատերազմի ժամանակ փրկեիք, երբ դավաճանում էիք, թողնում փախչում էիք: 30 տարի Արցախը փրկեիք: Հիմա եկել նորից աթոռ եք ուզում, նորից ձեր թալանն եք ուզում&raquo;, ասաց 30 տարի առաջ Հայաստանից Հոլանդիա տեղափոխված մի կին: Հայաստանյան իրադարձություններին ուշի ուշով հետևող հոլանդահայերին վրդովվեցրել է նաև Գյումրիում ցուցարարների կողմից տարեց մարդկանց ծեծի ենթարկելու փաստը. &laquo;Ո՞նց ծեծեցին էն պապիկին: Իրենցը ծեծ ու ջարդն է, ծեծեն, սպանեն: Թուրքերը պատերազմի ժամանակ մի պապիկի էին ծեծել, հիմա նույնն լ իրենք են արել, ո՞նց կարելի է մի քանի հոգով հարձակվել ու նման բան անել: Եվ դրանք պիտի գան իշխանությա՞ն: Եթե իրենցից որևէ մեկը Հայաստանում եկավ իշխանության, Հայաստանը կկործանվի: Դրսի հայերից ոչ ոք նրանց չի աջակցում: Ես կարծում եմ, որ Քոչարյանը ընդմիշտ պետք է վերանա՝ իր ամբողջ ազգուտակով&raquo;:</span></p>\n<p>&nbsp;</p>\n<p><iframe src=\"https://www.youtube.com/embed/1qRO8vkuVxw\" width=\"560\" height=\"314\" allowfullscreen=\"allowfullscreen\"></iframe></p>\n<div id=\"ms_483423354264\" class=\"\" style=\"caret-color: #000000; color: #000000; font-style: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-tap-highlight-color: rgba(26, 26, 26, 0.3); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-decoration: none; box-sizing: border-box;\"></div>', '', '/upload/BCABB818-ECBA-4CBD-9229-5A2F28B9B1FB.jpg', '', '', '', 0, 1, 84, '2022-05-11 17:05:23', '2022-05-12 08:35:21'),
(116, 11, 'Ավանի թաղապետի նախկին տեղակալ Ներսես Պողոսյանը մեղադրվում է ռազմամթերքի շրջանառության մեջ. գործը դատարանում է', '', '<p>Ավանի թաղապետի նախկին տեղակալ Ներսես Պողոսյանի վերաբերյալ քրեական գործը դատարանում է։ Այս մասին NEWS.am-ը տեղեկանում է Դատական-տեղեկատվական համակարգից։</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container d-inline-block mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<p>Ըստ մեղադրանքի՝ Ներսես Պողոսյանը չունենալով &laquo;Զենքի մասին&raquo; &laquo; օրենքի 13-րդ հոդվածով սահմանված՝ ռազմամթերքի շրջանառություն իրականացնելու՝ այն ձեռք բերելու եւ պահելու իրավունք, ինչպես նաեւ ռազմամթերքի շրջանառություն իրականացնելու՝ ОС ոստիկանությունում օրենքով սահմանված կարգով ձեւակերպված համապատասխան թույլտվություն, քննությամբ չպարզված հանգամանքներում ապօրինի կերպով ձեռք է բերել, փոխադրել եւ պահել ռազմամթերք հանդիսացող թվով 4 հատ &laquo;ТТ&raquo; տեսակի ատրճանակներից եւ համապատասխան տրամաչափի ակոսափող այլ զենքերից կրակվելու համար նախատեսված 7.62մմ/7.62ճ25/ տրամաչափի փամփուշտներ, որոնք հայտնաբերվել են 2022 թվականի մարտի 4-ին վերջինիս Երեւան քաղաքի Խուդյակով 136/1 տան խուզարկության ընթացքում:</p>\n<div class=\"middle-news mceNotEditable\" data-url=\"/loader/single-related-post?category=5&amp;post_type=popular\">\n<p>Կատեգորիա - 5</p>\n<p>Փոստի տեսակը - popular</p>\n</div>\n<p>Նա մեղադրվում է ՀՀ ՔՕ 235-րդ հոդվածի 1-ին մասի (ապօրինի կերպով զենք, ռազմամթերք, պայթուցիկ նյութեր կամ պայթուցիկ սարքեր ձեռք բերելը, իրացնելը, պահելը, փոխադրելը կամ կրելը) հատկանիշներով։</p>\n<p>Ներսես Պողոսյանի նկատմամբ որպես խափանման միջոց է ընտրվել չհեռանալու մասին ստորագրությունը։</p>\n<p>Գործը մակագրվել է Երեւանի ընդհանուր իրավասության դատավոր Մանվել Շահվերդյանին։</p>', '', '/upload/20220511201605141325.jpg', 'https://news.am/arm/news/701284.html', 'Ներսես Պողոսյանը չունենալով «Զենքի մասին» « օրենքի 13-րդ հոդվածով սահմանված՝ ռազմամթերքի շրջանառություն իրականացնելու…', '', 0, 0, 57, '2022-05-11 17:16:36', '2022-05-12 06:14:12'),
(117, 5, '«Հայրը գիշերը խոսել էր որդու հետ, բարձր տրամադրությամբ է եղել». մահացած հայտնաբերված զինծառայողը Շաքի գյուղից էր (ֆոտո)', '', '<div><img src=\"https://news.am/uploads/images/000000/33/75572109_1002671806742050_7783937704927952896_n.jpg\" /></div>\n<div><img src=\"https://news.am/uploads/images/000000/33/81064774_1044021005940463_3142515712354942976_n.jpg\" /></div>\n<div><img src=\"https://news.am/uploads/images/000000/33/92925830_1124577587884804_4925379576344870912_n.jpg\" /></div>\n<p>ՀՀ ՊՆ N զորամասերից մեկում այսօր՝ մայիսի 11-ին, կրծքավանդակի շրջանում մահացու հրազենային վիրավորում ստացած Արգիշտի Եղյանը Սյունիքի մարզի Շաքի համայնքից էր։ 20&ndash;ամյա Արգիշտին բանակ էր զորակոչվել մեկ տարի չորս ամիս առաջ։</p>\n<p>NEWS.am-ի հետ զրույցում Շաքի համայնքի վարչական ղեկավար Ռուբեն Մանուչարյանն ասաց, որ առավոտյան են լսել Արգիշտիի մահվան լուրը։</p>\n<p>&laquo;Հայրը երեկ ուշ երեկոյան բավականին երկար խոսել է Արգիշտիի հետ։ Ամեն ինչ լավ էր տղայի հետ, բարձր տրամադրություն է ունեցել, ուրախ, բայց թե ինչու է նման բան եղել՝ չգիտենք, հուսով ենք՝ համապատասխան մարմինները կպարզեն։ Մի քանի օր առաջ էլ ես եմ հնարավորություն ունեցել տեսակապով զրուցել նրա հետ, բարձր տրամադրություն ուներ, որեւէ անհանգստություն չուներ&raquo;,&ndash; պատմեց գյուղի վարչական ղեկավարը։</p>\n<p>Արգիշտի Եղյանը սովորում էր ԵՊՀ&ndash;ի Եվրոպական լեզուների եւ հաղորդակցության ֆակուլտետի &laquo;Անգլերեն, իսպաներեն եւ հաղորդակցության&raquo; բաժնի 1&ndash;ին կուրսում։</p>\n<p>&laquo;Իր բացառիկ ընդունակությունների համար, երբ դեռ&nbsp; աշակերտ էր, ԱՄՆ&ndash;ին են տարել, հետո կորոնավիրուսի պատճառով հետ էր եկել։ Արգիշտին հավասարակշռված, գրագետ, զարգացած, խելոք տղա էր&raquo;,&ndash; պատմեց Ռուբեն Մանուչարյանը։</p>\n<p>Արգիշտին երկու ավագ քույր ունի։</p>\n<p>Ավելի վաղ հաղորդել էինք, որ մայիսի 11-ին, ժամը 10:15-ին, Հանրապետության արեւելյան ուղղությամբ տեղակայված N զորամասի մարտական հենակետերից մեկում հայտնաբերվել է շարքային Արգիշտի Արմենի Եղյանի (ծնվ. 2002 թ.) դին՝ կրծքավանդակի շրջանում հրազենային վնասվածքով։</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', '', '/upload/20220511205052301174.jpg', 'https://news.am/arm/news/701274.html', 'Արգիշտին հավասարակշռված, գրագետ, զարգացած, խելոք տղա էր...', '', 0, 0, 61, '2022-05-11 17:51:17', '2022-05-12 06:14:10'),
(118, 5, 'Մայրը սրտի խնդիրներ ունի, բոթը լսելիս ուշագնաց եղավ. մանրամասներ՝ այսօր դիրքում մահացած հայտնաբերված զինծառայող Արգիշտի Եղյանից', '', '<div><img src=\"https://lurer.com/disc/11-05-22/0022e5a083fc7351ca11d3c139a488b3.jpg\" /></div>\n<div><img src=\"https://lurer.com/disc/11-05-22/e0b80bdb8fba9ce023da8f7806a83eec.jpg\" /></div>\n<div><img src=\"https://lurer.com/disc/11-05-22/5694b1a68756fd5af105c4b1605cd78c.jpg\" /></div>\n<div><img src=\"https://lurer.com/disc/11-05-22/baed1d895e71ae4384eb9ad3c52a9096.jpg\" /></div>\n<div><img src=\"https://lurer.com/disc/11-05-22/2fc6c6adad1888fd8588d4ed26f91d42.jpg\" /></div>\n<div><img src=\"https://lurer.com/disc/11-05-22/a9b8f84a900a7e57fcba1c85b1871bb0.jpg\" /></div>\n<p>Lurer.com-ը գրում է. ՊՆ-ն այսօր հայտարարություն&nbsp;տարածեց&nbsp;այն մասին, որ ժամը 10:15-ին, հանրապետության արևելյան ուղղությամբ տեղակայված N զորամասի մարտական հենակետերից մեկում հայտնաբերվել է շարքային Արգիշտի Արմենի Եղյանի (ծնվ. 2002 թ.) դին՝ կրծքավանդակի շրջանում հրազենային վիրավորումով։ Արգիշտի Եղյանին ճանաչելու նպատակով&nbsp;ԼՈՒՐԵՐ.com-ը կապ հաստատեց Սիսիանի Շաքե գյուղի համայնքի ղեկավար Ռուբեն Մանուչարյանի հետ, որը, ինչպես հետո պարզ դարձավ, մահացած զինծառայողի ընտանիքի բարեկամն էր. Նա նշեց, որ ընտանիքում 3 երեխա էին՝ երկու քույր և մեկ որդի, զինծառայողի հայրը մեխանիզատոր է, մայրը՝ տնային տնտեսուհի, պարկեշտ ընտանիք են: Ընտանիքի բարեկամը նրան ներկայացրեց մեկ դարձվածքով՝&nbsp;&laquo;Աստծո անմեղ գառ&raquo;: &laquo;Նա շատ խաղաղ էր, համեստ և ամաչկոտ, դաստիարակված, միմիայն դրական հատկանիշներով էր օժտված, անգամ այդ տարիքում, երբ մեկի հետ զրուցում էր ամաչում էր, այսքանից հետո նրա կռվարար լինելու մասին խոսելն ավելորդ եմ համարում: Արգիշտին սովորում էր պետհամալսարանում, անգլերեն և իսպաներեն լեզուների մասնագետ էր: Ինքը գերզարգացած երիտասարդ էր, իր բացառիկ ընդունակությունների շնորհիվ համալսարանը նրան մեկ տարով ուղարկեց ԱՄՆ, սակայն կորոնավիրուսի հետևանքով նա շուտ վերադարձավ և զորակոչվեց բանակ&raquo;,- նշեց համայնքի ղեկավարը: Վերջինս ասաց, որ Արգիշտին ծառայում էր Վարդենիսի զորամասերից մեկում, և որ շուտով կլրանար նրա ծառայության մեկ ու կես տարին: Ինչպես նշեց Ռուբեն Մանուչարյանը, իմանալով մահացած զինվորի մոր սրտի խնդրի մասին, ընտանիքին շատ զգույշ են հայտնել բոթը. &laquo;Մենք նախքան այդ լուրը հայտնելը, բոլոր պայմանները ստեղծեցինք, շտապօգնության մեքենա կանչեցինք, որովհետև մայրը սրտի խնդիր ունի: Երբ մայրը դուրս եկավ տանից, մենք այդ բոթը հայտնեցինք և ինքը վատացավ՝ ուշագնաց եղավ, բժիշկն ու բուժքույրը անմիջապես համապատասխան օգնություն ցուցաբերեցին նրան:&nbsp;Երբ այդ բոթը Արգիշտիի հորը հայտնեցինք, նա լացեց և ասաց, որ մինչև գիշերը ժամը 3:00-ը խոսել է որդու հետ և որևէ արտառոց բան չի նկատել, ինչպես միշտ այդ ժամանակ էլ Արգիշտին ուներ շատ բարձր տրամադրություն&raquo;,- ցավով ընդգծեց նա: Մեր զրուցակիցը պատմեց, որ Արգիշտիի հետ վերջին անգամ խոսել է մայիսի 9-ին՝ ինչ-որ առիթի ժամանակ և նա բարձր տրամադրությամբ, ժպիտը դեմքին, տեսազանգով խոսել է բոլոր ներկաների հետ և ասել՝ ամեն ինչ շատ լավ է իր մոտ:&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>', '', '/upload/20220511205309360584.jpg', 'https://www.blognews.am/arm/news/794861', 'ՊՆ-ն այսօր հայտարարություն տարածեց այն մասին, որ ժամը 10:15-ին, հանրապետության արևելյան ուղղությամբ տեղակայված ...', '', 0, 0, 1872, '2022-05-11 17:53:47', '2022-05-12 10:02:54'),
(119, 1, '«Հայրենասեր» Գռզոն վստահեցրել է, թե իրենք ոչ մի վատ բան չեն արել, այլ ընդամենը տնտեսություն են զարգացրել', '', '<p>&laquo;Հայրենասեր&raquo; ԳռզոնԳռզոն հայտարարել է, թե բնիկ երևանցի լինելով՝ քաջատեղյակ է՝ 90-ականներից ով ինչով է զբաղվել: ՔՊ-ական օլիգարխը վստահեցրել է, թե իրենք ոչ մի վատ բան չեն արել, այլ ընդամենը տնտեսություն են զարգացրել ընտանյոք հանդերձ՝ առանց հորինելու ու աչպարարության՝ միաժամանակ հպարտանալով հարկեր վճարելով:Գռզոյի փողերը մեկը հաշվե՞լ է:</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<p>Շատերն, անկասկած, փորձած կլինեն անել դա, սակայն դժվար թե որևէ մեկին հաջողված լինի կոնկրետ պատկերացում կազմել դրա մասին. անթիվ միլիոնների տեր միլիարդատիրոջ ունեցվածքի չափը որոշելը անշնորհակալ գործ է: Իսկ որտեղի՞ց նրան այդ միլիարդները: Բոլորը գիտեին, որ Գռզոյենք դեռ ԽՍՀՄ-ի վերջին շրջանից՝ 80-ականներից, որոշակի մասնավոր գործունեությամբ զբաղվում էին: Բայց հետո եկան 90-ականներն, ու սովետական ցեխավիկները դարձան միլիարդատերեր, և դա՝ ոչ միայն Հայաստանում: Գռզոն իր հիմնական դրամագլուխը դիզել է ՀՀՇ-ի տարիներին՝ մի շրջան, երբ թալանվեց ողջ Խորհրդային Հայաստանի բազմամիլիարդանոց ունեցվածքը,որի մի մասն,ի դեպ, վաճառվեց նաև թուրքերին: Թալանի գլխավոր կանալներից մեկը հենց Գռզոյենք էին, որ սերտաճած լինելով իշխանությունների հետ՝ անմիջապես իրենցով արեցին բիզնեսի համապատսխան ոլորտները՝ բանկերից մինչև մանրամեծածախ առևտուր, տոնավաճառներ: Իսկ թե հիմա ինչով է զբաղված Գռզոն, բոլորի աչքի առաջ է. Նիկոլին քծնելով փորձում է դառնալ նոր Հայաստանի միակ իրական, խոշորագույն օլիգարխը՝ ստվերում թողնելով մրցակիցներին: Ճիշտ է՝ նա դա ներկայացնում է որպես հայրենասիրություն՝ հնչեցնելով առաջին հայացքից մեծ թվացող թվեր՝ իր մուծած հարկերի մասին, բայց կոծկում է այն հարցի պատասխանը, թե այդ թվերն ի՞նչ հարաբերակցության մեջ են գտնվում իր ստացած իրական եկամուտների հետ: Հազարն աշխատելն ու դրանից մեկը պետությանը վերադարձնելը հայրենասիություն չէ:Հանրությունը տեսել է՝ սրանք ինպես են տնտեսություն զարգացնում. իրենք են զարգանում, այլ ոչ թե տնտեսությունը:</p>', '', '/upload/20220511220918702359.jpg', 'https://www.blognews.am/arm/news/794853/hayrenaser-grzon-vstahecrel-e-te-irenq-och-mi-vat-ban-chen-arel-ayl-yndameny-tntesutyun-en-zargacrel.html', 'Գռզոն հայտարարել է, թե բնիկ երևանցի լինելով՝ քաջատեղյակ է՝ 90-ականներից ով ինչով է զբաղվել:', '', 0, 0, 93, '2022-05-11 19:09:54', '2022-05-12 09:26:32'),
(120, 5, 'Ռոբերտ Քոչարյանը դստեր հետ մասնակցում էր «Դիմադրություն» շարժման հանրահավաքին', '', '<p>Երևանի Ֆրանսիայի հրապարակում տեղի ունեցած &laquo;Դիմադրություն&raquo; շարժման այսօրվա հանրահավաքին մասնակցել է նաև ՀՀ երկրորդ նախագահ Ռոբերտ Քոչարյանն՝ իր դստեր Գայանեի հետ:</p>\n<p>Հիշեցնենք, որ Ֆրանսիայի հրապարակում այսօր՝ մայիսի 11-ին, ժամը 20։00-ին մեկնարկել էր &laquo;Դիմադրություն&raquo; շարժման հանրահավաքը։</p>\n<p>Նշենք, որ վաղ առավոտից շարժման մասնակիցները մայրաքաղաքի տարբեր ուղղություններով ավտոերթեր են իրականացրել և զուգահեռաբար շարունակել են անհնազանդության ակցիաները։</p>\n<p>Ինչպես ավելի վաղ հայտնել էինք, երթի մասնակիցները շրջափակել էին նաև քաղաքապետարանի շենքը:</p>', '', '/upload/20220511221351625633.jpg', 'https://iravaban.net/382645.html', 'Երևանի Ֆրանսիայի հրապարակում տեղի ունեցած «Դիմադրություն» շարժման այսօրվա հանրահավաքին մասնակցել է նաև ՀՀ երկրորդ նախագահ Ռոբերտ Քոչարյանն՝ իր դստեր Գայանեի հետ: Հիշեցնենք, որ Ֆրանսիայի հրապարակում այսօր՝ մայիսի 11-ին, ժամը 20։00-ին մեկնարկել էր «Դիմադրություն» շարժման հանրահավաքը։ Նշենք, որ վաղ առավոտից շարժման մասնակիցները մայրաքաղաքի տարբեր ուղղություններով ավտոերթեր են իրականացրել և զուգահեռաբար շարունակել են անհնազանդության ակցիաները։ Ինչպես ավելի...  Read more &raquo;', '', 0, 0, 49, '2022-05-11 19:14:10', '2022-05-12 09:09:23'),
(121, 5, 'Սերժիկին նահանջի հրաման են տվել, ասել են՝ ետ եկեք, չի ենթարկվել, պատասխանել է՝ դուք եկեք, նահանջելով մինչև ո՞ւր եք հասնելու (Photo)', '', '<p>Պետրոսյան Սերժիկը Շուշիից էր։ Ծնվել, մեծացել էր այնտեղ, հետո նաև ազատագրել էր իր հայրենի բերդաքաղաքը։</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container d-inline-block mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<p>Առաջին պատերազմի օրերին անդամագրվել էր Արկադի Տեր- Թադևոսյանի՝ Կամանդոսի ստեղծած 15 հոգանոց դիվերսիոն հետախուզական հատուկ ջոկատին։ Այդ ջոկատով էլ մասնակցել էր Շուշիի ազատագրմանը։ Պատերազմից հետո մնացել ու ապրել էր իր ազատագրած Շուշիում։ Անգամ մերժել էր Երևանում աշխատանքի անցնելու Սերժ Սարգսյանի առաջարկը։ Դեռևս 92 թվականին, Չլդրան գյուղի մոտ, տանկի արկի բեկորից Սերժիկը ծանր գանգուղեղային վիրավորում էր ստացել։ Այդ բեկորը նրան պետք է ուղեկցեր հետագա ողջ կյանքի ընթացքում, մինչև հաջորդ ու վերջին պատերազմը... Չնայած երկրորդ կարգի հաշմանդամությանը, այս պատերազմի առաջին իսկ օրերից Սերժիկը կամավորագրվում է ու մեկնում առաջնագիծ։ Լինում է Ակնայում, հետո սեփական կամքով տեղափոխվում դեպի Հադրութի ուղղությամբ։ Հոկտեմբերի 25-ին, Զարդանաշեն գյուղի մատույցների առաջին դիրքերը Սերժիկենց ջոկատն էր պահում։ Նահանջի հրաման են ստացել, ասել են՝ ետ եկեք, ինչին Սերժիկը պատասխանել է. &laquo;Չէ, դուք եկեք։ Նահանջելով մինչև ու՞ր եք հասնելու, Շուշի ու Ստեփանակե՞րտ&raquo;։ 3 հոգով մնացել են այդ դիրքում, մնացածը նահանջել են։ Հենց այդ դիրքերում էլ հերոսաբար զոհվեցին Սերժիկ Պետրոսյանն ու նրա 2 ընկերները։ Մարմինները դուրս բերելիս նրանց մոտ ոչ մի չկրակված փամփուշ կամ չօգտագործված նռնակ չէինգ գտել... 3 հոգով մինչև վերջին շունչն ու փամփուշտը կռիվ էին տվել թուրքի դեմ ու զոհվել մերձամարտում։ Զոհվել էին՝ մինչև վերջին շունչը պաշտպանելով իրենց բաժին Արցախը։</p>\n<p><iframe src=\"https://www.youtube.com/embed/Rir-HLQ_54I\" width=\"840\" height=\"480\"></iframe><iframe src=\"https://www.facebook.com/plugins/video.php?height=314&amp;href=https%3A%2F%2Fwww.facebook.com%2Fblognews.am%2Fvideos%2F1519785931710543%2F&amp;show_text=false&amp;width=560&amp;t=0\" width=\"840\" height=\"480\"></iframe></p>', '', '/upload/20220512001231365077.jpg', 'https://www.blognews.am/arm/news/780934/serzhikin-nahanji-hraman-en-tvel-asel-en-et-ekeq-chi-entarkvel-pataskhanel-e-duq-ekeq-nahanjelov-minchev-ur-eq-hasnelu-photo.html', 'Պետրոսյան Սերժիկը Շուշիից էր։ Ծնվել, մեծացել էր այնտեղ, հետո նաև ազատագրել էր իր հայրենի բերդաքաղաքը։', '', 0, 0, 212, '2022-05-11 21:13:14', '2022-05-12 09:48:12'),
(122, 5, 'Դանին ա, Դավոյի Դանին. ինքը մինչև հիմա կարոտում ու սպասում ա հորը. նրա պես հարյուրավոր երեխաների անվերադարձ խաթարված մանկության համար մինչ օրս ոչ ոք պատասխան չի տվել', '', '<p>Դանին ա, Դավոյի Դանին։ Ինքը մինչև հիմա կարոտում ու սպասում ա հորը, ինքը մինչև հիմա մի տեղը ցավեցնելիս կամ գիշերները վատ երազ տեսնելիս պապայա կանչում... Դանիի ու նրա պես հարյուրավոր երեխաների անվերադարձ խաթարված մանկության համար մինչ օրս ոչ ոք պատասխան չի տվել...</p>\n<p><iframe src=\"https://www.facebook.com/plugins/video.php?height=476&amp;href=https%3A%2F%2Fwww.facebook.com%2Faram1307%2Fvideos%2F518097816523132%2F&amp;show_text=true&amp;width=267&amp;t=0\" width=\"267\" height=\"591\"></iframe></p>', '', '/upload/20220512001824568019.jpg', 'https://www.blognews.am/arm/news/794792/danin-a-davoyi-danin-inqy-minchev-hima-karotum-u-spasum-a-hory-nra-pes-haryuravor-erekhaneri-anveradardz-khatarvats-mankutyan-hamar-minch-ors-och-oq-pataskhan-chi-tvel.html', 'Դանին ա, Դավոյի Դանին։', '', 0, 0, 71, '2022-05-11 21:18:43', '2022-05-12 09:56:51'),
(123, 1, 'Տեսանյութ։ Նիկոլ  վարչապետ  Գերեվարվածների ծնողները՝ ընդդիմադիր ցուցարարներին', '', '<p><iframe style=\"border: none; overflow: hidden;\" src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Ffb.watch%2FcXR7O6Hun2%2F&amp;width=500&amp;show_text=false&amp;height=375&amp;appId\" width=\"500\" height=\"375\" frameborder=\"0\" scrolling=\"no\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/nikol-pashinyan-1.jpg', '', '', '', 0, 1, 84, '2022-05-11 21:23:46', '2022-05-12 09:53:35'),
(124, 5, 'Օքսֆորդի համալսարանը ի պատասխան ադրբեջանի հաստատել է , որ տոլման հայկական է', '', '<p style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\">Օքսֆորդի համալսարանի տեղեկատուն նշում է, որ տոլման ծագել է որպես հայկական ուտեստ, իսկ հետո անցել է թուրքական խոհանոց։</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container d-inline-block mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<figure class=\"wp-block-image\" style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\"><img style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"http://slim.am/wp-content/uploads/2022/05/10840413_0_117_999_682_1920x0_80_0_0_d3369c63fa8248bff2d34d3311b1b1af-770x440.jpg\" alt=\"\" /></figure>\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\">Տոլման պալատական ուտեստ է եղել Օսմանյան կայսրությունում։ Ծագումը հիմնովին չի կապվում օսմանյան ծագման հետ այն պատճառով, որ լցոնված բանջարեղենը հույների ու արաբների մոտ հանդիպում էր տեղական անվանումներով։</p>\n<div class=\"text-center\">\n<div class=\"ads-el-container d-inline-block mceNotEditable\"><span class=\"ads-element mceNotEditable text-ads\">Գովազդ</span></div>\n</div>\n<figure class=\"wp-block-image\" style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\"><img style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"http://slim.am/wp-content/uploads/2020/07/TV_2015-08-21.jpg\" alt=\"\" /></figure>\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\">Խաղողի վազը հայերեն կոչվում է նաև &laquo;տոլի&raquo;, որն &laquo;ուդուլի&raquo; ձևով հիշատակվում է դեռևս Վանի թագավորության արձանագրություններում: Ընդհանրապես, հայկական ավանդական կենսաապահովման մշակույթում տոլման փաթաթվում էր խաղողի տերևով:</p>\n<div class=\"middle-news mceNotEditable\" data-url=\"/loader/single-related-post?category=5&amp;post_type=latest\">\n<p>Կատեգորիա - 5</p>\n<p>Փոստի տեսակը - latest</p>\n</div>\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\">Տոլմա բառն առաջացել է ուրարտերեն &laquo;ուդուլի&raquo; բառից, որ նշանակում է խաղողի վազ: Այստեղից էլ ծագել է տոլին՝ խաղողածինը: Առհասարակ, ավանդական տոլման խաղողի թփով և գառան մսով տոլման է: Հնագիտական պեղումները հավաստում են, որ Հայկական լեռնաշխարհում հնագույն ժամանակներում ամենակիրառականը եղել է ոչխարի միսը: Ապա հաջորդաբար գալիս են կովը, այծը, ձին և խոզը:</p>\n<figure class=\"wp-block-image\" style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\"><img style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"http://slim.am/wp-content/uploads/2022/05/SummerTolma.jpg\" alt=\"\" /></figure>\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\">Առհասարակ, որևէ կերակրատեսակի ծագման և ազգային պատկանելության մասին խոսելից պետք է հաշվի առնել երեք հանգամանք. անվանման ծագումը, կիրառությունը տվյալ ժողովրդի մոտ և պատրաստման գործիքը: Գաղտնիք չէ, որ տոլման հայերի ամենասիրելի և ամենամասսայական կերակրատեսակներից է՝ մեր տոնական սեղանների պարտադիր ճաշացանկը:</p>\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\">Պատմաբան, խոհարարական գրքերի հեղինակ Պոխլյոբկինի կարծիքով՝ հենց ուտելիքն սկզբնապես ի հայտ է եկել հայկական խոհանոցում․</p>\n<figure class=\"wp-block-image\" style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\"><img style=\"box-sizing: inherit; height: auto; max-width: 100%; border-radius: inherit;\" src=\"http://slim.am/wp-content/uploads/2020/07/77383.jpg\" alt=\"\" /></figure>\n<p style=\"box-sizing: inherit; margin: 0px 0px 1.5em; padding: 0px; border: 0px; caret-color: #222222; color: #222222; font-family: -apple-system, system-ui, BlinkMacSystemFont, \'Segoe UI\', Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 17px; -webkit-text-size-adjust: 100%;\">XVII դարից մինչև XIX դարը Հայասատանը բաժանված էր Թուրքիայի և Իրանի միջև։ Այս ժամանակահատվածում Հայաստանի տնտեսությունը, նրա մարդկային և նյութական ռեսուրսներն անկում ապրեցին, բայց հոգևոր և նյութական մշակույթը չիփոխվել, չմահացավ նաև հայկական խոհանոցը։ Ընդհակառակը, հայերն իրենց ներդրումն են մտցրել թուրք֊սելջուկների խոհանոցում, այնպես որ շատ բնական հայկական ուտեստներ ավելի ուշ հայտնի դարձան Եվրոպային՝ թուրքերի միջոցով կամ իբր թե որպես թուրքական խոհանոցի ուտեստ (օրինակ՝ տոլման)։</p>', '', '/upload/B2788CCC-7775-442D-ADD6-408C521A16B6.jpg', '', '', '', 0, 0, 68, '2022-05-11 21:28:47', '2022-05-12 09:58:24'),
(125, 1, 'Բացառիկ Տեսանյութ։ Ոստիկանապետ Վահե Ղազարյանը զանգել էր Իշխան Սաղաթելյանին.', '', '<p><iframe src=\"https://www.youtube.com/embed/zPkMJnzguWI\" width=\"560\" height=\"314\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/37C3CE39-B1D8-4E3A-9478-5D63B844537D.jpg', '', '', '', 0, 1, 264, '2022-05-11 21:35:00', '2022-05-12 09:57:36'),
(126, 11, 'Տեսանյութ։ Թե ինչպես է փողի դիմաց ցույցերին մասնակցելու առաջարկ ստացել։ Պատմում է ուսխորհրդի նախագահը', '', '<p><iframe style=\"border: none; overflow: hidden;\" src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Ffb.watch%2FcXVnU2ckuV%2F&amp;width=500&amp;show_text=false&amp;height=281&amp;appId\" width=\"500\" height=\"281\" frameborder=\"0\" scrolling=\"no\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/5F6AFB7C-B52D-4D7E-AD87-3BC17EC59276.jpg', '', '', '', 0, 1, 122, '2022-05-11 22:43:30', '2022-05-12 09:50:29'),
(127, 1, 'Դրանք սաղ ծուխու շներ են Քոչոյի ու Սերժի շներնեն 5000 դրամանոց ես ոնց գնամ դրանց միանամ Տեսանյութ', '', '<p><iframe src=\"https://www.youtube.com/embed/oNb7CZH_2XQ\" width=\"560\" height=\"314\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/1E9508CE-0CDF-4737-BAB4-6E568BFE6CE1.jpg', '', '', '', 0, 1, 92, '2022-05-12 06:26:18', '2022-05-12 09:54:25'),
(128, 5, 'Եթե Նիկոլը մեզի ստեղ բերած էղնիր, սաղ քաղաքը ստեղ կեղնիր, ընդդիմությանը տեղ չէր մնա․․․ Գյումրեցի', '', '<p><iframe src=\"https://www.youtube.com/embed/eZqkL6I_p6k\" width=\"560\" height=\"314\" allowfullscreen=\"allowfullscreen\"></iframe></p>', '', '/upload/B207A131-170A-4588-A9FF-8B71AC9CF0C1.jpg', '', '', '', 0, 1, 724, '2022-05-12 06:31:02', '2022-05-12 10:03:17');
