INSERT INTO `widgets` (`id`, `name`, `type`, `description`, `file_url`, `admin_file_url`, `created_at`, `updated_at`) VALUES
(1, 'Փոքր փոստեր', 'posts', 'Փոքր փոստերը ունենում են ուղղանկյունաձև նկար՝ ձախ մասում։ \n                 Փոստի վերնագիրը գտնվում է նկարից աջ և ընդգրկում է նկարի աջ մասը և երկար \n                 լինելու դեպքում շարունակում է նկարի ներքևի մասից։ Ամսաթիվը և դիտումների քանակը կշարունակի վերնագրին։', 'components.widgets.small-category-content-loader', 'admin.components.widgets.posts-widget', NULL, NULL),
(2, 'Միջին փոստեր (Հորիզոնական)', 'posts', 'Միջին փոստերը ունենում են ուղղանկյունաձև կամ քառակուսի նկար՝ ձախ մասում։ \n                 Փոստի վերնագիրը գտնվում է նկարից աջ։ Տպվում է նաև նյութից մի փոքր հատված։ Ամսաթիվը և դիտումների քանակը ներքևից։', 'components.widgets.middle-category-text-right-content-loader', 'admin.components.widgets.posts-widget', NULL, NULL),
(3, 'Միջին փոստեր (Ուղղահայաց)', 'posts', 'Միջին փոստերը ունենում են ուղղանկյունաձև կամ քառակուսի նկար՝ վերևի մասում։ \n                 Փոստի վերնագիրը գտնվում է նկարից ներքև և ընդգրկում է 3 տող և երկար \n                 լինելու դեպքում շարունակության փոխարեն \"...\" է դրվում։ Ամսաթիվը և դիտումների քանակը վերնագրի ներքևից։', 'components.widgets.middle-category-text-bottom-content-loader', 'admin.components.widgets.posts-widget', NULL, NULL),
(4, 'Բաժանորդագրվել', 'subscription', 'Դաշտ բաժանորդագրվելու համար։', 'components.widgets.subscription-form', 'admin.components.widgets.subscription-widget', NULL, NULL),
(5, 'HTML', 'html', 'Դաշտը նախատեսված է տեղադրել պատրաստի կոդեր որոնք կբացազատվեն որպես կոմպոնենտ։', 'components.widgets.html', 'admin.components.widgets.html-widget', NULL, NULL);
